.tbt {margin-left: 5px !important;}

/* ===SafeSearch repositioning=== */
div.lsd {top: 32px !important; left: 5px !important;}

/* ===Sponser links dim=== */
div#ImgCont>table[align="center"], div#tads {opacity: .4!important;}

/*[END]*/
