css:
@-moz-document url-prefix("https://moodle-exia.cesi.fr"), url-prefix("https://moodle-examens.cesi.fr") {
/*-----------Header------------*/
.header-main{
	display:none;
}
.main-nav{
    position: relative;
    max-width: 0;
    top :-52px;
    background-color: rgb(255,255,255,0);
    min-height: 50px;
    pointer-events:all ;
}
.top-bar{
    pointer-events:auto;
}
.main-nav .nav li.open a,
.main-nav .nav li a:hover, 
.nav .open a, 
.nav .open a:focus, 
.nav .open a:hover {
    background-color: rgb(255,255,255,0);
}
.dropdown-menu{
    background-color: #26619c;
}

#page{
    margin-top:35px;
}

/*-----------Prosits------------*/
#page #region-main .region-main-inner{
    background: rgb(255,255,255,0);
}
/*Homepage's useless blank box*/
.region-main-inner .box.generalbox.sitetopic{
    display:none;
}
.region-main-inner .box.mdl-align{
    background:rgb(255,255,255,0);
}
/*Navigation pane*/
#page .block-region .block{
    margin-top:27px;
    background:rgb(255,255,255,0);
    box-shadow: 0 0 4px -2px #333;
}

/*-----------Pop-up-------------*/

/*General content*/
.contenu_co{
	max-width: 900px;
    margin: auto;
}

/*Useless pop-up infos*/
.celActForm, .consignes, .infos, .tabContenu td:not(.celActAppr){
    display:none;
}

/*Week section*/
.contenu_co>.activite{
  	box-shadow: 5px 0 5px -7px #333, -5px 0 5px -7px #333, 0 -5px 5px -7px #333, 0 5px 5px -7px #333;
    margin-bottom: 20px;
}
/*Titles*/
.activite_ti span {
	font-size: 1.5em;
}

.matPedago_ti{
    padding-bottom: 5px;
    border-bottom-style: dotted;
}

/*Links*/
a.op_txt_ul {
	font-size: 1.1em;
    padding-right: 0;
    font-weight: bold;
    color: #5c88da;
    background: #fff;
    float: right;
    margin-bottom: 25px;
}
a.op_txt_ul:hover, a.op_txt_ul:visited{
	color: #d2005e;
}
}
