@-moz-document domain(drillz.io) {


.rm {
	color: lightgrey;
	padding: 10px;
	min-width: 220px;
	cursor: default;
    background-image: url("https://media.giphy.com/media/l3q2RgN7WUjeUUXm0/source.gif");
	
}

.rm-heading {
	color: #fff;
	font-size: 16px;
	text-decoration: wavy;
	color: 4px solid hsl(0, 100%, 50%);
  -webkit-animation: rainbow 5s infinite alternate;
}


@-webkit-keyframes rainbow {
  0% {color: hsl(0, 100%, 51%);}
  10% {color: hsl(20, 100%, 50%);}
  20% {color: hsl(54, 100%, 50%);}
  30% {color: hsl(84, 100%, 50%);}
  40% {color: hsl(128, 100%, 50%);}
  50% {color: hsl(178, 100%, 50%);}
  60% {color: hsl(214, 100%, 50%);}
  70% {color: hsl(236, 100%, 50%);}
  80% {color: hsl(278, 100%, 50%);}
  90% {color: hsl(306, 100%, 50%);}
  100% {color: hsl(0, 100%, 53%);}
}

.rm-heading #drill_type span { 
-webkit-animation: rainbow 5s infinite alternate;
}

.rm #killFeed .resource-item span { 
-webkit-animation: rainbow 5s infinite alternate;
}

.upgrade .resource-item .resource-count { 
 background: red;
    background: -webkit-linear-gradient(left, orange , yellow, green, cyan, blue, violet);
    background: -o-linear-gradient(right, orange, yellow, green, cyan, blue, violet);
    background: -moz-linear-gradient(right, orange, yellow, green, cyan, blue, violet);
    background: linear-gradient(to right, orange , yellow, green, cyan, blue, violet);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
}

