css:
@-moz-document domain("linuxfromscratch.org") {
:root
{
    --main-bg-color: #21262b;
	--main-fg-color: #bbb;
	--lighter-bg-color: #31363b;
    --lighter-fg-color: #ccc;
	--highlight-bg-color: #41464b;
    --highlight-fg-color: #ddd;
    --link-normal-color: #5080a0;
    --link-highlight-color: #50a0a0;
}

body
{
    background-color: var(--main-bg-color);
    color: var(--main-fg-color);
}

div.navbar, #leftmenu ul, input, div.navheader, div.navfooter,
.blfs ul.compact li p
{
    background-color: var(--lighter-bg-color);
    color: var(--lighter-fg-color);
}

input:hover, div.navheader ul, div.navfooter ul, div.note, div.tip,
div.important, div.warning, div.caution
{
    background-color: var(--highlight-bg-color);
    color: var(--highlight-fg-color);
}

a:link, a.ulink:link
{
    text-decoration: none;
    color: var(--link-normal-color);
    border-bottom: dotted 1px var(--link-normal-color);
}

a:visited, a:hover
{
    text-decoration: none;
    color: var(--link-highlight-color);
    border-bottom: dotted 1px var(--link-highlight-color);
}

h1, h2, h3, h4, h5, h6, b, .strong
{
    color: var(--main-fg-color);
}

div.navheader
{
    border-bottom: 1px solid var(--lighter-bg-color);
}

div.navfooter
{
    border-top: 1px solid var(--highlight-bg-color);
}

.preface h1, .part h1, .chapter h1, .appendix h1, .index h1, .sect1 h1
{
    background-color: var(--highlight-bg-color);
    color: var(--highlight-fg-color);
    border-bottom: .1em solid var(--lighter-bg-color);
}

pre, pre.screen
{
    background-color: var(--lighter-bg-color);
    color: var(--lighter-fg-color);
    border: 1px solid var(--highlight-bg-color);
}

pre.userinput, pre.root
{
    color: var(--lighter-fg-color);
}

div.note, div.tip
{
    border: 2px solid var(--lighter-bg-color);
}

div.note img, div.tip img
{
    filter: invert(0.7);
}

}
