css:
@-moz-document domain("www.typescriptlang.org") {
.container {
	padding-left: 264px;
}

@media (max-width: 991px) and (min-width: 768px) {
	.container {
		padding-left: 0;
	}
}
.toc-container {
	position: fixed;
	left: 0;
	top: 50px;
	background-color: rgba(233, 233, 233, .6);
	margin-right: 0;
}
#docs-navbar .toc-item .active {
    background: #294E80!important;
    display: block;
    padding: 3px;
    color: rgb(255,255,255);
    font-size: 16px;
    font-weight: initial!important;
}

#docs-navbar .toc-item .active:hover {
	color: rgba(255,255,255,.77)!important;
}


.update-banner {
	width: auto;
	background-color: white;
}
}
