css:
@-moz-document url-prefix("https://tff-forum.de/") {
#linkbar, #subheader, .topic-actions, .back2top, .postbody br, .post.bg3, .postbody>h3, .postbody .signature, .postprofile  {
    display: none;
}

#headerbar{
	height: auto;
    margin-bottom: 0;
}


#headerbar-inner, #topbar{
	height: auto;
    padding-left: 20px;
}

#page-body{
    width: 100%;
}

#page-body>h1, .top-navbar {
	padding-left: 20px;
    padding-right: 20px;
}


#page-body-inner > .topic-actions ~ .topic-actions{
    display: block;
}

#main-nav{
    height: auto;
}

.content{
    min-height: auto;
    line-height: 1.3em;
    padding-right: 50px;
}
.inner-block{
    width: auto;
    padding: 0px;
}

.postbody{
    width: 100%;
}

.postbody .author {
    display: block;
}

.postbody .content{
    border-top: none;
    padding-top: 0;
}

.content blockquote{
    padding-top: 0;
    padding-bottom: 0;
}

.postbody p.author{
    margin: 0;
    padding: 0;
    font-size: 10px;
}

.post.bg1.unreadpost{
    padding: 5px;
}
}
