css:
@-moz-document domain("facebook.github.io") {

body {
    background-color: #222222;
}

.toc .toggleNav ul li a {
    border: none;
    color: #bfbebe;
    display: block;
    font-size: 14px;
    padding: 4px 0;
    transition: color .3s;
}

.toc .toggleNav ul li.navListItemActive a, .toc .toggleNav ul li a:focus, .toc .toggleNav ul li a:hover {
    color: #80afcc;
}

.mainContainer {
    background-color: #222222!important;
}

.hljs {
    line-height: 20px;
    background-color: rgb(0, 0, 0);
}

.display-guide-quickstart.display-platform-ios.display-os-mac .quickstart.ios.mac, .display-guide-quickstart.display-platform-ios.display-os-linux .quickstart.ios.linux, .display-guide-quickstart.display-platform-ios.display-os-windows .quickstart.ios.windows, .display-guide-quickstart.display-platform-android.display-os-mac .quickstart.android.mac, .display-guide-quickstart.display-platform-android.display-os-linux .quickstart.android.linux, .display-guide-quickstart.display-platform-android.display-os-windows .quickstart.android.windows, .display-guide-native.display-platform-ios.display-os-mac .native.ios.mac, .display-guide-native.display-platform-ios.display-os-linux .native.ios.linux, .display-guide-native.display-platform-ios.display-os-windows .native.ios.windows, .display-guide-native.display-platform-android.display-os-mac .native.android.mac, .display-guide-native.display-platform-android.display-os-linux .native.android.linux, .display-guide-native.display-platform-android.display-os-windows .native.android.windows {
    display: block;
    color: #c3c3c3;
}

a, abbr, acronym, address, applet, article, aside, audio, b, big, blockquote, body, canvas, caption, center, cite, code, dd, del, details, dfn, div, dl, dt, em, embed, fieldset, figcaption, figure, footer, form, h1, h2, h3, h4, h5, h6, header, hgroup, html, i, iframe, img, ins, kbd, label, legend, li, mark, menu, nav, object, ol, output, p, pre, q, ruby, s, samp, section, small, span, strike, strong, sub, summary, sup, table, tbody, td, tfoot, th, thead, time, tr, tt, u, ul, var, video {
    color: #a7a7a7;
}

.toc .toggleNav .navGroup .navGroupCategoryTitle {
    color: #05a5d1;
    font-size: 18px;
    font-weight: 500;
    line-height: 1.2em;
    margin-bottom: 8px;
    margin-top: 0;
}

::-webkit-scrollbar {
  width: 10px;
}

/* Track */
::-webkit-scrollbar-track {
  background: #222222; 
}
 
/* Handle */
::-webkit-scrollbar-thumb {
  background: #888; 
}

/* Handle on hover */
::-webkit-scrollbar-thumb:hover {
  background: #555; 
}
}
