css:
@-moz-document url(https://ayrshiremetals.opencrm.co.uk) {
html {
}
/*----- DEFAULT TEXT, BORDER & BACKGROUND COLORS -----*/

 * {
    color: #c6ffff !important;
    box-shadow: none !important;
    background-color: #838383 !important;
    border-color: #e6e6fa !important;
    border-top-color: #e6e6fa !important;
    border-bottom-color: #e6e6fa !important;
    border-left-color: #e6e6fa !important;
    border-right-color: #e6e6fa !important
}
body {
    background: transparent !important
}
.settings img {
    filter: brightness(0.5) sepia(1) hue-rotate(-180deg) saturate(5);
}
.input, .select {
    color: #c6ffff !important;
    border-style: solid !important;
    border-width: 1px !important;
    border-color: #e6e6fa !important;
}
.settings a:link, .settings a:active, .settings a:visited {
 	color: #c6ffff !important;
}
.dataLabel, .datafield, .value, td, .moduleListTitle, .moduleListTitle td, input.query_string {
    color: #c6ffff !important;
}
element.sytle, input, input.placeholder, textarea {
    color: #c6ffff !important;
}
#typeTabs li.ui-state-default a, .ui-tabs, .tabLink, a.tabLink, #detailtabs, .detailtabs, #typeTabs, #typeTabs li.ui-state-active a {
    color: #c6ffff !important;
    border-style: solid !important;
    border-width: 0px !important;
    border-color: #e6e6fa !important;
}
.searchform select:active, .searchform textarea:active {
    border-style: solid !important;
    border-width: 5px !important;
    border-color: #e6e6fa !important;
}
#sidebar-menu, .l-sidebar, .sf-menu .sf-vertical .sf-js-enabled .sf-arrows, .sidebarTarget .sf-with-ul div, .l-sidebar div, .l-sidebar li, .sf-with-ul span, #sidebar-menu span {
    background-color: #003399 !important;
}
.sidebaricon, .l-sidebar ul, .l-sidebar li, .l-sidebar a {
    background-color: #003399 !important;
    color: #c6ffff !important;
}
header, header div, header button, header input, header form, header span, header nav, header img, header ul, header li, header a, img .company_logo, .sf-button ul li a, li.main-li {
    background-color: #003399 !important;
}
header.topnavbar .sc-button {
    border-right-color: #003399;
}
header div.l-company_logo_container {
    background-color: #ffffff !important;
}
header nav ul li a, header li a, .sf-button a {
    color: #c6ffff !important;
    opacity: 1 !important;
}
header nav ul li ul li:hover a, header nav ul li:hover a, header li:hover a, .sf-button:hover a {
    color: #c6ffff !important;
    opacity: 0.7 !important;
}
.datepast {
    color: #f49242 !important;
}
ul li:hover a {
    color: #c6ffff !important;
}
.list-container tr, .list-container td, .company_message_text p, .company_message_text strong, .company_message_text a, .company_message_text href, .company_message_text font, .company_message_text b, .company_message_text i {
    background-color: #003399 !important;
}
.user-superfish span {
    opacity: 1 !important;
}
::placeholder {
    color: #c6ffff !important;
    opacity: 0.5 !important;
}
::-moz-selection {
    background: #377 !important;
    color: #c6ffff !important;
}
::selection {
    background: #377 !important;
    color: #c6ffff !important;
}
:focus {
    outline: -webkit-focus-ring-color auto 6px !important;
}
}
