    border-color: rgb(188, 49, 99);
    border-radius: 2px;
    color: rgba(255, 255, 255, 1);
}
.tabularBoxTitle > header, .tabularListRowHead {
    border: 0px #000 solid;
    background-image: url(https://i.imgur.com/X7nnixt.png), linear-gradient(to right, #000000, rgba(0, 0, 0, 1));
    background-repeat: no-repeat;
    background-position: center;
    border-radius: 3px;
    height: 88px;
}
.wbbBoardList .wbbCollapsibleCategory h2 {
    align-items: center;
    display: flex;
    font-weight: 600;
    text-shadow: 1px 1px 1px #000;
    text-align: center;
}
.wbbBoardList .wbbCollapsibleCategory h2 + small {
    color: rgba(255, 255, 255, 1);
    margin-left: 26px;
    text-align: center;
    text-shadow: 1px 1px 1px #000;
}
.inputItemList {
    background-color: rgb(39, 38, 43);
    border: 1px solid rgb(140, 65, 173);
    border-radius: 0;
    color: rgb(255, 255, 255);
    font-weight: 400;
    outline: none;
    padding: 4px 8px;
    font-family:"Lato", Arial, Helvetica, sans-serif;
    line-height: 1.48;
    display: flex;
    flex-wrap: wrap;
    padding-bottom: 0;
    padding-top: 5px;
}
.inputItemList:focus, .inputItemList:hover {
    background-color: rgb(36, 66, 95);
    border-color: rgba(126, 26, 188, 1);
    color: rgb(255, 255, 255);
}
.boxesSidebarLeft .box .boxMenu li.active > .boxMenuLink, .boxesSidebarRight .box .boxMenu li.active > .boxMenuLink {
    color: rgb(255, 255, 255);
    background: linear-gradient(to left, rgb(39, 38, 43) 10%, rgb(99, 65, 173) 90%);
}
.boxesSidebarLeft a, .boxesSidebarRight a {
    color: rgb(255, 255, 255);
}
.boxesSidebarLeft .box .boxMenu li.active > .boxMenuLink:hover, .boxesSidebarRight .box .boxMenu li.active > .boxMenuLink:hover {
    color: rgb(255, 255, 255);
}
.boxesSidebarLeft a:hover {
    align-items: flex-start;
    display: flex;
    color: #fff;
    background: linear-gradient(to left, rgb(39, 38, 43) 10%, rgb(76, 76, 76) 90%);
}
.boxesSidebarRight a:hover {
    align-items: flex-start;
    display: flex;
    display: inline-block;
    color: #fff;
    background: transparent;
}
.boxesSidebarLeft .box .boxMenu li.active > .boxMenuLink {
    margin-left: 0px;
    padding-left: 20px;
}
.warning {
    background-color: rgba(183, 31, 82, 1);
    border-color: rgba(250, 40, 32, 1);
    color: rgb(255, 255, 255);
    border-radius: 3px;
}
.htmlContent table tr:hover > td, .messageBody > .messageText table tr:hover > td, .messageSignature > div table tr:hover > td, .redactor-layer table tr:hover > td, .table tr:hover > td, .messageTableOverflow > table tr:hover > td {
    background: rgb(30, 30, 30);
}
.jsOnly .invisible, li .invisible {
    display: inline-block;
}
.pageNavigation a {
    color: rgb(133, 7, 183);
}
.pageNavigation a:hover {
    color: #fff;
}
.tabularListRow:not(.tabularListRowHead):hover {
    background: linear-gradient(to right, rgb(39, 38, 43), rgb(36, 66, 95), rgb(39, 38, 43));
}
.wcfLikeCounter.likeCounterLiked {
    color: #00e6e2 !important;
  }
.messageSidebar a { 
    color:#ebcffc
}
.messageSidebar a:hover { 
    color:#f9b3d8
}
.messageFooterGroup > .likesSummary {
    margin-top: 0;
    padding: 5px;
    background: linear-gradient(to right, rgb(252, 80, 158), rgb(39, 38, 43));
}
.dialogContainer > .dialogContent {
    background: rgb(0, 0, 0);
    color: #fff;
}
.commentListAddComment.collapsed {
    background-color: transparent;
    cursor: pointer;
    overflow: hidden;
    padding: 10px 20px;
    position: relative;
}
.commentListAddComment.collapsed {
    cursor: pointer;
    border: 1px solid rgb(68, 35, 95);
    background: rgb(12, 12, 12);
    overflow: hidden;
    padding: 10px 20px;
    position: relative;
}
.icon, .fa {
    color: #fff;
}
.datePicker {
    background-color: rgb(127, 65, 173);
    border-radius: 2px;
    box-shadow: 0 2px 6px rgba(0, 0, 0, 0.16), 0 3px 6px rgba(0, 0, 0, 0.23);
    color: rgb(255, 255, 255);
    display: none;
    position: absolute;
    width: 240px;
    z-index: 450;
}
.datePicker > ul > li > a.active, .datePicker > ul > li > a:not(.otherMonth):hover {
    background-color: rgb(173, 60, 145);
    color: rgb(255, 255, 255);
}
.datePicker > ul > li > a:not(:last-child), .datePicker > ul > li > span:not(:last-child) {
    border-right: 1px solid rgb(65, 35, 95);
}
.datePicker > ul > li > a {
    color: rgb(255, 255, 255);
}
.datePicker > ul > li > a.otherMonth {
    color: rgb(0, 0, 0);
    cursor: default;
}
.datePicker > ul > li.weekdays, .datePicker > ul > li.weekdays + li {
    border-top-color: rgb(78, 35, 95);
}
.datePicker > ul > li {
    border-top: 1px solid rgb(78, 35, 95);
    display: flex;
}
.datePicker > footer {
    border-top: 1px solid rgb(78, 35, 95);
    display: none;
    padding: 10px;
    position: relative;
    text-align: center;
}
.codeBox {
    background-color: rgb(27, 27, 30);
    border: 1px solid #6a41ad;
}                                            
.codeBox.collapsed > .toggleButton {
    bottom: 0;
    box-shadow: 0px 0px 0px 0px #1d1d1d;
    left: 0;
    background: #1b1b1b;
    padding-bottom: 10px;
    position: absolute;
    right: 0;
    z-index: 1;
}
.codeBox > .toggleButton {
    background-color: #1b1b1e;
    cursor: pointer;
    display: block;
    padding: 10px 20px 0 10px;
    text-align: center;
    font-weight: 400;
}
.redactor-layer pre {
    background-color: #27262b !important;
    box-shadow: 0 0 3px rgba(0, 0, 0, .12), 0 1px 2px rgba(0, 0, 0, .24);
    border-radius: 2px;
    color: #fff !important;
    font-family: Consolas, 'Courier New', monospace;
    margin: 1em 0;
    padding: 10px 20px;
    position: relative;
    white-space: pre-wrap;
    word-break: break-all;
    word-wrap: break-word;
}
.redactor-layer > :not(p):last-child {
    margin-bottom: 20px !important;
    background-color: #27262b;
    color: #fff;
    border: 1px solid #57338e;
}
.redactor-layer woltlab-spoiler {
    background-color: #27262b;
    box-shadow: 0 0 3px rgba(0, 0, 0, .12), 0 1px 2px rgba(0, 0, 0, .24);
    border-radius: 2px;
    border: 1px solid #6b3591;
    display: block;
    margin: 1em 0;
    padding: 10px 20px;
    position: relative;
}
.spoilerBox > .spoilerBoxContent {
    background-color: rgba(15, 15, 15, 0.7);
    border-radius: 3px;
    border: 1px solid #8c0963;
}
.pollContainer .pollResultItem .pollMeter {
    background-color: rgb(41, 39, 43);
    height: 5px;
    margin-top: 5px;
}
.pollContainer .pollResultItem .pollMeter > .pollMeterValue {
    background-color: rgb(168, 0, 139);
    height: 100%;
}
.redactor-toolbar > li > a {
    background-color: rgb(92, 27, 99);
    border: 1px solid #8e3678;
    box-shadow: none;
    color: #fff;   
}
.inlineList li:hover {
    border-bottom: 0px dashed #5f235f;
    background: transparent;
}
.error {
    background-color: rgba(219, 20, 99, 1);
    border-color: rgba(219, 20, 99, 1);
    color: rgba(255, 255, 255, 1);
    border-radius: 3px;
}
.messageFooterNote {
    color: #888;
}
.userMention
{
    color: #f97cf9;
    text-decoration: none;
}
.userMention a
{
    color: #f97cf9;
    text-decoration: none;
}
.inputItemList > .item {
    background-color: rgb(123, 58, 142);
    border-radius: 2px;
    color: rgb(255, 255, 255);
    cursor: default;
    max-width: 100%;
    padding: 1px 5px;
}
.content > .section > .containerList > :first-child {
    border-top: 1px solid rgb(74, 36, 96);
}
.content > .section > .containerList > :last-child {
    border-bottom: 1px solid rgb(74, 36, 96);
}
.containerList > li:last-child {
    border-bottom: 1px solid rgb(74, 36, 96);
}
#main .sidebar {
    background: #151515;
}
#main {
    padding: 8px 0 0px;
    background-color: #151515;
}
.interactiveDropdownItems > li:hover {
    max-width: 400px;
    background: linear-gradient(to right, rgb(35, 34, 38), rgb(59, 35, 95), rgb(39, 38, 43));
    color: #504f51;
}
 .quoteBox > .toggleButton
