css:
@-moz-document url-prefix("https://www.youtube.com/watch") {
body{
overflow:hidden;	
}

#comments,
#logo,
#guide-button,
#end,
#thumbnail-container,
#playlist-actions,
#meta-contents,
#like-bar,
#related,
#byline-containerz,
.publisher,
.ytp-right-controls,
ytd-thumbnail,
ytd-video-meta-block,
yt-view-count-renderer,
ytd-sentiment-bar-renderer,
ytd-playlist-panel-video-renderer #menu{
    display:none !important;
}

#info-contents .ytd-watch-flexy{
	border-bottom:none !important;
}

.html5-video-player{
    width: 100%;
    height: 39px;
}

ytd-searchbox.ytd-masthead {
    margin: 0 20px;
    display: block;
}

h1.ytd-video-primary-info-renderer{
	position: absolute;
	top: 150px;
	background-color:#000;
	font-size:1.2rem !important;
	padding:15px;
	color:#FFF !important;
	text-transform:uppercase;
}

ytd-playlist-panel-video-renderer{
min-height:12px !important;	
}

#menu-container.ytd-video-primary-info-renderer{
	position: absolute;
	top: 230px;
}

div.ytp-chrome-bottom{
	opacity:1 !important;
	background-color:#000;
	left:0 !important;
	width:100% !important;
}

.ytp-chrome-bottom[aria-hidden="true"]{
	opacity:1 !important;
}

#video-title{
	text-transform:uppercase;
	font-size:1rem !important;
}

#columns,ytd-masthead,ytd-page-manager,#content,ytd-app{
	background:none !important;
	background-color: transparent !important;	
}

ytd-watch-flexy{
background-image:url('https://cdn.pixabay.com/photo/2015/07/30/17/24/audience-868074_960_720.jpg');	
background-size:cover;
min-height:600px;
}

#container.ytd-masthead,ytd-menu-renderer{
	background-color:#000;
}


}
