css:
@-moz-document domain("forums.rotoworld.com") {
#header,
#campaign,
#footer
{
    min-width: 800px !important;
}

#footer .container,
#header-region .container
{
    min-width: 800px;
}

.container
{
    width: auto;
}
}

@-moz-document domain("forums.rotoworld.com") {
#ipsLayout_header nav{
    background-color: #151515;
}
#ipsLayout_header header {
    background-color: #151515;
}
.ipsNavBar_primary > ul > li.ipsNavBar_active > a {
    background: #6f6f6f;
    color: #ffffff;
}
.ipsNavBar_secondary {
    background: #151515;
    color: #ffffff;
}
.ipsNavBar_secondary > li.ipsNavBar_active a {
    font-weight: bold;
    color: #ffffff;
}
.ipsNavBar_primary > ul > li > a, .ipsNavBar_secondary > li > a {
    color: #6f6f6f;
}
.ipsBreadcrumb > ul > li {
    color: #ffffff;
}
body {
    color: #ffffff;
    background-color: #151515; 
}

a{
    color: lightgray;
}

.ipsDataList.ipsDataList_zebra .ipsDataItem:not( .ipsDataItem_selected ):not( .ipsModerated ):not( .ipsDataItem_new ):not( .ipsDataItem_success ):not( .ipsDataItem_warning ):not( .ipsDataItem_error ):not( .ipsDataItem_info ):not( .ipsDataItem_status ):nth-child(even) {
    background: #151515;
}

.ipsDataList.ipsDataList_zebra{
    background: #151515;
}

.ipsWidget.ipsWidget_vertical .ipsWidget_title, .ipsWidget.ipsWidget_horizontal .ipsWidget_title {
    color: #ffffff;
    background: #151515;
}

.ipsBox:not( .ipsBox_transparent ):not( .ipsModerated ) {
    background-color: #151515;
}

.ipsButtonBar {
    background: #151515;
}

.ipsApp .ipsButton_important {
    background: #353535;
    color: #ffffff;
}

.ipsType_normal.ipsType_richText {
    color: white;
}

.ipsType_medium {
    color: white;
}

.ipsApp .ipsButton_link.ipsFollow, .ipsApp .ipsButton_link.ipsPromote {
    background: #464646;
	color: white;
}
.ipsButtonBar .ipsPagination a {
    color: #fff;
}
.ipsPagination.ipsPagination_mini a {
    background-image: initial;
    background-attachment: initial;
    background-origin: initial;
    background-clip: initial;
    padding: 0 4px;
    color: #ffffff;
    border-radius: 2px;
    background-color: #717171; 
}

.ipsButtonBar .ipsPagination .ipsPagination_pageJump a {
    color: #fff;
}
.ipsButtonBar .ipsPagination .ipsPagination_page.ipsPagination_active a {
    background: #171717;
    border-color: #3e4148;
    color: #fff;
}
.ipsPagination:not( .ipsPagination_mini ) .ipsPagination_page a, .ipsPagination:not( .ipsPagination_mini ) .ipsPagination_next a, .ipsPagination:not( .ipsPagination_mini ) .ipsPagination_prev a, .ipsPagination:not( .ipsPagination_mini ) .ipsPagination_first a, .ipsPagination:not( .ipsPagination_mini ) .ipsPagination_last a {
    background: #6f6f6f;
    color: #ffffff;
}

.ipsPagination .ipsPagination_page a {
    text-shadow: 0px 1px 0px rgba(255,255,255,0.6);
}
.ipsBreadcrumb [data-action="defaultStream"], .ipsBreadcrumb [data-action="markSiteRead"] {
    font-size: 12px;
    font-weight: 400;
    color: #ffffff;
}
.ipsQuote_citation {
    background: #353535;
    color: fff;
}

.ipsQuote .ipsQuote_citation a {
    color: #fff;
}

.ipsQuote {
    background: #6f6f6f;
}


.ipsPagination .ipsPagination_page.ipsPagination_active a {
    background: #6f6f6f;
    border-color: #ffffff;
    color: #fff;
}
#elMobileNav {
    background: #151515;
}
a.ipsType_blendLinks, .ipsType_blendLinks a {
    color: #b1c5d2;
}
.cke_wysiwyg_frame, .cke_wysiwyg_div {
    background-color: #6f6f6f;
    color: white;
}
.cke_top {
    white-space: normal;
    background: #545454;
}
.ipsAreaBackground {
    background: #545454;
}
.ipsAreaBackground_light {
    background: #151515;
}
.ipsComment_highlighted.ipsBox:not(.ipsBox_transparent):not(.ipsModerated):not(.ipsComment_selected):not(.ipsComment_popular ), .ipsComment_highlighted:not(.ipsBox_transparent):not(.ipsModerated):not(.ipsComment_selected):not(.ipsComment_popular ) .ipsAreaBackground_reset {
    background-color: #151515;
}
.cForumTitle {
    background-color: #3a3a3a;
}
.ipsType_richText {
    color: #bfbfbf;
}
.ipsType_pageTitle {
    color: #fff;
}
.ipsPagination:not( .ipsPagination_mini ) .ipsPagination_page a, .ipsPagination:not( .ipsPagination_mini ) .ipsPagination_next a, .ipsPagination:not( .ipsPagination_mini ) .ipsPagination_prev a, .ipsPagination:not( .ipsPagination_mini ) .ipsPagination_first a, .ipsPagination:not( .ipsPagination_mini ) .ipsPagination_last a {
    background: #6f6f6f;
    color: #ffffff;
}
.ipsButtonBar .ipsPagination .ipsPagination_page.ipsPagination_active a {
    background: #171717;
    border-color: #3e4148;
    color: #fff;
}
}
