css:
@-moz-document domain("burbuja.info") {
* {
    background: none !important;
    color: #fffc !important;
    border-width: 1px !important;
    border-color: #fff !important;
    border-radius: 0px !important;
    text-shadow: 0 0 0 transparent, 0 0px 0px rgba(0, 0, 0, 0.0) !important;
    box-shadow: 0 0 0 transparent, 0 0px 0px rgba(0, 0, 0, 0.0) !important;
}

.externalLink,
.internalLink {
    text-decoration: underline !important;
}
.externalLink:hover,
.internalLink:hover {
    text-decoration: underline !important;
    color: #DFB !important;
}

h3 * {
    color: #DFB !important;
}

.pageWidth {
    margin: 0% 2%;
}

.pageContent {
    margin: 0;
    padding: 0;
}

#headerProxy {
    height: 64px !important;
    ;
}

.xenOverlay,
.Menu,
.secondaryControls {
    background-color: #b555 !important;
    border-width: 1px !important;
    border-color: #fff !important;
}

body,
.Tooltip,
.Popup,
.xenTooltip,
#calroot,
.mce-tooltip,
.mce-container,
.mce-panel,
.xenPreviewTooltip {
    background-color: #454545 !important;
}

#forums,
#forums *,
#ProfilePostList > *,
.breadcrumb,
.sidebar,
.secondaryContent,
.message,
.messageUserBlock,
.avatarHolder img,
.messageList {
    border: 0px solid rgb(0, 0, 0) !important;
}

.mce-btn {
    border: 0px solid rgb(0, 0, 0) !important;
}

.message {
    padding: 0px !important;
    margin: 0px !important;
    min-height: 64px !important;
}


.message * {
    padding: 1px !important;
    margin: 0px !important;
}

.messageContent .messageText {
    font-family: Helvetica, Tahoma, Arial, sans-serif !important;
    line-height: 1.0em !important;
    font-size: 10pt;
}

.message .messageUserInfo {
    width: 96px !important;
}

.message .avatar img {
    width: 64px;
    height: 64px;
}

.nodeIcon,
.loginButton,
.arrow,
.message .extraUserInfo,
.breadcrumb,
.nodeStats,
.discussionList .secondRow,
.previewTooltip .avatar,
#logoBlock,
.breadBoxTop,
.message .helper {
    display: none !important;
}

.onlineMarker {
    top: 3px !important;
    ;
    left: 3px !important;
    ;
    background-color: #3F6 !important;
    ;
    width: 4px;
    height: 4px;
    opacity: 0.8;
    border-width: 0px !important;
    ;
}

.previewTooltip .text {
    margin-left: 0px;
}


.likesSummary,
.messageMeta,
.editDate,
.message .userText {
    opacity: 0.0;
}

.message:hover .messageMeta,
.message:hover .likesSummary,
.message:hover .editDate,
.profilePostListItem:hover .messageMeta,
.message:hover .userText,
.tabLinks {
    opacity: 0.55;
}

.button {
    border: 1px solid #888 !important;
    box-shadow: 0px 0px 0px 0px rgb(0, 0, 0) !important;
}

/* mios ********************************************** */
/*div.p-pageWrapper{
 margin-left: auto;
  margin-right: auto;
  width: 70%;
}*/
/*.p-body,
.p-header,
.p-sectionLinks,
.p-navSticky{
    flex: 0 0 auto !important;
    max-width: 1000px;
}*/
.structItem-title a[id^='js-XFUniqueId'] {
    color: rgb(255, 150, 150) !important;
    font-weight: bold;
}
div.tooltip-content-inner div.bbWrapper {
    /* color: #ddd !important; */
    background-color: #000000a8 !important;
}

.menu.menu--wide {
    border: 1px solid #e6e494 !important;
    box-shadow: 1px 1px 1px 1px #bbb !important;
}

.menu.menu--wide,
div.menu-content div.js-filterMenuBody form .menu-row.menu-row--separated label {
    color: #fff !important;
    background-color: #555 !important;
}
div.menu-content div.js-filterMenuBody form .menu-row.menu-row--separated select {
    color: rgb(255, 248, 150) !important;
    background-color: #777 !important;
}
a.filterBar-menuTrigger,
.p-nav-scroller.hScroller .hScroller-scroll.is-calculated ul li div a,
.p-nav-opposite {
    color: rgb(255, 248, 150) !important;
    background-color: #00000087 !important;
}

.p-sectionLinks-inner.hScroller a.p-navEl-link {
    color: rgb(200, 255, 255) !important;
}

li[itemprop='itemListElement'] a[itemprop='item'] span[itemprop='name'] {
    color: rgb(255, 248, 150) !important;
}

time.u-dt {
    color: rgb(255, 248, 150) !important;
}
/*  admins */
span.username--staff.username--moderator.username--admin {
    color: #fff !important;
    font-size: larger;
    font-weight: bold;
}

/*div.p-title,*/
header.message-attribution.message-attribution--split,
div.message-cell.message-cell--user {
    background-color: #0000002e !important;
    margin: 10px 5px 10px 0px !important;
}

article.message-body {
    font-size: larger;
}

div.p-title h1.p-title-value {
    color: #ff9696 !important;
    font-weight: bold;
}

div.p-description {
    background-color: #ff969621 !important;
    border: 1px solid #ffffff36 !important;
}

.message-cell.message-cell--user {
    flex: 0 0 190px !important;
}

.message-userDetails {
    font-size: smaller;
}

div.message-cell.message-cell--main {
    margin: 0px 0px 7px 0px !important;
}

div.reactionsBar {
    background-color: #ff969621 !important;
}

.bbCodeBlock--quote {
    border: none;
}

div.bbCodeBlock-title {
    margin: 0px 0px 0px 33px !important;
}

div.bbCodeBlock-content {
    background-color: #ffff0008 !important;
    margin: 3px 3px 3px 33px !important;
    border: 1px solid #ff0 !important;
}

a.bbCodeBlock-sourceJump {
    color: #ff0 !important;
    font-size: small !important;
    font-weight: bold !important;
}

div.bbCodeBlock-title {
    color: rgb(255, 150, 150) !important;
}

div.tooltip-content,
div.overlay {
    background-color: #666 !important;
}

div.memberTooltip-header {
    background-color: #0000002e !important;
}
div.memberTooltip-info,
div.memberHeader-stats {
    background-color: #390169c7 !important;
}
div.memberHeader-blurb,
div.memberHeader-blurb dl.pairs dt {
    color: #bd9f28 !important;
}

dl.pairs dt {
    color: #9597ca !important;
    font-weight: bold !important;
}
dl.pairs dd a {
    text-decoration: underline !important;
}

dl.pairs dd {
    color: #2dbd28 !important;
}

div.node-stats dl.pairs dt,
div.structItem-cell.structItem-cell--meta dl.pairs dt {
    color: #fff !important;
}

div.node-stats dl.pairs dd,
div.structItem-cell.structItem-cell--meta dl.pairs dd {
    color: #DFB !important;
}

div.menu.menu--structural.menu--potentialFixed div.menu-content {
    background-color: #666 !important;
    border: none !important;
}
div.menu.menu--structural.menu--potentialFixed div.menu-content a {
    color: #fff !important;
}
div.menu.menu--structural.menu--potentialFixed div.menu-content a:hover {
    background-color: #6fb776 !important;
}
div.menu.menu--left.is-active.is-complete div.menu-content {
    background-color: #666 !important;
    border: 1px solid #ff0 !important;
}
div.menu.menu--left.is-active.is-complete div.menu-content a {
    color: #ff0 !important;
    font-size: larger !important;
}
div.menu.menu--left.is-active.is-complete div.menu-content a:hover {
    background-color: #6fb776 !important;
}
ul.listInline.listInline--bullet li dl.pairs dt,
div.block-body.block-row dl.pairs dt {
    color: #ffffff8f !important;
}
ul.listInline.listInline--bullet li dl.pairs dd,
div.block-body.block-row dl.pairs dd {
    color: #ff09 !important;
}

div.message-avatar-wrapper a[id^='js-XFUniqueId'] {
    width: auto !important;
    height: auto !important;
}

div.message-avatar-wrapper a[id^='js-XFUniqueId']:visited {
    color: rgb(255, 200, 150) !important;
}

div.message-avatar {
    display: block !important;
    float: left !important;
}

div.contentRow-main.contentRow-main--close a[href^='/community/threads/'],
a.node-extra-title[href^='/community/threads/'] {
    color: rgb(255, 150, 150) !important;
}
div.contentRow-main.contentRow-main--close a[href^='/community/threads/']:visited,
a.node-extra-title[href^='/community/threads/']:visited {
    color: #ffffff8f !important;
}

/* febrero*/
div.fr-toolbar input,
div.fr-input-line input,
div.fr-action-buttons button,
div.fr-popup,
span.fr-sr-only {
    background-color: #000 !important;
    color: white !important;
    box-shadow: none !important;
    line-height: 3 !important;
}

div.p-header-logo--image {
    display: none;
}

div.bbMediaWrapper-inner iframe,
div.bbMediaWrapper {
    height: 100px !important;
    width: 800px !important;
    overflow: hidden;
    position: static !important;
}
/* Febrero 02 */
div.message-userExtras {
    display: block;
    height: 25px !important;
    overflow: hidden;
}
div.message-userExtras:hover {
    height: auto !important;
    display: block;
}

h4.message-name {
    background-color: #ff969624 !important;
    padding: 3px 2px 3px 2px;
    font-size: larger;
    font-weight: bold;
}
div.message-avatar div a img,
div.message-avatar {
    vertical-align: top !important;
    text-align: left !important;
    max-height: 50px !important;
    width: auto !important;
}

div.message-avatar div a img:hover,
div.message-avatar:hover {
    vertical-align: top;
    min-height: 100px !important;
    min-width: 100px !important;
}
  article.message-body div a.link:visited{
   color: #b2d5f2db !important;
}

article.message-body div a.link{
   color: #ebf2b2de !important;
}
aside.message-signature div > img,
aside.message-signature div a img{
    max-height: 40px;
    width: 40px;
}

aside.message-signature div  br, aside.message-signature div span br{
    display:none;
}

  aside.message-signature div,
aside.message-signature div >span,
aside.message-signature div b >span,
aside.message-signature div span b,
aside.message-signature div span{
    color:#abeaab69 !important;
}

li.pageNav-page--current a{
     background-color: #ffa000 !important;
    color: #000 !important;
}

  
}
