css:
@-moz-document domain("bs.to") {
html {
    background-image: url("https://wallpapers.wallhaven.cc/wallpapers/full/wallhaven-738918.jpg") !important;
  background-size: cover !important;
  background-attachment: fixed !important;
  background-position: center center !important;
}

.andere-serien div.genre, .home>#teaser, .home>.column>section, body.exceeded>section {
    background-image: linear-gradient(#000000 0,#000000 24px);
    background-image: -moz-linear-gradient(#000000 0,#000000 24px);
    background-image: -webkit-linear-gradient(#000000 0,#191970 34px);
}
body,
body a {
    font-size: 14px;
    }

::-webkit-scrollbar-track-piece
  {
    background-color: #212121;
    -webkit-box-shadow: inset 0 0 0 rgba(0, 0, 0, 0) !important;
    border-radius: 0 !important;
    border: none !important;
  }

::-webkit-scrollbar {
    background-color: rgba(62, 62, 62, .92);
    border: 1.5px solid rgb(62, 62, 62);
    width: 15px;
}

::-webkit-scrollbar-thumb {
    background-color: rgba(31, 31, 31, 1);
    border: 1.5px solid rgb(62, 62, 62);
}

div.fb-like{
    height: 0px !important;
    overflow: hidden !important;
}

section.navigation{
    height: 0px !important;
    overflow: hidden !important;
}

section.navigation>a, section.navigation>div{
    height: 0px !important;
    overflow: hidden !important;
}

section.navigation>div:first-child {
    color: #C9C9C9;
    background: -webkit-linear-gradient(#000000,#191970 15px);
    border: 1px solid #000;
}

section.navigation>a, section.navigation>div{
    background: #1F1F1F;
    border: 1px solid #000;
}

section.navigation>a:hover {
    background: #191970 !important;
}
.home #sbMsg {
    background-color: #3e3e3e;
    border: 1px solid #000;
    color: #000000;
}

   .serie table.episodes {
    border-radius: 3px;
    box-shadow: 0px 0px 5px #455963;
}

.serie table.episodes tr {
    background-color: #263238;
    border-top: 1px solid #455963;
    padding: 3px 0px;
}

.serie table.episodes tr.watched {
    background-color: #455A64 !important;
    border-top: 1px solid #455963;
    padding: 3px 0px;
}

.serie table.episodes tr:hover,
.serie table.episodes tr.watched:hover {
    background-color: #536c78 !important;
}

.serie table.episodes td a {
    color: #ddd;
}

.serie table.episodes tr td:nth-child(1) a {
    background-color: #40525c;
}

.serie table.episodes tr.watched td:nth-child(1) a {
    background-color: #5a727f !important;
}

#sp_right a {
    margin-bottom: 5px;
}

.fas.fa-fw {
    width: 1.5em
}
nav>ul {
    background: linear-gradient(#000000 0,#000000 24px);
    background: -moz-linear-gradient(#000000 0,#000000 24px);
    background: -webkit-linear-gradient(#000000 0,#191970 34px);
    box-shadow: 0 0 2px rgba(0,0,0,.75),0 -1px 0 rgba(110, 17, 17, 0.7) inset;
    -moz-box-shadow: 0 0 2px rgba(0,0,0,.75),0 -1px 0 rgba(17,68,110,.7) i;
    -webkit-box-shadow: 0 0 2px rgba(0,0,0,.75),0 -1px 0 rgba(0, 0, 0, 0) inset;
}
  
nav>ul>li:hover {
    background: linear-gradient(#000000 0,#000000 24px);
    background: -moz-linear-gradient(#000000 0,#000000 24px);
    background: -webkit-linear-gradient(#000000 0,#000000 24px);
    box-shadow: 0 0 2px rgba(0,0,0,.75),0 -1px 0 rgba(110, 17, 17, 0.7) inset;
    -moz-box-shadow: 0 0 2px rgba(0,0,0,.75),0 -1px 0 rgba(17,68,110,.7) i;
    -webkit-box-shadow: 0 0 2px rgba(0,0,0,.75),0 -1px 0 rgba(0, 0, 0, 0) inset;
}


nav>ul>li>ul>li {
    border-color: #000 !important;
    color: #000 !important;
    background: rgba(0, 0, 0, 0.9) !important;
}

nav>ul>li>ul>li:hover {
    background: rgba(25,25,112) !important;
}

.serie .frame ul li.active {
    background: #191970 !important;
}


body>#root {
    box-shadow: 0 0 3px #000;
    -moz-box-shadow: 0 0 3px #000;
    -webkit-box-shadow: 0 0 3px #000;
    background: url(https://i.imgur.com/kY7z5Yr.jpg) no-repeat #2b2b2b;
}

body>#root>header a.banner {
    background: inherit !important;
}
.serie .infos div {
    padding: 4px 0px;
    /* offset-x | offset-y | blur-radius | spread-radius | color */
    box-shadow: 0px 3px 2px -2px #606060;
}

.serie .infos div span {
    min-width: 22%;
    max-width: 35%;
}

.serie .infos div p {
    max-width: 60%;
}

.serie .infos div p span:hover {
    background: #5a727f;
    padding: 1px 4px 1px 4px;
}

.serie .infos div p a {
    color: #a0d0ea;
}


#root>section {
    background-color: #1F1F1F !important;
    color: #DCDCDC;
}

nav>ul>li {
    border-right: 1px solid #000000;
}

form#login {
    box-shadow: 0 5px 10px rgba(0,0,0,.2);
    -moz-box-shadow: 0 5px 10px rgba(0,0,0,.2);
    -webkit-box-shadow: 0 5px 10px rgba(0,0,0,.2);
    background: linear-gradient(#000000 0,#000000 24px);
    background: -moz-linear-gradient(#000000 0,#000000 24px);
    background: -webkit-linear-gradient(#000000 0,#191970 144px);
}

.faq dt:target {
    background: #171717!important;
}

.faq dt:hover {
    background: #161616;
}

.faq dt {
    border: 1px solid #000;
    background: linear-gradient(to bottom,#1c1c1c 0,#2c1414 100%) rgba(0,0,0,0);
    background: -moz-linear-gradient(to bottom,#1c1c1c 0,#2c1414 100%) rgba(0,0,0,0);
    background: -webkit-linear-gradient(to bottom,#1c1c1c 0,#2c1414 100%) rgba(0,0,0,0);
}

.hoster-tabs>li.active>a:focus, .hoster-tabs>li.active>a:hover, .serie .hoster-tabs>li.active>a {
    background-color: rgba(255, 255, 255, 0.8);
    border-color: #ddd #ddd transparent;
    color: #555;
}

.andere-serien div.genre ul, .home>#teaser>div, .home>.column>section>div, body.exceeded>section>div {
    background-color: #313131;
}

.andere-serien div.genre ul>li>a, section#defect>div>ul>li>a, section#newest_episodes a>div:first-child, ul#seriesList>li>a.prop {
    color: #C9C9C9;
}
a {
    color: #939393;
}


.serie table.edit tr, .serie table.episodes tr {
    border: 1px solid #000;
}

.serie .scrollbar .handle {
    background: #191970;
}

.serie .epiInfo {
    background-color: rgba(255, 255, 255, 0.8);
    box-shadow: 0 0 1px 1px rgba(0,0,0,.2);
    color: black;
}

.home #shoutbox dl>dt, .home li:nth-child(even) {
    background-color: #3e3e3e;
}

section#news ul li {
    background-color: inherit !important;
}

section#news ul li .header {
    background-color: #191970;
}

.home #shoutbox dl>dt, .home li:nth-child(even) {
    background-color: #3e3e3e;
}

section#news>div>article>header {
    background-color: #654b4b;
}

.nice-select, .nice-select .list {
    background-color: rgba(0, 0, 0, 0.9) !important;
}

.nice-select {
    border-color: rgba(25,25,112) !important;
}

.nice-select .option.focus, .nice-select .option.selected.focus, .nice-select .option:hover {
    background-color: rgba(15, 0, 0, 0.9) !important;
}

}
