css:
@-moz-document domain("bdsmlr.com") {
:root {
--colour-hover-right: #122943;
--colour-main: #001935;
--colour-header: #001935;
--colour-header-border: #21374F;
--colour-search-box: #405368;
--colour-search-grey: #838f9d;
--colour-black: #000000;
--colour-black-dull: #000000C0;
--colour-white: #ffffff;
--colour-red: #ff492f;;
--colour-orange: #ff8a00;
--colour-green: #00cf35;    
--colour-blue: #00b8ff;
--colour-purple: #7c5cff;
--colour-grey: #ff62ce;    
--colour-notification: #00b8ff;
--colour-menu-grey: #a6afb8;
--colour-menu-hover: #E5EEF6;
--colour-names: #000000;
--colour-follow-button: #667586;
}
}

@-moz-document domain("bdsmlr.com") {
.activityunits:hover { background: var(--colour-hover-right); }
.menu-up:hover { background: var(--colour-menu-hover); }
.profilemenu a:hover,.postcount:hover { background: var(--colour-menu-hover) !important;}
.profilemenu.loaded { color: var(--colour-black)!important; opacity: 1.0 !important; }
.profilemenu.loaded > a,b,span { color: var(--colour-black) !important; opacity: 1.0 !important; }
.header .menu ul li:hover a {
    opacity: 1; color: var(--colour-black) !important;
}
.profilemenu .counts,.fa { color: var(--colour-menu-grey) !important; }

/* Top-Right Nav for Blogs */
div.topholder {
    background: var(--colour-header);
    right: 0px;
    top: 0px; padding: 3px 5px 5px 5px;
    border-radius: 0px 0px 0px 10px
}
div.topholder .holderopts {
    background-color: rgba(255, 255, 255, 0.1);
    margin: 0; 
    padding: 2px 10px;
    border-radius: 5px;
} 

/* Dashboard */
.content {
/* It's not quite where Tumblr's is*/
margin-left: -7px;
}
.upwrapper {
    margin-bottom: 40px;
}
.news {
    min-height: 99px !important;
}

.suggestiontitle {
color: var(--colour-white);
padding-left: 10px;
padding-right:10px;
margin-top: 0px;
}
.rightblog {
    margin-bottom: 5px;
    padding-left: 15px;
    padding-right: 15px;
    margin-top: -3px;
}
.content {
width: 1030px;
}
.content aside.sidebar {
    margin-left: 0px;
}
aside.sidebar {
    width: 350px;
    float: right;
}

.suggestionholder {
padding-left: 10px; padding-right: 10px;
width: 320px; 
}
.suggestionholder a.ndata {
width: 260px; display: inline-block;
}
.followsbutton {
font-size: 28px;
margin-left: 0px;
margin-top: 3px; margin-right: 0px; float: right;
color: var(--colour-follow-button);
}
.sugname {
    color: var(--colour-white) !important;
    white-space: pre;
    height: 30px; margin-bottom: -10px;
}
.suggestion {
    color: var(--colour-menu-grey);
    width:320px !important; display: inline-block;
}
.suggestionholder:hover { background: var(--colour-hover-right); }
.suggestion span:nth-of-type(2) { color: var(--colour-menu-grey) !important;}

.header {
    background: var(--colour-header);
    border-bottom: 1px solid;
    border-color: var(--colour-header-border) !important;
    /* overrides */
    height: 63px;
    /* overflow: hidden; */ /* This line will break the menu */
    position: fixed;
    margin-bottom: 21px;
}

.header a.logo img {
    /* padding: 18px 13px 0 15px; */
    float: left;
    margin-left: 0;
    display: none; width: 130px;
}

 .header a.logo::after {
    /* padding: 18px 13px 0 15px; */ padding: 0px; 
    content: "";
    float: left;
    margin-left: 19px;
    margin-top: 19px;
    display: block;
    background-color: rgba(0, 0, 0, 0) !important;
    width: 26px;
    height: 34px; 
    background-position: left top;
    background-attachment: local;
    background-repeat: no-repeat;
    object-fit: scale-down; 
    background-size: contain;
	background-image: url(data:image/svg+xml;base64,);
}

.header .search {
    float: left;
    margin-right: 80px;
    margin-top: 18px;
    margin-left: 15px;
    width: 540px; 
}

.header .search input::placeholder {
color: var(--colour-search-grey) !important; 
}
.header .search input {
    background-color: var(--colour-search-box); border: none;
    color: white; 
    border-radius: 3px; height: 34px; padding-left: 39px;
    font-weight: normal; padding-top: 0px;
    background-position: 12px 9px;
    background-attachment: local;
    background-repeat: no-repeat;
    object-fit: scale-down; 
    background-size: 18px;
    background-image: url(data:image/svg+xml;base64,);
}

.newsfeed .update {
    display: none;
}

/* Dashboard Post Buttons */

i.black {
    color: var(--colour-black) !important;
}

i.light-red {
    color: var(--colour-red) !important;
}

i.orange {
    color: var(--colour-orange) !important;
}

i.light-green {
    color: var(--colour-green) !important;
}

i.light-blue {
    color: var(--colour-blue) !important;
}

i.violet {
    color: var(--colour-purple) !important;
}

i.gray {
    color: var(--colour-grey) !important;
}

body {
    background: var(--colour-main) !important;
}
/* Top Menu Push Down More */
header .menu {
    margin-top: 20px !important;
}
/* Dashboard Notifications Colour */
#counter {
    padding: 3px 4px; margin-left: -25px;
    background: var(--colour-notification);
    color: var(--colour-main);
    min-width: 21px !important;  text-align: center;
    border: 2px solid;
    border-color: var(--colour-main);
    border-radius: 4px;
}
/* Keep Dashboard Home Button Opaque */
a.m_home { opacity: 1 !important;}
/* Hide Ads */
div.atitle, div.aholder {
display: none;
}

/* Increase Contrast of Blogger Names */
.post_content a.ndata, .post_info a {
    color: var(--colour-names) !important; 
}
div.activityunits.tooltipstered > a.ndata

/* To Top Button */
.fa-arrow-circle-up,.fa-arrow-circle-up::before  {
    color: var(--colour-search-grey); opacity: 1;
    left: auto !important;
    right: 10px !important; position: fixed; bottom: 10px;
}

/* Stop Create Menu Jitters */
div.create {
    overflow: hidden;
    height: 99px; padding: 5px 0px 0px 0px !important;
}
div.create .post-triggers {
margin: 0px !important;
padding-left: 0px !important;
}
div.create .post-triggers li {
margin-top: -9px; 
width: 70px !important; 
}

div.create ul.post-triggers li a span {
color: var(--colour-black); font-weight: 100 !important;
}
.profilemenu, .menumessageholder,.activitymenuholder {
right: 10px; position: absolute; z-index: 1; display: block;
width: 300px;
height: 600px;
}

/* Try to reduce annoyances */
div.feed.clearfix > div.avatar + div.news.panel  > .post_content > .post_info > a[href=""] {
display: none;
}
div.feed.clearfix > div.avatar > a[href=""] > i.fas.fa-ad {
display: none;
}
/* Adjust LikeContainer icons */
#likecontainer .likesheart { color: var(--colour-red) !important;}
#likecontainer .likeheart, #likecontainer .fa-ellipsis-h, #likecontainer .fa-comment, #likecontainer .fa-retweet, #likecontainer .fa-cog { color: var(--colour-black-dull) !important; }

}
