css:
@-moz-document domain("forum.aion.gameforge.com") {
* {
	box-sizing: border-box;
    color: #8a8a8a;
}
a,
a:hover,
.messageReduced .messageTitle a,
.messageReduced .messageTitle a:hover{
	color: #8a8a8a;
}
body {
	background: #0c0c0c;
    overflow-x: hidden;
    font-size: 14px;
    font-family: 'Arial', sans-serif;
}
#content a:hover, .sidebar a:hover {
    text-decoration: none;
}
button.buttonPrimary, input[type="button"].buttonPrimary, input[type="submit"], .button.buttonPrimary, a.button.buttonPrimary {
    background: red;
}
.contentTitle {
	padding: 10px 10px;
}
#content .box, .sidebar .box {
    border: 0;
}
#content .boxTitle, #content .section.sectionContainerList > .sectionHeader, #content .sectionTitle {
    color: #7e7e7e;
}
.button {
    background: #6e6e6e !important;
    color: #1a1a1a !important;
}
.button:hover {
	background: #4e4e4e !important;
    color: #a4a4a4 !important;
}
button.active, input[type="button"].active, input[type="reset"].active, input[type="submit"].active, .button.active, a.button.active, button:hover, input[type="button"]:hover, input[type="reset"]:hover, input[type="submit"]:hover, .button:hover, a.button:hover {
    background: #6e6e6e;
    color: #1a1a1a;
}
.dialogContainer > .dialogContent,
.dialogContainer > .dialogContent .dialogFormSubmit {
    background: #4e4e4e;
}
.containerList > li:not(:last-child){
	border-color: #3e3e3e;
}
.containerList > li:hover {
    background: rgba(120, 120, 120, .1);
}
.pagination,
.contentHeader {
    padding: 0 10px;
}
.pagination > ul > li.active > a, .pagination > ul > li > span, .pagination > ul > li,
.pagination > ul > li.active > a, .pagination > ul > li.active > span, .pagination > ul > li,
.pagination > ul > li.active > a, .pagination > ul > li.active > span, .pagination > ul > li > a {
	background: transparent;
    color: #FFF;
}
.pagination > ul > li.active > a, .pagination > ul > li.active > span, .pagination > ul > li > a:hover {
	background-color: #1e1e1e;
    color: #f4f4f4;    
}
.userMention {
    background-color: #3e3e3e;
    color: #FFF !important;
}
.pageHeaderPanel {
    background-color: #0c0c0c;
	border-bottom: 1px solid #2c2c2c;   
}
.pageHeaderFacade {
    display: none;
}
.pageNavigation {
    margin-top: 60px;
    max-width: 1400px;
    border-left: 1px solid #2c2c2c;
    border-right: 1px solid #2c2c2c;
    border-top: 1px solid #2c2c2c;
    border-bottom: 1px solid #2c2c2c;

    background: #0c0c0c;
}
.pageNavigation .breadcrumbs > ol > li > a {
    color: #dadada;
    text-decoration: none;
    font-size: 1em;
}
.pageNavigation .breadcrumbs > ol > li > a:hover {
    color: #7e7e7e;
}
.pageNavigation .icon:before{
    color: #dadada;
}
.pageNavigation .icon:hover:before{
    color: #7e7e7e;
}
.tabularBoxTitle > header, .tabularListRowHead {
	background: #0e0e0e;
    /*border: 1px solid #4e4e4e;*/
    border: 0;
    border-radius: 0;
    text-align: left;
}
.main {
    padding: 0;
}
.info {
    background-color: #2c2c2c;
    border-color: red;
}
.sidebar {
    display: none;
}
.message {
	border-top: 1px solid #2c2c2c;
    border-width: 1px !important
}
.messageHeaderMetaData 
.messagePublicationTime {
    color: #7e7e7e;
}
.messageSignature {
    border-top: 1px solid #2c2c2c;
}
.mainMenu .boxMenu > li > a,
.userPanel > ul > li > a {
	background: #0c0c0c;
    color: #dadada;
}
.mainMenu .boxMenu > li.active > a, 
.mainMenu .boxMenu > li:hover > a {
    background: #1b1b1b;
    color: #fafafa;
}
.wbbCollapsibleCategory h2 > a:hover {
	color: #7e7e7e;
}
.badge, a.badge,
a.badge.label{
	color: #fff;
}
.badge.badgeUpdate, 
a.badge.badgeUpdate {
    background-color: #4e4e4e;
    color: #dadada;
    box-shadow: unset;
}

.mainMenu .boxMenu .boxMenuDepth1 {
	border-radius: 0;
}
.mainMenu .boxMenu .boxMenuDepth1 > li > a:hover {
    background: #0c0c0c;
    color: #fff;
}

.sidebar .boxContainer .box:first-child {
    display: none;
}
.content > .section {
    border: 0;
}
#main .layoutBoundary {
	background: #0c0c0c;
    border-left: 1px solid #2c2c2c;
    border-right: 1px solid #2c2c2c;
    border-bottom: 1px solid #2c2c2c;
    padding: 10px;
}
.popover,
.popoverContent {
    background: #2c2c2c;
}
.mainMenu .boxMenu .boxMenuDepth1 > li.active > a, .mainMenu .boxMenu .boxMenuDepth1 > li > a:hover {
	background: #2c2c2c;
}
.section {
    background: #1e1e1e;
    padding: 10px;
    color: #8a8a8a;
}
.section a {
    color: #4e4e4e;
}
.section a:hover {
    color: #5e5e5e;
}
.userAvatarImage {
    border: 1px solid #2a2a2a;
    background: transparent;
	border-radius: 50%;
}
.messageSidebar {
	background: #1a191c;
    border-right: 1px solid #323232;
}
.message {
    background: #1a1a1a;
    color: #8a8a8a;
}
.content > .section, .content > form, .sectionContainer {
     background: #2c2c2c;
}
.content:not(:last-child) {
    flex-basis: calc(100%);
    max-width: calc(100%);
}
.quoteBox {
    background-color: #323232;
}
.pagination > ul > li.disabled {
    display: none;
}
.htmlContent table tr:hover > td, 
.messageBody > .messageText table tr:hover > td, 
.messageSignature > div table tr:hover > td, 
.redactor-layer table tr:hover > td, 
.table tr:hover > td, 
.messageTableOverflow > table tr:hover > td {
    background: transparent;
}
.redactorAutosaveRestored {
    background: #3e3e3e;
}
woltlab-quote, woltlab-spoiler, .quoteBox,.redactor-layer pre {
	background: #1f1f1f !important;
    border: 1px solid #2e2e2e;
}
.quoteBoxIcon {
	margin: 0;
    padding: 10px 30px;
}
.quoteBoxIcon > a > img  {
	filter: grayscale(.5);
}
.quoteBoxIcon > a > img:hover  {
	filter: grayscale(0);
}
.quoteBox > .toggleButton,
.quoteBox.collapsed > .toggleButton {
    margin-top: 30px;
	box-shadow: 0 -10px 50px 10px #1a1a1a;
    background: #3e3e3e;
    font-style: normal;
    font-size: .8em;
    text-transform: uppercase;
    padding-bottom: 10px;
}
.interactiveDropdownItemsContainer {
	background: #1e1e1e;
}
.interactiveDropdownItems > li a {
	color: #4e4e4e;
}
.interactiveDropdownItems > li a:hover {
    color: #5e5e5e;
}
.dialogContainer{
	background: #3e3e3e;
}
.inputItemList,
input[type="date"], 
input[type="datetime"], 
input[type="email"], 
input[type="number"], 
input[type="password"], 
input[type="search"], 
input[type="tel"], 
input[type="text"], 
input[type="url"], 
select, 
textarea,
.inputItemList:hover,
input[type="date"]:hover, 
input[type="datetime"]:hover, 
input[type="email"]:hover, 
input[type="number"]:hover, 
input[type="password"]:hover, 
input[type="search"]:hover, 
input[type="tel"]:hover, 
input[type="text"]:hover, 
input[type="url"]:hover, 
select:hover, 
textarea:hover,
.inputItemList:focus,
input[type="date"]:focus, 
input[type="datetime"]:focus, 
input[type="email"]:focus, 
input[type="number"]:focus, 
input[type="password"]:focus, 
input[type="search"]:focus, 
input[type="tel"]:focus, 
input[type="text"]:focus, 
input[type="url"]:focus, 
select:focus, 
textarea:focus {
    margin: 0;
    background: #3e3e3e !important;
    border: 1px solid #4f4f4f;
    box-shadow: unset;
    border-radius: 0;
    color: #9f9f9f;
}
.redactor-box {
	background: #2a2a2a;
}
.redactor-toolbar,
.messageTabMenuNavigation > ul{
	background: #4e4e4e;
    border: unset;
}
.messageTabMenuNavigation a span {
    color: #cfcfcf;
}
.redactor-toolbar > li > a:hover {
	background: #2e2e2e;
}
.redactor-toolbar > li > a:hover span {
	color: #1a1a1a;
}
.messageTabMenu:not(.messageTabMenuContent) > .messageTabMenuContent.active {
 	background: #3e3e3e; 
}
.messageTabMenu > .messageTabMenuContent.active {
	background: transparent;
}
.messageTabMenuNavigation > ul > li {
	border: 0;
}
.messageTabMenuNavigation > ul > li.active > a::after {
    display: none;
}
.messageTabMenu.messageTabMenuContent > nav > ul > li > a {
    color: #6f6f6f;
}
.messageTabMenu.messageTabMenuContent > nav > ul > li.active > a {
    color: #f2f2f2;
}
button, button.buttonPrimary, input[type="button"].buttonPrimary, input[type="submit"], .button.buttonPrimary, a.button.buttonPrimary, a.button  {
    background: #2e2e2e !important;
    color: #afafaf !important;
    box-shadow: unset;
    border: 0;
}
buttony:hover, button.buttonPrimaryy:hover, input[type="button"].buttonPrimaryy:hover, input[type="submit"]:hover, .button.buttonPrimaryy:hover, a.button.buttonPrimary:hover {
	color: #3e3e3e;
    border: 0 !important;
}
#content .buttonList.smallButtons a.button {
    color: #7e7e7e !important;
}
#content .buttonList.smallButtons a.button:hover {
    border: 0;
    text-decoration: none;
}
.formSubmit {
	text-align: left;
}
woltlab-quote:not(.redactorCalcHeight)::before,
woltlab-spoiler:not(.redactorCalcHeight)::before,
.redactor-layer pre:not(.redactorCalcHeight)::before, 
.redactor-layer pre.woltlabHtml::before{
    color: #fff !important;
}
.badge, a.badge {
    background-color: #1e1e1e;
    color: #a2a2a2;
}
.boxesFooterBoxes a,
.pageFooter a{
    color: #3e3e3e !important;
}
.wbbBoardList .wbbBoardDescription,
.contentHeaderDescription{
	color: #6e6e6e !important;
}
.wbbBoardList .icon {
    color: #8e8e8e !important;
}
dl.statsDataList > dt,
.messageGroupListStatsSimple{
	color: #8e8e8e;
}
.containerList > li .buttonGroupNavigation > ul {
	background: #3e3e3e;
}
.containerList > li .buttonGroupNavigation > ul > li > a > span {
    color: #afafaf !important;
}
.boxesFooterBoxes {
    background-color: #0d0d0d;
}
.boxesFooter {
	background-color: #1a1a1a;
}
.pageFooterCopyright {
	background-color: #2a2a2a;
}
.pageFooterCopyright a {
    color: #4e4e4e !important;
}
.messageList:not(.messageReducedList) > li {
	border: 0;
}
.pagination {
    background-color: #1c1c1c;
    padding: 5px;
}
.pagination li a {
    color: #a2a2a2 !important;
}
.pagination li.active {
	color: #a0a0a0 !important;
}
.commentListAddComment, .redactor-layer {
	background: #3e3e3e !important;
    box-shadow: unset;
    border: 0;
}
.messageTabMenu:not(.messageTabMenuContent) > .messageTabMenuContent.active {
	border: 0;
}
.messageTabMenu.messageTabMenuContent > nav {
    border: 0;
}
.redactor-toolbar > li > a.redactor-act, .redactor-toolbar > li > a.dropact {
	background-color: #6e6e6e;
}
.boxesFooterBoxes .boxTitle {
    color: #c2c2c2;
}
.messageShareButtons a:hover {
    background: #1a1a1a !important;
}
.popover, .popoverContent a {
    color: #5e5e5e;
}
.popover, .popoverContent a:hover {
    color: #7e7e7e;
}
.popover .buttonList a span {
    color: #5e5e5e !important;
}
.contentHeader .contentTitle, h1 {
	color: #7e7e7e;
}
.userPanel > ul > li > a .icon {
    color: #7e7e7e !important;
}

.userPanel > ul > li.dropdownOpen > a, .userPanel > ul > li.open > a, .userPanel > ul > li:hover > a {
	background: #2e2e2e;
}
.interactiveDropdownHeader,
.interactiveDropdownShowAll{
	background: #0c0c0c;
}
.interactiveDropdownItemsContainer {
    border: #4a4a4a;
}
.interactiveDropdownShowAll {
    color: #4e4e4e !important;
}
.interactiveDropdownItems > li:not(:last-child) {
    border-color: #3a3a3a;
}
.interactiveDropdownItems > li:hover {
	background: #1a1a1a;
}
.mainMenu .boxMenu .boxMenuDepth1 {
    background: #0a0a0a;
}
#content .box, .sidebar .box,
.tabularBoxTitle > header, .tabularListRowHead,
.wbbBoardList .wbbBoard{
    background: #1e1e1e;
}
.tabularListRow:not(.tabularListRowHead) {
	border-top: 1px solid #3e3e3e;
}
.wbbBoardList .wbbBoard:hover,
.tabularListRow:not(.tabularListRowHead):hover {
    background: #1a1a1a;
}
.tabularListRowHead > .tabularListColumns > li {
    color: #7a7a7a;
}
#content .boxTitle, #content .section.sectionContainerList > .sectionHeader, #content .sectionTitle {
    border-bottom-color: #4e4e4e;
}
.embeddedAttachmentLink, .embeddedImageLink {
    border: 0;
}
.userProfileUserWithCoverPhoto .userProfileCoverPhoto {
    background-image: unset !important;
}
.wbbBoardList .wbbBoardContainer {
    border-bottom-color: #3e3e3e;
}
#content .box, .sidebar .box, .tabularBoxTitle > header, .tabularListRowHead, .wbbBoardList .wbbBoard {
    padding: 10px;
}
.wbbBoardList > li.wbbCategory:not(:last-child) {
    margin-bottom: 0;
}
.wbbCollapsibleCategory h2 > a {
    color: #7a7a7a;
}
.htmlContent table, .messageBody > .messageText table, .messageSignature > div table, .redactor-layer table, .table {
    border: 0;
}
.messageQuickReplyCollapsed .messageQuickReplyContent {
	background: #2e2e2e;
}
.messageQuickReplyCollapsed .messageQuickReplyContent::before,
.messageQuickReplyCollapsed .messageQuickReplyContent::after {
    color: #7e7e7e;
}
.messageReduced .messageHeader {
    background: #4e4e4e;
}
.menuOverlayItemList {
    background: #1e1e1e;
}
.menuOverlayItemLink,
.menuOverlayItemLinkIcon{
    background: #2e2e2e;
}
.menuOverlayItemLink.active, .menuOverlayItemLinkIcon.active {
	background: #4e4e4e;
}
.sidebar + .content:not(:last-child) {
        max-width: 100%;
}
.mainMenu .mainMenuShowNext {
    background: linear-gradient(to right, transparent 0%, rgba(3, 3, 3, 1) 75%);
}
}
