css:
@-moz-document url-prefix("https://habitica.com/") {
.tasks-column.reward {display:none;}
.tasks-list .column-background {display:none !important;}
.task-title {padding-bottom:0 !important;}
.task-title p {margin-bottom:0;}
.tasks-column.habit .task-title p {padding-right:70px;}
.task-control {width:16px;height:16px;margin-top:11px !important;}
.left-control {width:35px !important;min-height:24px !important;}
.habit-control {margin-top:-11px !important;}
.habit-control .positive {margin-top:3px;}
.habit-control .negative {margin-top:7px;}
.check.svg-icon {margin-top:13px !important;}
.task-content .small-text {position:absolute;top:5px;right:14px;}
.tasks-column.habit .task-content .small-text {right:55px;}
.tasks-column.todo .task-content .small-text {display:none !important;}
.tasks-column.col-lg-3 {flex: 0 0 33%;max-width:33%;}
#app-header {background-color:#000 !important;border-top:1px solid #444;border-bottom:1px solid #444;}
#app-header * {color:#eee !important;}
#create-task-btn {position:absolute;top:40px;right:0px;background:#444;}
.tasks-navigation .btn {padding:5.5px 15.5px;}
.filter-panel {max-width:initial !important;}
.filter-panel p {white-space:nowrap;}
.filter-panel .col-6 {flex:0 0 100%;max-width:100% !important;}
body {background-color:#0a0a0a;color:#eee;}
h2 {color:#eee;}
.filter.active {color:#fff !important;border-bottom:0 !important;}
.badge-purple {background:#444;}
.tasks-list {background:#222 !important;}
.task-content {background:#444 !important;color:#eee !important;}
.task-title {color:#eee !important;}
.task-disabled-habit-control-bg {background-color:#555;}
.left-control + .task-content {border-radius:0 !important;}
.topbar {background:#000 !important;}
.footer-row {display:none;}
.quick-add {padding: 0 !important;}
.quick-add:focus {padding: 12px 16px !important;background:#555 !important;color:#fff !important;}
.quick-add-tip {display:none !important;}
.topbar-item:hover {background:#555 !important;}
.topbar-item:hover .topbar-dropdown {background:#555 !important;}
.topbar-item:hover .topbar-dropdown .topbar-dropdown-item:hover {background:#777 !important;}
.sidebar,.standard-sidebar {background-color:#000 !important;}
.sidebar h3,.standard-sidebar h3 {color:#eee;}
.quest-active-section .quest-box {background:#111 !important;color:#eee !important;}
.quest-active-section .quest-box h4, .quest-active-section .quest-box a {color:#eee !important;}
.grey-progress-bar {background-color:#333 !important;margin-bottom:10px;}
.boss-health-bar {background-color:#888 !important;}
.card {background:#000 !important;}
.card-body {background:#000 !important;}
.card-body h3, .card-body p, .card-body ul {color:#eee !important;}
.card-body p.time {font-size:11px !important;}
.card-body hr {border-color:#333;}
.item-with-icon {background:#000 !important;}
h1, h3 {color:#eee !important;}
.secondary-menu {background-color:#000 !important;}
.nav-link.active {box-shadow:0 4px 0 #eee !important;color:#fff !important;}
.topbar .topbar-item.active:not(:hover) {box-shadow:none !important;}
.secondary-menu a {color:#fff !important;font-size:14px !important;padding:11px 14px !important;}
.secondary-menu a:hover {background:#555 !important;color:#fff !important;}
.dropdown-label {color:#eee;}
.dropdown-item:active, .dropdown-item:hover, .dropdown-item.active {background-color:#eee;color:#111;}
.btn-secondary:active, .btn-secondary:focus, .btn-secondary.active, .dropdown > .btn-secondary:active, .dropdown > .btn-secondary:focus, .dropdown > .btn-secondary.active,.dropdown.show > .dropdown-toggle:not(.btn-success) {border-color: #666 !important;}
.btn-show-more:hover {color:#666 !important;}
.topbar-item:hover .topbar-dropdown .topbar-dropdown-item.active {background:#333 !important}
.at-text {color:#dada00 !important;}
.modal-content {background-color:#222;}
.modal-content h2, .modal-content h4, .modal-content h5 {color:#fff;}
.edit-avatar h3 {color:#333 !important;}
h3.purple {color:#333 !important;}
.svg-icon svg {color:#ddd;}
.task:hover {box-shadow:0 1px 8px 0 rgba(26, 26, 26, 0.12), 0 4px 4px 0 rgba(26, 26, 26, 0.16) !important}
.task:hover .left-control, .task:hover .right-control, .task:hover .task-content {border-color:#999 !important;}
.row.tasks-navigation {display:none;}
.chat-row textarea {background-color:#000 !important;color:#fff !important;border-color:#333 !important;}
.item {border-radius:15px !important;background-color:#eee !important;}
h4 {color:#eee !important;}
.challenge {background-color:#000 !important;}
.challenge .well {background-color:#333 !important;}
.challenge .well > div {color:#eee !important;}
.challenge a {color:#eee !important;}
.challenge p {color:#eee !important;}
.category-label {background-color:#333;}
.challenge .challenge-prize {background-color:#333 !important;border-bottom-left-radius:12px !important;}
}
