css:
@-moz-document 
url-prefix("http://steamcommunity.com/profiles/"), 
url-prefix("https://steamcommunity.com/profiles/"),
url-prefix("http://steamcommunity.com/id/"), 
url-prefix("https://steamcommunity.com/id/"),
url-prefix("http://steamcommunity.com/my/"),
url-prefix("https://steamcommunity.com/my/") {

/*Last update on Feb 2019*/

.profile_header_bg {
max-width: 1190px;
}

.profile_header_bg {
top: 50px;
}

.profile_header_bg_texture {
background-image: url( 'https://steamcdn-a.akamaihd.net/steamcommunity/public/assets/lny2019/goldenprofile/profile_header_bg_texture_gold.jpg' );
background-position: bottom;
}

.profile_header_bg_texture:before {
background: url("https://i.imgur.com/UI37OyZ.png") no-repeat scroll center top transparent; 
z-index: 2 !important; 
pointer-events: none; 
padding-right: 25px; 
padding-left: 25px; 
position: absolute; 
content: ""; 
bottom: 0px; 
right: 0px; 
left: 0px; 
top: -60px; 
max-width: 1190px;
}

.profile_content {
top: 43px;
padding-left: 37px;
padding-right: 37px;
width: 926px;
left: 0;
right: 0;
margin: auto;
float: none;
position: relative;
border-top: 1px solid rgba( 228, 202, 99, 0.5 );;
}
 
.profile_background_overlay_content { 
 background: url( 'https://steamcdn-a.akamaihd.net/steamcommunity/public/assets/lny2019/goldenprofile/LNY_bg_pattern.jpg' ) !important;
background-size: 100px 100px;
opacity: 1;
}

.profile_customization {
background-image: url( 'https://steamcdn-a.akamaihd.net/steamcommunity/public/assets/lny2019/goldenprofile/showcase_bg_gold.png' );
background-color: #0b0c0e;
}

.profile_header_badge {
background: #0b0c0e;
border: 1px solid rgba( 228, 202, 99, 0.5 );
}

.screenshot_showcase_screenshot:hover {
border: 1px solid #e4ca63;
}

.profile_comment_area .commentthread_header, 
.profile_leftcol_header {
background: rgba(251, 214, 10, 0.75);
color: #3c1802;
}

.showcase_stat.favoritegroup_inchat {
background: rgba( 228, 202, 99, 0.5 );
}

.profile_comment_area .commentthread_paging {
    background: rgba( 0, 0, 0, 0.75 );
    padding: 0 4px;
    border-radius: 4px;
}

/*Texts*/
.persona_name, 
.profile_customization_header, 
div.bb_h1, 
.bb_wysiwyg h1, 
.persona.in-game, 
a.persona.in-game, 
.persona.in-game a, 
.friend_status_in-game, 
.friend_status_in-game div, 
.friend_status_in-game a, 
.btn_grey_black, 
.persona.online, 
a.persona.online, 
.persona.online a, 
.friend_status_online, 
.friend_status_online div, 
.friend_status_online a {
color: #e4ca63 !important;
}

.profile_leftcol_header h2 {
    color: #3c1802;
}

/*Better Avatars*/
.playerAvatar.online {
background: -webkit-linear-gradient( 145deg, #d5bf6a 10%,#ffe2a9 40%,#ba995c 52%,#ba995c 61%,#d5bf6a 85%);
background: linear-gradient( 145deg, #d5bf6a 10%,#ffe2a9 40%,#ba995c 52%,#ba995c 61%,#d5bf6a 85%);;
}
.playerAvatar.in-game {
background: -webkit-linear-gradient( 145deg, #d5bf6a 10%,#ffe2a9 40%,#ba995c 52%,#ba995c 61%,#d5bf6a 85%);
background: linear-gradient( 145deg, #d5bf6a 10%,#ffe2a9 40%,#ba995c 52%,#ba995c 61%,#d5bf6a 85%);
}
.playerAvatar.offline {
background: -webkit-linear-gradient(top, #959595 0, #333 100%);
background: linear-gradient(to bottom, #959595 0, #333 100%);
}

/*Improved borders for avatars*/
.playerAvatar {
padding: 3px !important;
border-radius: 2px !important;
}

/*Fixes borders for profile avatar*/
.profile_header .playerAvatarAutoSizeInner {
margin: 3px !important;
border-radius: 2px !important;
}

/*Re-align the profile summary a little bit.*/
.profile_header .playerAvatar.profile_header_size {
margin-right: 14px;
}

/*Border Left*/
.profile_header_bg:before{
content: "";
position: absolute;
width: 25px;
z-index: 1;
left: 95px;
right: 0;
top: 0;
bottom: 0;
background: url( 'https://steamcdn-a.akamaihd.net/steamcommunity/public/assets/lny2019/goldenprofile/side_gold.jpg' ) 0px -50px repeat-y;
}

.profile_content:before{
content: "";
position: absolute;
width: 25px;
z-index: 1;
left: 0;
right: 0;
top: 0;
bottom: 0;
background: url( 'https://steamcdn-a.akamaihd.net/steamcommunity/public/assets/lny2019/goldenprofile/side_gold.jpg' ) 0px 231px repeat-y;
}

/*Border Right*/
.profile_header_bg:after{
content: "";
position: absolute;
width: 25px;
z-index: 1;
left: 1070px;
right: 0;
top: 0;
bottom: 0;
background: url( 'https://steamcdn-a.akamaihd.net/steamcommunity/public/assets/lny2019/goldenprofile/side_gold.jpg' ) 0px -50px repeat-y;
transform: scaleX(-1);
}

.profile_content:after{
content: "";
position: absolute;
width: 25px;
z-index: 1;
left: 975px;
right: 0;
top: 0;
bottom: 0;
background: url( 'https://steamcdn-a.akamaihd.net/steamcommunity/public/assets/lny2019/goldenprofile/side_gold.jpg' ) 0px 231px repeat-y;
transform: scaleX(-1);
}

/*Images*/
.screenshot_showcase_staticon.favorite { 
filter: hue-rotate(200deg) saturate(1.9);}

[src="https://steamcommunity-a.akamaihd.net/public/images/sharedfiles/icons/icon_rate.png"]{
filter: hue-rotate(205deg) saturate(1.9);
}

/*Images*/
.screenshot_showcase_staticon.favorite{ 
filter: hue-rotate(200deg) saturate(1.9);
}

[src="https://steamcommunity-a.akamaihd.net/public/images/sharedfiles/icons/icon_rate.png"]{
filter: hue-rotate(205deg) saturate(1.9);
}

.published_file_icon {
filter: hue-rotate(195deg) saturate(5.1) brightness(2.4);
}

}
