css:
@-moz-document domain("twitter.com") {
/*BEGIN 2019-02-16*/

/*Main background*/
body {
    background: #1b1b1b;
}
.ProfileHeading-toggle {
    background-color: #303030;
}

/*Page header*/
.global-nav-inner {
    background: #303030;
    background-color: rgb(48, 48, 48);
}

/*Search pill*/
.global-nav .search-input {
    background-color: #000000;
    color: #14171a;
}

/*Profile header*/
.ProfileCanopy-navBar {
    background-color: #303030;
}

/*Edit profile button surroundings*/
.ProfileNav-item--userActions {
    background-color: #303030;
}

/*Edit profile button*/
.EdgeButton--tertiary {
    background-color: #1b1b1b;
    border: 1px solid #66757f;
    border-top-color: rgb(102, 117, 127);
    border-right-color: rgb(102, 117, 127);
    border-bottom-color: rgb(102, 117, 127);
    border-left-color: rgb(102, 117, 127);
    color: #66757f;
}

/*Shared tweet background*/
.TwitterCard .MomentCard {
    background-color: #303030;
}

/*"Tweets" header band*/
.ProfileHeading-toggleItem.is-active, .ProfileHeading-toggleItem.is-active:hover, .ProfileHeading-toggleItem.is-active:focus {
    color: #b5d1ec;
}

/*Tweet background*/
.tweet, js-stream-tweet, js-actionable-tweet, js-profile-popup-actionable, dismissible-content, original-tweet, js-original-tweet, my-tweet has-cards cards-forward {
    background-color: #303030;
}

/*Tweet border between tweets*/
.tweet {
    border-bottom: 1px solid #4c4c4c;
    border-bottom-color: rgb(76, 76, 76);
}

/*Tweet owner*/
.fullname {
    color: #c0c0c0;
}

/*Tweet text*/
body {
    color: #c0c0c0;
}

/*Shared thumbnail*/
.TwitterCard {
    background: #303030;
    background-color: rgb(48, 48, 48);
}

/*Shared thumbnail border*/
.TwitterCard-container {
    border-color: #5f5f5f;
    border-top-color: rgb(95, 95, 95);
    border-right-color: rgb(95, 95, 95);
    border-bottom-color: rgb(95, 95, 95);
    border-left-color: rgb(95, 95, 95);
}

/*Shared thumbnail header text*/
.TwitterCard-title, js-cardClick, tcu-textEllipse--multiline {
    color: #c0c0c0;
}

/*Shared thumbnail text*/
.tcu-resetMargin, u-block, TwitterCardsGrid-col--spacerTop, tcu-textEllipse--multiline {
    color: #c0c0c0;
}

/*Who to follow panel*/
.module .flex-module {
    background-color: #303030;
    border: 1px solid #14171a;
    border-top-color: rgb(48, 48, 48);
    border-right-color: rgb(48, 48, 48);
    border-bottom-color: rgb(48, 48, 48);
    border-left-color: rgb(48, 48, 48);
}

/*Who To Follow divider lines*/
.wtf-module .UserSmallListItem {
    border-bottom: 1px solid #485664;
    border-bottom-color: rgb(72, 86, 100);
}

/*Who to follow header text*/
.flex-module-header h3 {
    color: #c0c0c0;
}

/*Side panel text*/
.ProfileSidebar .flex-module-header h3 {
    color: #c0c0c0;
}

/*Trends for you panel*/
.Trends {
    background-color: #303030;
    border-top-color: rgb(0, 0, 0);
    border-right-color: rgb(0, 0, 0);
    border-bottom-color: rgb(0, 0, 0);
    border-left-color: rgb(0, 0, 0);
}

/*Tweet bar*/
.home-tweet-box, .LiveVideo-tweetBox, .RetweetDialog-commentBox {
    background-color: #303030;
}

/*tweet bar input*/
input, textarea, div[contenteditable], .t1-select {
    background-color: #1b1b1b;
}

/*tweet input text*/
input:focus, textarea:focus, div[contenteditable="true"]:focus, div[contenteditable="true"].fake-focus, div[contenteditable="plaintext-only"]:focus, div[contenteditable="plaintext-only"].fake-focus {
    outline: 0;
    outline-color: initial;
    outline-style: initial;
    outline-width: 0px;
    color: #c0c0c0;
    border-color: rgba(66, 92, 101, .32);
    border-top-color: rgba(0, 132, 180, 0.5);
    border-right-color: rgba(0, 132, 180, 0.5);
    border-bottom-color: rgba(0, 132, 180, 0.5);
    border-left-color: rgba(0, 132, 180, 0.5);
}

/*See <x> new tweets*/
.new-tweets-bar {
    background-color: #303030;
    border-bottom: 1px solid #e6ecf0;
    border-bottom-color: rgb(230, 236, 240);
    border-top: 1px solid #e6ecf0;
    border-top-color: rgb(230, 236, 240);
    color: #1da1f2;
}

/*"+" button next to tweet button*/
.EdgeButton--secondary {
    background-color: #303030;
}

/*Your profile box, top-left of page*/
.DashboardProfileCard {
    border: 1px solid #000000;
    border-top-color: rgb(0, 0, 0);
    border-right-color: rgb(0, 0, 0);
    border-bottom-color: rgb(0, 0, 0);
    border-left-color: rgb(0, 0, 0);
    background: #303030;
    background-color: rgb(48, 48, 48);
}



/*BEGIN Notifications page*/

/*Notifications list background*/
.stream-item:not(.no-header-background-module) {
    background: #303030;
    background-color: rgb(48, 48, 48);
}
/*Header border*/
.content-header, .content-no-header {
    border: 1px solid #000000;
    border-top-color: rgb(0, 0, 0);
    border-right-color: rgb(0, 0, 0);
    border-bottom-color: rgb(0, 0, 0);
    border-left-color: rgb(0, 0, 0);
}
/*Header bottom border*/
.NotificationsHeadingContent {
    /*background-color: #fff;*/
    border-bottom: 1px solid #000000;
    border-bottom-color: rgb(0, 0, 0);
}
/*Left & right borders*/
.stream-item:not(.no-header-background-module) {
    background: #303030;
    border-left: 1px solid #000000;
    border-left-width: 1px;
    border-left-style: solid;
    border-left-color: rgb(0, 0, 0);
    border-right: 1px solid #000000;
    border-right-width: 1px;
    border-right-style: solid;
    border-right-color: rgb(0, 0, 0);
    background-clip: padding-box;
}
/*bottom borders*/
.ActivityItem {
    padding: 10px 12px 10px 70px;
    border-bottom: 1px solid #000000;
    border-bottom-width: 1px;
    border-bottom-style: solid;
    border-bottom-color: rgb(0, 0, 0);
}
.tweet {
    border-bottom: 1px solid #000000;
    border-bottom-width: 1px;
    border-bottom-style: solid;
    border-bottom-color: rgb(0, 0, 0);
}

/*Owner quoted*/
.QuoteTweet-fullname {
    color: #c0c0c0;
}

/*END Notifications page*/



/*END 2019-02-16*/

}
