css:
@-moz-document url-prefix("https://tonarinoyj.jp/episode/"), url-prefix("https://kuragebunch.com/episode/"), url-prefix("https://shonenjumpplus.com/episode/"), url-prefix("https://comic-days.com/episode/"), url-prefix("https://comic-action.com/episode/"), url-prefix("https://magcomi.com/episode/"), regexp(".*?/episode/\\d+") {
/* 非表示 */
.page-ad,
.js-page-ad {
    visibility: hidden; }

/* 削除 */
.viewer-btn-normal-container,
.viewer-colophon-recommend,
#ad-nav-area-wrap {
    display: none !important; }

.viewer {
    min-height: 100vh !important; }

canvas.page-image {
    margin: 0 !important;
    height: 100% !important; }

.scroll-horizontal .page-navigation-forward.rtl,
.scroll-horizontal .page-navigation-backward.rtl {
    background-image: none !important; }
}
