css:
@-moz-document url-prefix("https://blog.rocketseat.com.br/") {
body, .post-share, pre, code {
  background: rgb(32, 30, 41) !important;
  color: white;
}
a {
  color: rgb(177, 163, 226);
  border: 0 !important;
}
a:hover, a:focus {
  color: rgb(113, 89, 193);
  opacity: .8;
}
svg {
  fill: white !important;
}
code {
  background: rgb(32, 30, 41);
}
blockquote {
	border-color: rgb(177, 163, 226);
}
hr {
  background: rgb(177, 163, 226);
	opacity: .25;
}
.section-post.wrap {
  background: rgb(40, 36, 52);
}

.section-disqus {
	background: #efefef;
  border-radius: 0 0 5px 5px;
}

.lightense-backdrop {
  background: rgba(32, 30, 41, .95) !important;
}
}
