css:
@-moz-document domain("scrapbox.io") {
body { background: #eee }

.page,
.grid li.page-list-item a {
    box-shadow:
        0 1px 1px 0 rgba(60,64,67,.08),
        0 1px 3px 1px rgba(60,64,67,.16);
}

.grid li.page-list-item a { transition: .1s }

@media (hover: hover), (-moz-touch-enabled: 0) {
    .grid li.page-list-item a:hover {
        box-shadow:
            0 1px 3px 1px rgba(60,64,67,.2),
            0 2px 8px 4px rgba(60,64,67,.1);
    }
}

.telomere {
    opacity: .05;
    transition: .2s;
}
.page:hover .telomere {
    opacity: 1;
}

}
