css:
@-moz-document url-prefix("https://fuelphp.com/docs") {
body {
    background-color: rgba(42, 42, 42, 1);
    color: #eee;
}
pre code {
    background-color: rgba(0, 0, 0, 1);
    color: #fff;
}
p.note {
    background-color: rgba(254, 252, 175, 0.2);
}
a {
    color: #f0f;
}
#main h2 a, #main h3 a, #main h4 a {
    color: #fff;
}
#main h4 {
    text-shadow: none;
}
#main th {
    color: #fff;
    text-shadow: none;
}
}
