css:
@-moz-document domain(zerojudge.tw), domain(tcgs.tc.edu.tw) {
  .acstyle {
    color: #00BB00!important;
  }

.content_individual {
	background-color: #060606;
	color: #FFFFFF;
}
.FirstPageTitle,
.mainmenu {
	background-color: #60a060;
}
ul#tabmenu li {
	background-color: #222222;
	border-color: transparent transparent #282828;
}
  #tinymce {
    background-color: #0085c7!important;
  }
  .mce-container, .mce-container *, .mce-widget, .mce-widget * {
    margin: 0;
    padding: 0;
    border: 0;
    outline: 0;
    vertical-align: top;
    background: transparent;
    text-decoration: none;
    color: #b5b9bf!important;
    font-family: "Helvetica Neue", Helvetica, Arial, sans-serif;
    font-size: 14px;
    text-shadow: none;
    float: none;
    position: static;
    width: auto;
    height: auto;
    white-space: nowrap;
    cursor: inherit;
    -webkit-tap-highlight-color: transparent;
    line-height: normal;
    font-weight: normal;
    text-align: left;
    -moz-box-sizing: content-box;
    -webkit-box-sizing: content-box;
    box-sizing: content-box;
    direction: ltr
  }
  .mce-widget button {
    -moz-box-sizing: border-box;
    -webkit-box-sizing: border-box;
    box-sizing: border-box
  }
  .mce-container *[unselectable] {
    -moz-user-select: none;
    -webkit-user-select: none;
    -o-user-select: none;
    user-select: none
  }
  .mce-container ::-webkit-scrollbar {
    width: 8px;
    height: 8px;
    -webkit-border-radius: 4px
  }
  .mce-container ::-webkit-scrollbar-track, .mce-container ::-webkit-scrollbar-track-piece {
    background-color: transparent
  }
  .mce-container ::-webkit-scrollbar-thumb {
    background-color: rgba(53, 57, 71, 0.3);
    width: 6px;
    height: 6px;
    -webkit-border-radius: 4px
  }
  .mce-fade {
    opacity: 0;
    -webkit-transition: opacity .15s linear;
    transition: opacity .15s linear
  }
  .mce-fade.mce-in {
    opacity: 1
  }
  .mce-tinymce {
    visibility: visible !important;
    position: relative
  }
  .mce-fullscreen {
    border: 0;
    padding: 0;
    margin: 0;
    overflow: hidden;
    height: 100%;
    z-index: 100
  }
  div.mce-fullscreen {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: auto
  }
  .mce-tinymce {
    display: block;
    -webkit-border-radius: 2px;
    -moz-border-radius: 2px;
    border-radius: 2px
  }
  .mce-wordcount {
    position: absolute;
    top: 0;
    right: 0;
    padding: 8px
  }
  div.mce-edit-area {
    background: #FFF;
    filter: none
  }
  .mce-statusbar {
    position: relative
  }
  .mce-statusbar .mce-container-body {
    position: relative
  }
  .mce-fullscreen .mce-resizehandle {
    display: none
  }
  .mce-charmap {
    border-collapse: collapse
  }
  .mce-charmap td {
    cursor: default;
    border: 1px solid #232b33;
    width: 20px;
    height: 20px;
    line-height: 20px;
    text-align: center;
    vertical-align: middle;
    padding: 2px
  }
  .mce-charmap td div {
    text-align: center
  }
  .mce-charmap td:hover {
    background: #454f59
  }
  .mce-grid td div {
    border: 1px solid #d6d6d6;
    width: 12px;
    height: 12px;
    margin: 2px;
    cursor: pointer
  }
  .mce-grid {
    border-spacing: 2px;
    border-collapse: separate
  }
  .mce-grid a {
    display: block;
    border: 1px solid transparent
  }
  .mce-grid a:hover {
    border-color: #d6d6d6
  }
  .mce-grid-border {
    margin: 0 4px 0 4px
  }
  .mce-grid-border a {
    border-color: #d6d6d6;
    width: 13px;
    height: 13px
  }
  .mce-grid-border a:hover, .mce-grid-border a.mce-active {
    border-color: #d6d6d6;
    background: #0085c7
  }
  .mce-text-center {
    text-align: center
  }
  div.mce-tinymce-inline {
    width: 100%;
    -webkit-box-shadow: none;
    -moz-box-shadow: none;
    box-shadow: none
  }
  .mce-rtl .mce-wordcount {
    left: 0;
    right: auto
  }
  .mce-container, .mce-container-body {
    display: block
  }
  .mce-autoscroll {
    overflow: hidden
  }
  .mce-scrollbar {
    position: absolute;
    width: 7px;
    height: 100%;
    top: 2px;
    right: 2px;
    opacity: .4;
    filter: alpha(opacity=40);
    zoom: 1
  }
  .mce-scrollbar-h {
    top: auto;
    right: auto;
    left: 2px;
    bottom: 2px;
    width: 100%;
    height: 7px
  }
  .mce-scrollbar-thumb {
    position: absolute;
    background-color: #000;
    border: 1px solid #888;
    border-color: rgba(85, 85, 85, 0.6);
    width: 5px;
    height: 100%;
    -webkit-border-radius: 7px;
    -moz-border-radius: 7px;
    border-radius: 7px
  }
  .mce-scrollbar-h .mce-scrollbar-thumb {
    width: 100%;
    height: 5px
  }
  .mce-scrollbar:hover, .mce-scrollbar.mce-active {
    background-color: #AAA;
    opacity: .6;
    filter: alpha(opacity=60);
    zoom: 1;
    -webkit-border-radius: 7px;
    -moz-border-radius: 7px;
    border-radius: 7px
  }
  .mce-scroll {
    position: relative
  }
  .mce-panel {
    border: 0 solid #232b33;
    background-color: #404952;
    background-image: -moz-linear-gradient(top, #404952, #404952);
    background-image: -webkit-gradient(linear, 0 0, 0 100%, from(#404952), to(#404952));
    background-image: -webkit-linear-gradient(top, #404952, #404952);
    background-image: -o-linear-gradient(top, #404952, #404952);
    background-image: linear-gradient(to bottom, #404952, #404952);
    background-repeat: repeat-x;
    zoom: 1
  }
  .mce-floatpanel {
    position: absolute;
    -webkit-box-shadow: 0 5px 10px rgba(0, 0, 0, 0.2);
    -moz-box-shadow: 0 5px 10px rgba(0, 0, 0, 0.2);
    box-shadow: 0 5px 10px rgba(0, 0, 0, 0.2)
  }
  .mce-floatpanel.mce-fixed {
    position: fixed
  }
  .mce-floatpanel .mce-arrow, .mce-floatpanel .mce-arrow:after {
    position: absolute;
    display: block;
    width: 0;
    height: 0;
    border-color: transparent;
    border-style: solid
  }
  .mce-floatpanel .mce-arrow {
    border-width: 11px
  }
  .mce-floatpanel .mce-arrow:after {
    border-width: 10px;
    content: ""
  }
  .mce-floatpanel.mce-popover {
    background: transparent;
    -webkit-border-radius: 6px;
    -moz-border-radius: 6px;
    border-radius: 6px;
    -webkit-box-shadow: 0 5px 10px rgba(0, 0, 0, 0.2);
    -moz-box-shadow: 0 5px 10px rgba(0, 0, 0, 0.2);
    box-shadow: 0 5px 10px rgba(0, 0, 0, 0.2);
    top: 0;
    left: 0;
    background: #404952;
    border: 1px solid #232b33;
    border: 1px solid rgba(0, 0, 0, 0.25)
  }
  .mce-floatpanel.mce-popover.mce-bottom {
    margin-top: 10px;
    *margin-top: 0
  }
  .mce-floatpanel.mce-popover.mce-bottom>.mce-arrow {
    left: 50%;
    margin-left: -11px;
    border-top-width: 0;
    border-bottom-color: #232b33;
    border-bottom-color: rgba(0, 0, 0, 0.25);
    top: -11px
  }
  .mce-floatpanel.mce-popover.mce-bottom>.mce-arrow:after {
    top: 1px;
    margin-left: -10px;
    border-top-width: 0;
    border-bottom-color: #404952
  }
  .mce-floatpanel.mce-popover.mce-bottom.mce-start {
    margin-left: -22px
  }
  .mce-floatpanel.mce-popover.mce-bottom.mce-start>.mce-arrow {
    left: 20px
  }
  .mce-floatpanel.mce-popover.mce-bottom.mce-end {
    margin-left: 22px
  }
  .mce-floatpanel.mce-popover.mce-bottom.mce-end>.mce-arrow {
    right: 10px;
    left: auto
  }
  .mce-fullscreen {
    border: 0;
    padding: 0;
    margin: 0;
    overflow: hidden;
    background: #fff;
    height: 100%
  }
  div.mce-fullscreen {
    position: fixed;
    top: 0;
    left: 0
  }
  #mce-modal-block {
    opacity: 0;
    filter: alpha(opacity=0);
    zoom: 1;
    position: fixed;
    left: 0;
    top: 0;
    width: 100%;
    height: 100%;
    background: #000
  }
  #mce-modal-block.mce-in {
    opacity: .3;
    filter: alpha(opacity=30);
    zoom: 1
  }
  .mce-window-move {
    cursor: move
  }
  .mce-window {
    -webkit-border-radius: 6px;
    -moz-border-radius: 6px;
    border-radius: 6px;
    -webkit-box-shadow: 0 3px 7px rgba(0, 0, 0, 0.3);
    -moz-box-shadow: 0 3px 7px rgba(0, 0, 0, 0.3);
    box-shadow: 0 3px 7px rgba(0, 0, 0, 0.3);
    background: transparent;
    background: #404952;
    position: fixed;
    top: 0;
    left: 0;
    opacity: 0;
    -webkit-transition: opacity 150ms ease-in;
    transition: opacity 150ms ease-in
  }
  .mce-window.mce-in {
    opacity: 1
  }
  .mce-window-head {
    padding: 9px 15px;
    border-bottom: 1px solid #9e9e9e;
    position: relative
  }
  .mce-window-head .mce-close {
    position: absolute;
    right: 15px;
    top: 9px;
    font-size: 20px;
    font-weight: bold;
    line-height: 20px;
    color: #868c93;
    cursor: pointer;
    height: 20px;
    overflow: hidden
  }
  .mce-close:hover {
    color: #6f767e
  }
  .mce-window-head .mce-title {
    line-height: 20px;
    font-size: 20px;
    font-weight: bold;
    text-rendering: optimizelegibility;
    padding-right: 10px
  }
  .mce-window .mce-container-body {
    display: block
  }
  .mce-foot {
    display: block;
    background-color: #404952;
    border-top: 1px solid #9e9e9e;
    -webkit-border-radius: 0 0 6px 6px;
    -moz-border-radius: 0 0 6px 6px;
    border-radius: 0 0 6px 6px
  }
  .mce-window-head .mce-dragh {
    position: absolute;
    top: 0;
    left: 0;
    cursor: move;
    width: 90%;
    height: 100%
  }
  .mce-window iframe {
    width: 100%;
    height: 100%
  }
  .mce-window.mce-fullscreen, .mce-window.mce-fullscreen .mce-foot {
    -webkit-border-radius: 0;
    -moz-border-radius: 0;
    border-radius: 0
  }
  .mce-rtl .mce-window-head .mce-close {
    position: absolute;
    right: auto;
    left: 15px
  }
  .mce-rtl .mce-window-head .mce-dragh {
    left: auto;
    right: 0
  }
  .mce-rtl .mce-window-head .mce-title {
    direction: rtl;
    text-align: right
  }
  .mce-abs-layout {
    position: relative
  }
  body .mce-abs-layout-item, .mce-abs-end {
    position: absolute
  }
  .mce-abs-end {
    width: 1px;
    height: 1px
  }
  .mce-container-body.mce-abs-layout {
    overflow: hidden
  }
  .mce-tooltip {
    position: absolute;
    padding: 5px;
    opacity: .8;
    filter: alpha(opacity=80);
    zoom: 1
  }
  .mce-tooltip-inner {
    font-size: 11px;
    background-color: #000;
    color: #fff;
    max-width: 200px;
    padding: 5px 8px 4px 8px;
    text-align: center;
    white-space: normal
  }
  .mce-tooltip-inner {
    -webkit-border-radius: 3px;
    -moz-border-radius: 3px;
    border-radius: 3px
  }
  .mce-tooltip-inner {
    -webkit-box-shadow: 0 0 5px #000000;
    -moz-box-shadow: 0 0 5px #000000;
    box-shadow: 0 0 5px #000000
  }
  .mce-tooltip-arrow {
    position: absolute;
    width: 0;
    height: 0;
    line-height: 0;
    border: 5px dashed #000
  }
  .mce-tooltip-arrow-n {
    border-bottom-color: #000
  }
  .mce-tooltip-arrow-s {
    border-top-color: #000
  }
  .mce-tooltip-arrow-e {
    border-left-color: #000
  }
  .mce-tooltip-arrow-w {
    border-right-color: #000
  }
  .mce-tooltip-nw, .mce-tooltip-sw {
    margin-left: -14px
  }
  .mce-tooltip-n .mce-tooltip-arrow {
    top: 0px;
    left: 50%;
    margin-left: -5px;
    border-bottom-style: solid;
    border-top: none;
    border-left-color: transparent;
    border-right-color: transparent
  }
  .mce-tooltip-nw .mce-tooltip-arrow {
    top: 0;
    left: 10px;
    border-bottom-style: solid;
    border-top: none;
    border-left-color: transparent;
    border-right-color: transparent
  }
  .mce-tooltip-ne .mce-tooltip-arrow {
    top: 0;
    right: 10px;
    border-bottom-style: solid;
    border-top: none;
    border-left-color: transparent;
    border-right-color: transparent
  }
  .mce-tooltip-s .mce-tooltip-arrow {
    bottom: 0;
    left: 50%;
    margin-left: -5px;
    border-top-style: solid;
    border-bottom: none;
    border-left-color: transparent;
    border-right-color: transparent
  }
  .mce-tooltip-sw .mce-tooltip-arrow {
    bottom: 0;
    left: 10px;
    border-top-style: solid;
    border-bottom: none;
    border-left-color: transparent;
    border-right-color: transparent
  }
  .mce-tooltip-se .mce-tooltip-arrow {
    bottom: 0;
    right: 10px;
    border-top-style: solid;
    border-bottom: none;
    border-left-color: transparent;
    border-right-color: transparent
  }
  .mce-tooltip-e .mce-tooltip-arrow {
    right: 0;
    top: 50%;
    margin-top: -5px;
    border-left-style: solid;
    border-right: none;
    border-top-color: transparent;
    border-bottom-color: transparent
  }
  .mce-tooltip-w .mce-tooltip-arrow {
    left: 0;
    top: 50%;
    margin-top: -5px;
    border-right-style: solid;
    border-left: none;
    border-top-color: transparent;
    border-bottom-color: transparent
  }
  .mce-btn {
    border: 1px solid #364049;
    border-color: #202a33 #202a33 #202a33 #202a33;
    position: relative;
    text-shadow: 0 1px 1px rgba(0, 0, 0, 0.75);
    display: inline-block;
    *display: inline;
    *zoom: 1;
    -webkit-border-radius: 3px;
    -moz-border-radius: 3px;
    border-radius: 3px;
    -webkit-box-shadow: inset 0 1px 0 rgba(255, 255, 255, 0.2), 0 1px 2px rgba(0, 0, 0, 0.05);
    -moz-box-shadow: inset 0 1px 0 rgba(255, 255, 255, 0.2), 0 1px 2px rgba(0, 0, 0, 0.05);
    box-shadow: inset 0 1px 0 rgba(255, 255, 255, 0.2), 0 1px 2px rgba(0, 0, 0, 0.05);
    background-color: #4c5761;
    background-image: -moz-linear-gradient(top, #515c67, #454f59);
    background-image: -webkit-gradient(linear, 0 0, 0 100%, from(#515c67), to(#454f59));
    background-image: -webkit-linear-gradient(top, #515c67, #454f59);
    background-image: -o-linear-gradient(top, #515c67, #454f59);
    background-image: linear-gradient(to bottom, #515c67, #454f59);
    background-repeat: repeat-x;
    zoom: 1
  }
  .mce-btn:hover, .mce-btn:focus {
    color: #b5b9bf;
    background-color: #414a53;
    background-image: -moz-linear-gradient(top, #464f59, #3a424b);
    background-image: -webkit-gradient(linear, 0 0, 0 100%, from(#464f59), to(#3a424b));
    background-image: -webkit-linear-gradient(top, #464f59, #3a424b);
    background-image: -o-linear-gradient(top, #464f59, #3a424b);
    background-image: linear-gradient(to bottom, #464f59, #3a424b);
    background-repeat: repeat-x;
    zoom: 1
  }
  .mce-btn.mce-disabled button, .mce-btn.mce-disabled:hover button {
    cursor: default;
    -webkit-box-shadow: none;
    -moz-box-shadow: none;
    box-shadow: none;
    opacity: .4;
    filter: alpha(opacity=40);
    zoom: 1
  }
  .mce-btn.mce-active, .mce-btn.mce-active:hover {
    background-color: #363d45;
    background-image: -moz-linear-gradient(top, #3b424a, #2f363c);
    background-image: -webkit-gradient(linear, 0 0, 0 100%, from(#3b424a), to(#2f363c));
    background-image: -webkit-linear-gradient(top, #3b424a, #2f363c);
    background-image: -o-linear-gradient(top, #3b424a, #2f363c);
    background-image: linear-gradient(to bottom, #3b424a, #2f363c);
    background-repeat: repeat-x;
    zoom: 1;
    -webkit-box-shadow: inset 0 2px 4px rgba(0, 0, 0, 0.15), 0 1px 2px rgba(0, 0, 0, 0.05);
    -moz-box-shadow: inset 0 2px 4px rgba(0, 0, 0, 0.15), 0 1px 2px rgba(0, 0, 0, 0.05);
    box-shadow: inset 0 2px 4px rgba(0, 0, 0, 0.15), 0 1px 2px rgba(0, 0, 0, 0.05)
  }
  .mce-btn:not(.mce-disabled):active {
    background-color: #363d45;
    background-image: -moz-linear-gradient(top, #3b424a, #2f363c);
    background-image: -webkit-gradient(linear, 0 0, 0 100%, from(#3b424a), to(#2f363c));
    background-image: -webkit-linear-gradient(top, #3b424a, #2f363c);
    background-image: -o-linear-gradient(top, #3b424a, #2f363c);
    background-image: linear-gradient(to bottom, #3b424a, #2f363c);
    background-repeat: repeat-x;
    zoom: 1;
    -webkit-box-shadow: inset 0 2px 4px rgba(0, 0, 0, 0.15), 0 1px 2px rgba(0, 0, 0, 0.05);
    -moz-box-shadow: inset 0 2px 4px rgba(0, 0, 0, 0.15), 0 1px 2px rgba(0, 0, 0, 0.05);
    box-shadow: inset 0 2px 4px rgba(0, 0, 0, 0.15), 0 1px 2px rgba(0, 0, 0, 0.05)
  }
  .mce-btn button {
    padding: 4px 10px;
    font-size: 14px;
    line-height: 20px;
    *line-height: 16px;
    cursor: pointer;
    color: #b5b9bf!important;
    text-align: center;
    overflow: visible;
    -webkit-appearance: none
  }
  .mce-btn button::-moz-focus-inner {
    border: 0;
    padding: 0
  }
  .mce-btn i {
    text-shadow: 1px 1px #000
  }
  .mce-primary {
    min-width: 50px;
    color: #fff;
    border: 1px solid #364049;
    border-color: #202a33 #202a33 #202a33 #202a33;
    background-color: #006597;
    background-image: -moz-linear-gradient(top, #006fa6, #005580);
    background-image: -webkit-gradient(linear, 0 0, 0 100%, from(#006fa6), to(#005580));
    background-image: -webkit-linear-gradient(top, #006fa6, #005580);
    background-image: -o-linear-gradient(top, #006fa6, #005580);
    background-image: linear-gradient(to bottom, #006fa6, #005580);
    background-repeat: repeat-x;
    zoom: 1
  }
  .mce-primary:hover, .mce-primary:focus {
    background-color: #00547d;
    background-image: -moz-linear-gradient(top, #005e8d, #004467);
    background-image: -webkit-gradient(linear, 0 0, 0 100%, from(#005e8d), to(#004467));
    background-image: -webkit-linear-gradient(top, #005e8d, #004467);
    background-image: -o-linear-gradient(top, #005e8d, #004467);
    background-image: linear-gradient(to bottom, #005e8d, #004467);
    background-repeat: repeat-x;
    zoom: 1
  }
  .mce-primary.mce-disabled button, .mce-primary.mce-disabled:hover button {
    cursor: default;
    -webkit-box-shadow: none;
    -moz-box-shadow: none;
    box-shadow: none;
    opacity: .4;
    filter: alpha(opacity=40);
    zoom: 1
  }
  .mce-primary.mce-active, .mce-primary.mce-active:hover, .mce-primary:not(.mce-disabled):active {
    background-color: #004364;
    background-image: -moz-linear-gradient(top, #004d73, #00334d);
    background-image: -webkit-gradient(linear, 0 0, 0 100%, from(#004d73), to(#00334d));
    background-image: -webkit-linear-gradient(top, #004d73, #00334d);
    background-image: -o-linear-gradient(top, #004d73, #00334d);
    background-image: linear-gradient(to bottom, #004d73, #00334d);
    background-repeat: repeat-x;
    zoom: 1;
    -webkit-box-shadow: inset 0 2px 4px rgba(0, 0, 0, 0.15), 0 1px 2px rgba(0, 0, 0, 0.05);
    -moz-box-shadow: inset 0 2px 4px rgba(0, 0, 0, 0.15), 0 1px 2px rgba(0, 0, 0, 0.05);
    box-shadow: inset 0 2px 4px rgba(0, 0, 0, 0.15), 0 1px 2px rgba(0, 0, 0, 0.05)
  }
  .mce-primary button, .mce-primary button i {
    color: #fff;
    text-shadow: 1px 1px #333
  }
  .mce-btn-large button {
    padding: 9px 14px;
    font-size: 16px;
    line-height: normal;
    -webkit-border-radius: 5px;
    -moz-border-radius: 5px;
    border-radius: 5px
  }
  .mce-btn-large i {
    margin-top: 2px
  }
  .mce-btn-small button {
    padding: 3px 5px;
    font-size: 12px;
    line-height: 15px
  }
  .mce-btn-small i {
    margin-top: 0
  }
  .mce-btn .mce-caret {
