css:
@-moz-document domain("elpais.com") {
* {
    background-color: #333;
    color: azure !important;
}
.articulo-titulo a,
.articulo h2 a {
    color: azure;
}

.cabecera,
.seccion-submenu__interior,
.articulo,
.bloque__interior,
.global-header .section-bar_wrapper,
.flex_grid:not(.transparent) > * {
    background-color: #333;
    color: azure;
}

.articulo-entradilla,
.articulo .firma a,
.bloque-cabecera .bloque-cabecera-menu,
.bloque_tematico .articulo-entradilla {
    color: azure;
}

.bloque_destacadas .articulo-entradilla,
.bloque_tematico .articulos_apertura .articulo-entradilla,
.articulo-apoyos .apoyo-titulo a,
.articulo-entradilla,
.bloque_destacadas .articulos_cierre .articulo-entradilla,
.bloque_tematico .articulos_cuerpo .articulo:only-of-type .articulo-entradilla,
#bloque_tematico_10 > div > header,
.bloque-cabecera a,
.articulo-antetitulo a,
[class^="boton_"]:before {
    color: azure;
}

.envoltorio_publi,
#elpais_gpt-NSTD5 {
    background-color: #333;
}

.bloque-cabecera h3 {
    border-top: 2px solid azure;
}

#bloque_tematico_opinion > div > div > div > article div > div > div.firma::before {
    background: azure;
}

.boton_elpais-menu span {
    background: azure;
}

#cabecera-seccion > div > div.seccion-submenu > div > nav {
    color: azure;
}

#cabecera__interior > div.cabecera-superior > div > div.contenedor_derecha > div.navegacion-sucripcion > ul > li.newsletter > a {
    color: azure;
}

#cabecera-seccion > div > div.seccion-submenu,
#cabecera-seccion > div > div.seccion-submenu > div {
    color: azure !important;
}

#lmv_subpestanas,
#lmv_subpestanas > ul,
#li_lmv_todo {
    color: black !important;
}

#pie-comun9 > div > div.medios-destacados {
    display: none;
}

.contenedor {
    background-color: #333;
    border: 1px solid black;
}

#corporativo {
    background-color: #333;
}

#outbrain_widget_0 > div > div.ob-widget-section.ob-first > div,
#outbrain_widget_0 > div > div.ob-widget-section.ob-first > ul {
    display: none;
}

.articulo-trust,
.articulo-trust__inner,
.articulo-trust .trust-mas,
#salida_portada > div.contenedor > footer > div > div > div.corporativo {
    background-color: #333;
}

.lo_mas_visto .subpestanas ul li a {
    color: black !important;
}
.envoltorio_publi_botones .publi_luto_horizontal > div,
.articulo-apoyos [class^="apoyo_"] {
    background-color: #333;
}

#cabecera__interior > div.cabecera-superior > div > div.contenedor_derecha > div.navegacion-sucripcion > ul > li.suscribete > a {
    background: #333;
}

#cabecera {
    background-color: #333;
    border-bottom: 1px solid whitesmoke;
}

#elpais-menu {
    height: 100%;
}

#boton_usuario,
.trust_project .content,
.trust_project .content .more_link,
.background_gray_ultra_light,
.global-footer .footer-wrapper,
.global-header .header_collapsed.desktop_blue .button_primary.logout_button,
.global-header .header_collapsed.desktop_blue .button_primary.subscribe,
.corporativo,
.agrupador__interior,
#alertas,
.global-header .header_collapsed.desktop_blue {
    background-color: #333;
}

.share-bar {
    border-color: #333;
}

#pie-comun9 .inside,
.compartir,
.seccion-submenu__interior {
    border: none;
}

.cadena-ser .logo-ser {
    filter: invert(100%);
}

.global-header .header_collapsed.desktop_blue {
    border-bottom: 1px solid azure;
}

/*
.elpais-logo > a:nth-child(1) > span:nth-child(1) {
    filter: invert(100%);
}*/
}
