css:
@-moz-document domain(tumblr.com)
{a, body, .pop-menu {color: #333;}


body.flag--always-opaque-peepr .ui_peepr_glass, .identity .controls_section.user_list li .follow_list_item_blog:before, .identity .controls_section.user_list li .follow_list_item_blog:hover:before, .radar .radar_footer .radar_avatar::before,.identity .right_column::after, .post-forms-glass, .flag--accessibility-design-update .post-forms-glass, .post_avatar {background:rgba(54,70,93,.95);}

.identity, .l-container.l-container--two-column-dashboard .left_column, .l-container.l-container--two-column-dashboard .l-content, .l-container.l-container--two-column .l-content, .l-container.l-container--two-column-dashboard .right_column, .l-container.l-container--two-column .right_column, .l-header-container--refresh, .post-forms-glass, .post_avatar .post_avatar_link, .l-container.l-container--flex .l-content, .l-container.l-container--two-column-dashboard .left_column {background-color:#36465D;}

.plus-follow-button, .tab-bar-container .tab_notice {color: #36465d;}

.rapid-recs-container .rapid-recs {
    background-image: linear-gradient(180deg,rgba(0,25,53,0) 0,#36465d 15%,#36465d 85%,rgba(0,25,53,0));
}

.identity .controls_section.user_list li .follow_list_item_blog:hover:before{background-color:rgba(54,70,93,.1);}

.identity .controls_section.follow_list.recommended_tumblelogs > li.item {
    border-bottom: 1px solid hsla(0,0%,100%,.1);border-top: 0;}

.identity .controls_section.user_list li .follow_list_item_blog .tumblelog_title, .identity .controls_section li.section_header, .radar .radar_tumblelog_label .name {color: hsla(0,0%,100%,.6)}

.identity .controls_section.user_list li .follow_list_item_blog .tumblelog_name, .identity .controls_section a .count, .sidebar_link.explore_link, .identity .controls_section .open_blog .open_blog_link, .radar .radar_tumblelog_label .subheading, .identity .controls_section .sub_control {color: hsla(0,0%,100%,.4)}

.identity .controls_section.user_list li .follow_list_item_blog::before, .identity .controls_section.user_list li .follow_list_item_blog:hover::before, .post_avatar .post_avatar_link, .identity .controls_section.user_list li .follow_list_item_blog::before {
    border-right: 5px solid rgba(54,70,93,.1);}

.post-forms-glass {background: rgba(54,70,93,.95)}

.identity .controls_section.user_list li .follow_list_item_blog::before, .identity .controls_section.user_list li .follow_list_item_blog:hover::before, .post_avatar .post_avatar_link, .identity .controls_section.user_list li .follow_list_item_blog::before {
    border-right: none;
}

.post_full .post_permalink {
    border-top-color: #36465D;border-right-color: #36465D}

.tab_notice, .selected .tab_notice {background-color: #529ecc;}
.compose-button{fill: #529ecc;}

.tab-notice--outlined{border-color:#36465D}

.chrome.blue, .post-form--controls .controls-container .post-form--save-button .split-button .button-area.disabled, .post-form--controls .controls-container .post-form--save-button .split-button .button-area.disabled.active, .post-form--controls .controls-container .post-form--save-button .split-button .button-area[disabled],.ui_dialog .chrome.blue, .post-form--controls .controls-container .post-form--save-button .split-button, .post-form--controls .controls-container .post-form--save-button .split-button .button-area, .post-form--controls .controls-container .post-form--save-button .split-button .dropdown-area,.chrome.blue[disabled], .chrome.blue.disabled, .chrome.blue[disabled]:active, .chrome.blue.disabled:active{
    border-color: #529ecc;background-color: #529ecc;}

.notification .notification_right .preview_frame.link, .ui_post_badge.link, .notification .notification_right .preview_frame.link, .ui_post_badge.link, .ui_notes .activity-notification .activity-notification__icon .ui_post_badge.link {background-color: #56bc8a;}

.post_controls .post_control::before {color:rgba(0,0,0,0.45);}

}
