css:
@-moz-document url-prefix("https://studip.uni-giessen.de/"),url-prefix("http://studip.uni-giessen.de"),url-prefix("https://studip.uni-giessen.de/dispatch.php/")
{
body {
    color:white;
    background-color: #181818
}

.sidebar-image{
    content:url(https://i.imgur.com/RilU3AM.png);
    width:270px;
    margin-left: auto;
    margin-right: auto;
}

#barBottomContainer{background-color: #181818; color: white}

#layout-sidebar, #layout_container, #page_title_container, #flex-header, #tabs, #tabs2, #layout_page, #layout_content, #tabs li, .smiley-tabs li, .helpbar{
    background: #181818;
}

#page_title_container,#tabs {
    border-bottom: 0px;
    border-top: 0px;
    border:0px
}

table.default>caption, #tabs li.current, #tabs li.current a, #tabs li.current span.quiet, .smiley-tabs li.current a, .smiley-tabs li.current span.quiet, body:not(.fixed) #barTopMenu a{
    color:white
}

#tabs a, #tabs span.quiet, .smiley-tabs a{color:#b3b3b3}

tr.sortable, table.default>thead>tr>th{
    background-color: #2d2d2d;
    border:0px
}

a, a:link, a:visited {color: #b3b3b3}   /*Link generell*/
a:hover, a:active {color: white;}      /*Link mouseover*/
article.studip header h1, section.contentbox header h1 {color:white;}

.helpbar{right:0px}

.studip-widget .widget-header {
    background-color: #181818;
    color: #b3b3b3;
    font-weight: normal;
}
.studip-widget {border: 1px solid #181818;transition:none}

/*SIDEMENU*/
.sidebar .sidebar-widget-header {
    background:#7f7f7f;
    color: white;
    padding-left: 10px;
}
.sidebar-widget-content {
    background:#181818;
    border-top: 0px;
}
.sidebar .sidebar-widget, .sidebar .sidebar-widget-placeholder {border: 0px}

div.messagebox_info{
    color: white;
    background-color: #2d2d2d;
    border-color: #2d2d2d;
    background-image: url(https://i.imgur.com/tEXChm3.png);
}
article.studip, section.contentbox{border-color: #181818;}
article.studip header, section.contentbox header{background-color: #2d2d2d}

ul#tabs {padding-top: 10px;}

/*STUNDENPLAN*/
table#schedule_data thead tr td, table#schedule_data tbody td:first-child,  div.schedule_hours{
    background-color: #2d2d2d;
    color: #b3b3b3;
}
div.schedule_day {    border-right: 2px double #7f7f7f;}
nav.calendar-nav .calhead, h1, h2, h3, h4, h5, h6 {color: #b3b3b3;font-weight:normal}

.studip-tooltip {
    background-image: linear-gradient(to bottom,#2d2d2d,#2d2d2d);
    border: 0px;
    -webkit-border-radius: 4px;
    -moz-border-radius: 4px;
    border-radius: 4px;
    -webkit-box-shadow: 0 0px 0px;
    -moz-box-shadow: 0 0px 0px;
    box-shadow: 0 0px 0px ;
    text-shadow: 0 0px 0px;
}

/*NACHRICHTEN*/
body#messages-overview{background-color:#181818}
.ui-dialog.ui-widget.ui-widget-content {border: 0px; box-shadow: 0 0 15px rgba(0,0,0,.5);}
.ui-widget-content {background: #181818;color:#b3b3b3}
.message_body {background-color: #2d2d2d; margin: 0px; margin-top:5px;padding: 10px;}
.ui-dialog.ui-widget.ui-widget-content .ui-dialog-buttonpane {border-color: #2a3f5b;}
.ui-dialog.ui-widget.ui-widget-content .ui-dialog-buttonpane .ui-button, a.button, button.button {
    background: #2d2d2d;
    border: 0;
    color: #b3b3b3;
    font-weight: normal;
}

/*EVV*/
#zurueckDiv, #adressDiv, #simpleSearchDiv, .lectureItem0, .lectureItem1{border: 0px}
.lectureItem0, .lectureItem1{background-color: #2d2d2d}
abbr {color: #b3b3b3}
.lectureItemTitle {font-weight: bold}

/*ORANGE-VERNICHTUNG*/
*:focus, .focus_box, a.button:focus, button.button:focus {   /*Klick register now invisible*/
    outline: 0px
}
li.active{                                                   /*Irgendwelche obskuren Formen in der Navileiste oben*/
    background:#fff0;
    margin-left:-5px;
    -webkit-box-shadow:inset 0 0 0 1px #b3b3b300;
    -moz-box-shadow:inset 0 0 0 1px #b3b3b300;
    box-shadow:inset 0 0 0 1px #b3b3b300;
    position:relative;margin-right:-5px
}
.ui-dialog.studip-confirmation .ui-dialog-titlebar{         /*Gelbes Dialogfeld*/
    background-color:#b3b3b3;
    color:#000;
    text-align:left
}
/*LoginPage*/
div.index_container div.index_main {background-color: #181818}

