css:
@-moz-document domain("crunchyroll.com") {
::-webkit-scrollbar
{
width: 12px;
background-color: inherit;
}
::-webkit-scrollbar-track
{
-webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, 0.3);
border-radius: 10px;
background-color: inherit;
}
::-webkit-scrollbar-thumb
{
border-radius: 10px;
-webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, .3);
background-color:#888;
}
#template_container{
  padding:0 !important;
}
.welcome-featured-media {
    margin:0 auto;
}
#template_container.template-container,
#main_content, .wide-player-container-16-9{
    max-width:100% !important;
    width:100% !important;
    margin:0;
}
#showmedia_video {
    min-height:300px;
    height:300px;
    display:flex;
    flex-wrap:wrap;
    flex-direction:row;
}
#showmedia_video_box,
#showmedia_video_box_wide{
    width:100%;
    flex: 0 0 100%;
    height:300px;
}
@media screen and (min-height:400px) {
    #showmedia_video, #showmedia_video_box, #showmedia_video_box_wide {
        height:400px;
    }
}
@media screen and (min-height:700px) {
    #showmedia_video, #showmedia_video_box, #showmedia_video_box_wide {
        height:calc(100vh - 200px);
    }
}
#template_scroller {
    padding-top:0;
}
@media screen and (min-width:1600px) {
    #welcome_left {
        width:calc(100% - 500px);
    }
    #welcome_right {
        float:left;
        padding-left:20px;
    }
}
a[href="https://got.cr/slime-crx-bar"] {
    display:none;
}
}
