css:
@-moz-document regexp("https:\\/\\/app\\.asana\\.com\\/[0-9]*\\/[0-9]*\\/board"), regexp("https:\\/\\/app\\.asana\\.com\\/[0-9]*\\/[0-9]*\\/[0-9]*"), regexp("https:\\/\\/app\\.asana\\.com\\/[0-9]*\\/[0-9]*\\/list") {
/************************** SIDE BAR ******************************/
.sidebar-mountNode {
	width: 213px;
}

/************************** GRID VIEW ******************************/
.BoardBody-columnHorizontalScrollable {
    padding: 50px 5px 0;
}
.BoardColumn-addCardButton {
    margin: 0;
}
.BoardColumnScrollableContainer {
    margin-top: 3px;
}
.BoardBody-column {
    padding: 0 10px;
}
.BoardNewColumn {
    width: 150px;
}
.BoardColumnScrollableContainer-scrollable::-webkit-scrollbar {
    width: 5px;
    height: 8px;
    background-color: white;
}
.BoardColumnScrollableContainer-scrollable::-webkit-scrollbar-thumb {
    background: linear-gradient(to top, #1e5799 0%, #2989d8 50%, #207cca 51%, #7db9e8 100%);
}
.BoardCardWithCustomProperties-contents {
    padding: 2px 5px;
}
.BoardCardPotPills {
    margin: 0;
}
.BoardCardPotPills-potPill {
    margin: 0;
    height: 4px;
    border-radius: 2px;
    width: 25px;
}
.BoardCardWithCustomProperties-nameAndDropdownButton {
    margin: 0;
}
.BoardCardWithCustomProperties-assigneeAndDueDate {
    display: inline-block;
}
.BoardCardWithCustomProperties-assignee.is-popoverVisible, .BoardCardWithCustomProperties-assignee.BoardCardWithCustomProperties--alwaysVisible, .BoardCardWithCustomProperties--hover .BoardCardWithCustomProperties-assignee, .BoardCardWithCustomProperties-dueDate.is-popoverVisible, .BoardCardWithCustomProperties-dueDate.BoardCardWithCustomProperties--alwaysVisible, .BoardCardWithCustomProperties--hover .BoardCardWithCustomProperties-dueDate {
    display: inline-block;
    margin-right: 3px;
    vertical-align: top;
}
.BoardCardWithCustomProperties-assignee, .BoardCardWithCustomProperties-dueDate {
    display: inline-block;
}
.BoardCardWithCustomProperties-customProperties {
    margin: 0;
    display: inline-block;
}
.BoardCardCustomProperties-cellWrapper {
    padding: 0;
}
.BoardCardCustomPropertyPreviewCell-cell {
    max-width: unset;
}
.BoardCardWithCustomProperties-leftMetadata, .BoardCardWithCustomProperties-rightMetadata {
    align-items: unset;
}
.BoardCardWithCustomProperties .everhour-card .everhour-card-button.everhour-action-button-start, .BoardCardWithCustomProperties .everhour-card .everhour-card-button.everhour-action-button-continue, .BoardCardWithCustomProperties .everhour-card .everhour-card-button.everhour-action-button-stop {
    margin: 0;
}
.BoardCardCoverImage {
    display: none;
}



/************************** LIST VIEW ******************************/
.PotListPage:not(.PotListPage--withProjectClarityEnabled), .PotListPage-content--withProjectClarityEnabled {
	padding: 5px;
}

.ProjectPageView-list {
	padding: 2px;
}

.PotListPage-detailsPane {
    flex: 0 1 55%;
	margin-left: 5px;
}

.PotListPage-gridPane {
	flex: 0 1 85%;
}

.GridHeader {
	height: 30px;
}

.PotGridHeader-buttons {
	margin: 3px 0 0 0;
    padding: 0;
}

.Button.Button--small {	
    height: 24px;
    line-height: 24px;
    padding: 0px 9px;
}

.dropTargetRow {
	margin: 0;
    padding: 0;
}

.ItemRow.ItemRow--enabled {
	height: 24px;
}

.ItemRow--enabled:hover {
	border-color: green;
}

.FormElementButton.FormElementButton--small {
    height: 24px;
    line-height: 24px;
}

.DraggableTaskRow-dragHandle {
	width: 20px;
}

.PotPillsContainer-potPillLink {
	max-width: 100px;
}

.textInput.textInput--medium {	
    height: 24px;
    padding: 0;
    padding: 0px;
}

.CustomPropertyHeader--editable .CustomPropertyHeader-fieldHeading--text {
	width: 70px;
    margin: 0;
}

.CustomPropertyEditablePreviewContainer-textCell {
	width: 70px;
}

.CustomPropertyHeader--editable .CustomPropertyHeader-fieldHeading--number {
	width: 40px;
    margin: 0;
}

.CustomPropertyEditablePreviewContainer-numberCell {
	width: 40px;
}

.CustomPropertyHeader--editable .CustomPropertyHeader-fieldHeading--enum {
	width: 60px;
    margin: 0 5px;
}

.CustomPropertyEditablePreviewContainer-enumCell {
	width: 70px;
}

.CustomPropertyEnumEditablePreviewCell .ButtonSelect-downIcon {
	margin-left: -20px;
}
}
