css:
/* i really want this to be global */
@-moz-document domain(web.telegram.org) {

.md_modal_head,
.tg_head_split,
.im_dialog:hover,
.dropdown-menu,
.tg_checkbox.tg_checkbox_on span.icon-checkbox-outer,
.composer_emoji_tooltip,
.icon-tooltip-tail,
.im_dialog_badge_muted,
.im_record_bg,
.im_message_selected .im_message_outer_wrap,
.im_history_select_active .im_message_outer_wrap:hover,
.btn-primary:hover,
.btn-md:hover,
.contacts_modal_members_list .active a.contacts_modal_contact,
[class*="user_bgcolor"],
.im_message_unread_split,
code,
.im_message_date_split.im_service_message_wrap,
.md_modal_split_action,
.tg_slider_track,
.tg_slider_track_fill,
.tg_radio.tg_radio_on i.icon-radio,
.reply_markup_button:hover,
.btn-link:hover,
body {
	background: #000 !important;
}

a.tg_head_btn.dropdown-toggle:hover,
a.tg_head_btn:hover,
a.tg_head_btn.tg_head_peer_search_btn:hover,
li.im_dialog_wrap.active > a.im_dialog,
div.tg_head_peer_dropdown.tg_head_peer_media_dropdown.dropdown.open > a.tg_head_btn.dropdown-toggle,
div.page_wrap > div.im_page_wrap.clearfix,
.im_history_scrollable_wrap.nano-content,
.nano-slider,
.divider.ng-scope,
div.tg_head_logo_wrap > div.open > a.tg_head_btn.dropdown-toggle,
.im_dialogs_scrollable_wrap a.im_dialog_searchpeer,
.composer_emoji_tooltip_tab_shadow,
.composer_sticker_btn:hover,
#mpiv-popup,
a.composer_emoji_btn:hover,
.contacts_modal_members_list a.contacts_modal_contact:hover,
.tg_slider_thumb {
	background: #333 !important;
}

body > div.page_wrap > div.im_page_wrap.clearfix > div > div.im_dialogs_col_wrap.noselect,
.nano-pane,
.tg_head_logo_dropdown .dropdown-menu > li > a:hover,
.tg_head_peer_dropdown .dropdown-menu > li > a:hover,
.tg_checkbox.tg_checkbox_on i.icon-checkbox-inner,
.md-input,
.btn-primary,
.btn-md,
.im_message_reply_border,
.modal-content,
.reply_markup_button {
	background: #444 !important;
}

.im_message_webpage_wrap.clearfix {
	border-left: 10px solid #444 !important;
}

.im_message_outer_wrap.hasselect,
.form-control {
	border: 1px solid #444 !important;
}

.md_modal_iconed_section_wrap {
	border-bottom: 1px solid #444 !important;
}



[class*="modal"],
[class*="date"],
[class*="form"],
[class*="message"],
[class*="history"],
.im_short_message_media,
.im_dialog_chat_from,
.im_short_message_text,
.im_dialog_peer,
.im_dialog_date,
.im_history_col_wrap.noselect.im_history_loaded,
.im_message_author,
div.im_message_text > a,
.im_message_webpage_site,
.im_message_webpage_title > a,
.im_message_date_split_text,
.im_message_date_text.nocopy,
div.im_message_media > div > div > div > div.im_message_contact_name > a,
div.im_message_media > div > div > div > div > div.im_message_webpage_description > a,
div.tg_head_peer_title_wrap > a > div > span.tg_head_peer_status > span,
div.tg_head_peer_dropdown.tg_head_peer_media_dropdown.dropdown.open > ul > li > a,
.tg_head_logo_dropdown .dropdown-menu > li > a,
.peer_modal_profile_description,
.md_modal_section_link,
.md_modal_section_link:hover,
.tg_checkbox_label,
.md_modal_section_param_wrap .md_modal_section_param_name,
.md_modal_section_param_wrap .md_modal_section_param_value,
.md_simple_modal_body .modal_simple_form h4,
.md-input-label,
.btn-md,
.btn-md:focus,
.btn-md:hover,
.md-input,
.im_dialog_message,
.composer_emoji_tooltip_tab_emoji,
.composer_emoji_tooltip_tabs_stickers_active .composer_emoji_tooltip_tab_stickers,
.composer_emoji_tooltip_tab_stickers,
.composer_stickerset_title,
.im_message_unread_split,
.im_service_message,
.im_message_reply_author,
.im_message_author_admin,
.im_message_date,
.im_message_fwd_date,
code,
.btn-link,
a,
a:hover,
.im_message_views_cnt,
.md_modal_list_peer_description,
.md_modal_section_select_wrap .md_modal_section_param_name,
.reply_markup_button,
.composer_rich_textarea:empty::before {
	color: #fff !important;
}

div.im_dialogs_panel > div > input {
	color: #000 !important;
}

a:hover {
	text-decoration: underline !important;
}

.im_dialogs_col_wrap {
	border-right: 10px solid #333 !important;
}

.tg_head_split {
	border-top: 1px solid #fff !important;
	border-left: 1px solid #fff !important;
	border-right: 1px solid #fff !important;
	height: 49px;
}

.im_message_reply_border {
	width: 10px !important;
}

.nano-pane {
	width: 10px !important;
	right: -5px !important;
}

.nano-slider {
	margin: 0px !important;
}

.im_history_message_wrap {
	margin-right: 12px !important;
}

code {
	padding: 0px !important;
}

.im_history_scrollable_wrap.nano-content {
	right: -10px !important;
}

div.im_history_wrap.nano.has-scrollbar.active-scrollbar > div.nano-pane {
	top: 0px !important;
	right: 3px !important;
	width: 5px !important;
}

.im_history_pinned_wrap,
.dropdown-menu,
.md-input-group,
.composer_emoji_tooltip,
.icon-tooltip-tail,
#mpiv-popup,
.im_edit_panel_border,
a.tg_radio > span.icon-radio-outer {
	border: 0px !important;
}

.composer_emoji_tooltip_tab_shadow {
	box-shadow: none !important;
	height: 5px !important;
}

.composer_rich_textarea,
.composer_textarea {
	box-shadow: none !important;
	background: #444 !important;
	width: calc(100% - 30px) !important;
	padding: 0px 11px 0px 10px !important;
	border: 1px solid #fff !important;
	left: 0px !important;
}

::-webkit-scrollbar {
	width: 10px;
}

/* Track */
::-webkit-scrollbar-track {
	background: #444;
	border-left: 2px solid #333 !important;
	border-right: 3px solid #333 !important;
}

/* Handle */
::-webkit-scrollbar-thumb {
	background: #333 !important;
}

/* Handle on hover */
::-webkit-scrollbar-thumb:hover {
	background: #333 !important;
}

i {
	filter: brightness(1000%) !important;
}

.composer_emoji_tooltip_category i,
i.icon-checkbox-inner,
.icon-search-clear {
	filter: invert(100%) !important;
}

a.composer_emoji_tooltip_category.active > i {
	filter: brightness(1000%) !important;
}

.peer_modal_photo.telegram_modal_logo {
	filter: grayscale(1);
}

i.emoji {
	filter: none !important;
}

}
