css:
@-moz-document url-prefix("https://www.construct.net/en/forum"), url-prefix("https://www.construct.net/fr/forum") {
/*arkaplan*/
.hideWhenMiniMenu{
	 color: #d0d8d9;
    font-size: 2rem;
}
body, html {
    background-color: #1d1f21 !important;
}
/* forum anasayfası*/
/**********************************************/
/*forum kategorileri*/
ul.forumForums li.forum:nth-child(even) {
    background: #191b1e; 
}


ul.forumForums li.forum{
	background: #131517; 
}
/*kategori hover*/
table.forumsTable tr:hover {
    background: #25282c !important;
}
ul.forumForums span.forumForumDescription {

    color: #848b8c;/*d0d8d9*/
}
table.forumsTable td:nth-child(4) {

    color: #656c82;
}
table.forumsTable td:nth-child(5){
    color: #656c82;
}
table.forumsTable td:nth-child(8){
	color: #656c82;
}
/* üst forum basligi*/
ul.forumsWrapper li.categoryWrapper .categoryToWrapper {
	background: #000000;
}

ul.forumsWrapper li.categoryWrapper {

    background: #292b2f;
    border: 1px solid #607D8B;

}
ul.forumForums {

    border: 1px solid #292b2f;
}

ul.forumForums li.forum{

    border-bottom: 1px solid #392f19;

}

ul.forumsWrapper a.forumCatLink{
	color: #969489;
}
/* footer*/
.forumStats .title {
    background: #000000;
    color: #969489;

}
.forumStats {
	background: #1c1e21;
    color: #969489;
}

.forumStats .statWrap .stat {
	background: #292929;
}
.forumStats .statWrap .stat:nth-child(even) {
    background:#1c1e21 
}

/* konu sayfası*/
/**********************************************/
 /* üst çerceve*/
.topicsWrapperResponsive {
    background: #292b2f;
    border: 1px solid #607D8B;
}
.topicCols.head {
	color: #656c82;
}
.topicCols.head > div {
    background: #000000;
    height: 32px;
    line-height: 32px;
}
/*
.topicCols.head > div:nth-child(3) {
	color:red;
}
*/
.topicsWrapperResponsive ul.topics > li {
    border-bottom: 1px solid #392f19;
    border-left: 1px;
    border-right: 1px;
    background: #131517;
}
.topicsWrapperResponsive ul.topics > li:nth-child(even) {
    background: #191b1e;
}
.topicCols.head {
  
    border-bottom: 1px solid #221d12;
}
.timeago{
	color: #848b8c;
}
/*
.topicCols > div:nth-child(3) {
 color:grey;
}*/
.topicCols{
 color: #656c82;
}

/* Forum controls */
.forumControlsWrapper {
	color: #656c82;
}


.paginationWrap ul li a {
   
    color: #656c82;
    background: #1d1f21;
   
}
.paginationWrap ul li a:hover, .paginationWrap ul li a.selected {
    background: #f85753;
    color: #fff;
    text-decoration: none;
}


.paginationWrap ul li a.next, .paginationWrap ul li a.prev {
    background: #ece7e78c;
    opacity: 0.5;
}

.paginationWrap ul li a.dots {
    background: transparent;
    color: #8e8888;
}

ul.topics .pagination ul li a {

    color: #ffffff85;

}

ul.topics .pagination ul {

    border: 1px solid #392f19;

    background: #1d1f21;

}

ul.topics .pagination ul li {
    background: #1d1f21;

}

.forumJumpWrap select {
    background: #585757;
}


/* forum konuları*/
/**********************************************/
 
.postsRenderTotalWrapper>div.c {
	 color: #d0d8d9;
    
}
/*
.postsRenderTotalWrapper>div.c >h1 {
	font-size: 2rem;
}*/
ul.breadCrumbNav li:last-child a {
    color: #888282;
}

/* üst arama bölümü */


.forumControlsWrapper input[type=text] {
    /* border: 1px solid #c0c0c0; */

    background-color: #585757;
}


/* Main forum post wrapper */
ul.forumPostList {
    background: #292b2f;
    border-radius: 3px;
    border: 1px solid #607D8B;
       
   


}
ul.forumPostList ul.postTools {
    background: #000000;
   

}
.div>postText articleRender edited{
	background: #000000;
}
ul.forumPostList li.post {
    background: #191b1e;
     border: 1px solid #191b1e; 
}
ul.forumPostList li.post:nth-child(even) {
    background: #131517;
}
ul.forumPostList .postText {
	color: #848b8c;
}
ul.forumPostList li.post {


}
.articleRender {
    color: #8f9697;
}

.voteWrapper span.score {
    font-size: 2rem;
    display: block;
    padding: 15px 0;
    color: #969489;
}
/* katilma zamani*/
ul.forumPostList li.post .column:first-child ul {

    color:#969489;
}

/* @user */
ul.forumPostList .postText a.usernameReference {
    background:#2b2828
   
}

/* reklam */

.c3pWrap .inside {
    color:grey;
    background:black;
}
.c3pWrap p {
    color: #9a2a2a;

}

/* alinti*/


ul.forumPostList .postText blockquote {
    margin: 0;
    border-left: 5px solid #607d8b;
    background: #9e9e9e36;
 	background-repeat: no-repeat;

}

/* post gonder */

.newForumPostWrap textarea {
    height: 300px;
    background:grey;
}




/*////////////////////////////
/ arama bölümü
*/

textarea, input[type=text], input[type=number], input[type=password], input[type=number], select {
    background:grey;
    border: 1px solid #607D8B;
}
div#SearchForm>label{
    color: #98acb5;
}

/*
 ----------aktif kullanıcılar-----------
*/
.activeUsersTableWrap {

    background: #292b2f;
    border: 1px solid #607D8B;
    border-radius: 3px;
}
.activeUsersTableWrap table thead tr td {
    background: #000000 !important;
    color: #656c82;
}
.activeUsersTableWrap table tr {
    background: #131517;
}
.activeUsersTableWrap table tr:nth-child(even) {
    background: #191b1e;
}
.activeUsersTableWrap table tbody tr {
    border-bottom: 1px solid #392f19;
    border-left:1px solid #392f19;
    border-right:1px solid #392f19;
}
.activeUsersTableWrap table tbody tr:hover {
    background: #25282c !important;
}
/* Nav to topic in post search results */
.contextTopicNav {
    background: #272727;
}
.activeUsersTableWrap strong {
	color:grey;
}
ul.forumPostList .editInfo{
    background: #262829;
}
/* yeni konu baslat*/

.tagify{

    background: #9E9E9E;
}

.newForumPostWrap label {
    color:#b5a890;
}
.articleRender .tabWrap ul.tabs li a {
    color: #9E9E9E;
}
ul.forumPostList li.post.highlighted {
    background: rgba(29, 68, 86, 0.48);
}
.hideIfBCMobile{
    color:#d0d8d9;
}

ul.forumPostList .column.postPendingApproval {
    background: rgba(54, 33, 93, 0.19);
}

ul.forumPostList li.post.deleted {
    background: #292727;
    color: #666;
}

ul.forumPostList a.deletedPostExpander:hover {
    background: #131518;
    text-decoration: none;
}
ul.forumPostList a.deletedPostExpander {
    background: #000000;
    font-size: 0.7rem;
    line-height: 100%;
    display: block;
    padding: 3px 10px;
    padding-left: 175px;
}
}
