css:
@-moz-document domain("threadreaderapp.com") {
.background-blue.entry-support.hide-premium.pd-4.hide-redundant{
display:none;
}
.footer{
bottom:0px;
}

.navbar{
color:#8798A5;
background:#11131a;
}

body{
color:#8798A5;
background:#141D26;
}

#tweet_1.content-tweet, .content-header.content-tweet{
color:#8798A5;
}

.content-tweet{
color:#8798A5;
}

.list-content-header{
color:#8798A5;
}

.background-gray{
background:#141d26 !important;
border:1px solid #11131a;
}

.thread-card{
background:#11131a;
border:1px solid #131313;
}

.box-user .username a{
color:#1da1f2;
}

.thread-card .time{
background:#141d26;
color:#8798A5;
}

.thread-card .box-user{
border-bottom:solid 1px #141d26;
}

.footer{
background:#11131a;
}

.thread-info{
color:#b8b8b8;
}

.btn-share-thread .btn-outline-primary{
border-color:#11131a;
}

.entity-reveal-action{
background:#007bff;
}

h1, h2, h3, h4, h5, h6{
color:#8798A5;
}


.btn-secondary{
color:#11131a;
background-color:#1da1f2;
border-color:#141d26;
}
.btn-outline-dark{
color:#8798A5;
border-color:#11131a;
}
.card{
background-color:#11131a;
border:1px solid #131313;
}

.box-user .screenName a{
color:#b8b8b8;
}

.form-control{
background-color:#11131a;
border:1px solid #131313;
}

.background-blue{
display:none;
}

.thread-card .card-tweets a, .thread-card .card-tweets a:hover, a.thread-card-wrap, a.thread-card-wrap:hover{
color:#b8b8b8;
}

.nav-item.active, .nav-link.active {
    color: #8798A5;
    border-bottom: 2px solid #8798A5;
}

.alert-custom {
    background: #11131a !important;
    border: 1px solid #131313;
}

.nop, .nop a {
    color: #BE1E2D;
}

.entity-embed .youtube-player, .entity-image img, .entity-video video, .entity-video-gif video {
    background: #11131a;
    border: solid 1px #131313;
}

.btn-outline-secondary:hover {
    color: #8798A5;
    background-color: #11131a;
    border-color: #131313;
}

.btn-outline-secondary {
    border-color: #11131a;
}

.entity-url-preview {
    background: #11131a;
    border: 1px #131313 solid;
}

.entity-url-preview a small, .entity-url-preview a strong {
    color: #8798A5;
}

.entity-mention {
    color: #b8b8b8;
}

.button-tag {
    color: #8798A5;
    border: 1px #11131a solid;
    background: #141d26;
}

a.button-tag:hover, .btn-share-thread .btn-outline-primary:hover, .btn-outline-dark:hover {
    background-color: #11131a;
    border-color: #131313;
    text-decoration: none;
    color: #8798A5;
}

.form-control:focus {
    color: #8798A5;
    background-color: #11131a;
    border-color: #131313;
}

.table thead th {
    border-bottom: 2px solid #11131a;
}

.table td, .table th {
    border-top: 1px solid #11131a;
}

.toc a {
    color: #b8b8b8;
}

.help-page h2 {
    border-top: 1px solid #11131a;
}

.box-info {
    border-color: #131313;
    background: #11131a;
}

.modal-content {
    background-color: #141D26;
    border: 1px solid #131313;
}

.modal-header {
    border-bottom: 1px solid #11131a;
}

.modal-footer {
    border-top: 1px solid #11131a;
}

.sharingfooter {
    display: none;
}

.thread-card .box-user .avatar img {
    border: solid 2px #141d26;
}

a:hover {
    color: #76cbff;
    text-decoration: none;
}

code {
    background: #141d26 !important;
}

.user-info .box-subscribers{
border-top:1px solid #141d26;
}
.box-subscribers .more{
color:#8798A5;
}

.box-subscribers .subscriber-pp img{
border:solid 1px #141d26;
}
}
