css:
@-moz-document domain("christianforums.com"), domain("wilderssecurity.com") {
/**************************
GENERAL:
	#bdbdbd  	 off white  
	#696969		 light gray; 
	#7b7b7b  	 med gray
	#202020e8  dark gray;  */

div#headerMover {   
    background-color: #122435 !important }
div.xbTopBar {
	background-color: #202020d4;
	color: #ddd;
	border-color: #202020
}
ul li.mainTopLink a {
	color: #ddd !important
}
div#logoBlock,
div.pageContent {
	border-color: #202020d4 !important
}

div.pageContent, 
div#logoBlock,
div.footer {   /*masthead; table highlights 2 */
    background-color: #7b7b7b !important
}
/*navbar -deeper shade of brown, but ok. too much work to straighten out for now */
div.pageContent nav div.navTabs /*, 
ul.publicTabs li[class^="navTab"] ax */ {
	background: #202020d4 !important;
}
#header div.navTabs a.navLink {
	color: #ccc;
	font-weight: normal
}
li.navTab.forums.selected a.navLink,
li.navTab.forums.selected div[class*="tabLinks"],
#navigation div[class^="tabLinks"] {
	background: #bdbdbd !important;
}
/*only works on Forums tab
also, hover is teal */
li.navTab.forums.selected a  {
	color: #202020 !important
}		
ul.xbSocialLinks {
	display:none
}
li a[href^="watched/threads"],
li a[href*="new-"] {
	text-decoration: underline
}

/*
NW div.breadBoxTop nav span, a.crumb, span.arrow span, span.crust.homeCrumb, fieldset.breadcrumb {
	background: #bdbdbd
}*/

/* ***FORUM *** */
li[class*="sticky"] a:link, /* STICKY TOPICS */
li[class*="sticky"] {  
    color: #5a5959 !important;
    font-size: 14px;
    padding: 0px
}

/* ***THREAD*** */
li.message,
div.messageInfo.primaryContent, 
div.blogEntryContainer.section ol li,
div.section.ubsComments div#comments ol li { 
    /* POST 1; blog body; blog comments */
    background: #bdbdbd !important /*#a5d2ff */
}
/*Watch Thread link*/ 
#content  div  div  div.mainContainer  div  div  div.linkGroup.SelectionCountContainer  a{
	color: lightgreen !important;
	text-decoration: underline !important;
}
article a:link,
article a:visited {
	color: brown !important; 
	text-decoration:underline ;
	font-size: 14px !important; 
}
/*Post's Timestamp*/
span.item.muted a {
	color: #3681f1 
}
div.ubsBlogEntryByLine.muted span a,
div.ubsBlogEntryByLine.muted span,
div.ubsBlogEntryByLine.muted { color: white !important }

div.messageHeading,
h3.subHeading,
div[class*="categoryStrip"],
dl.sectionHeaders {      /*HEADERS 4 */
    background-color: #202020d4 !important
}
.messageUserInfo {width:160px}
div.messageUserBlock, 
div.extraUserInfo {
    background: white !important;
}
div.messageUserBlock h3 em strong {
margin-left: 6px
}
.messageUserBlock a.username {
	font-family: caviar dreams;
	font-weight: bold; 
	font-size: 20px; 
}

/*  MESSAGE TEXT */
blockquote[class^="messageText"] {
	font-family:  open sans, sans, noto sans, verdana; 
	font-size: 15px; 
	line-height: 120%;
	color: #333 !important ;
	margin-left: 8px;
}
/*red text*/
blockquote[class^="messageText"] span[style="color: #ff4d4d"]{
	color: red !important;
}
/*blockquote*/
blockquote.quoteContainer, 
blockquote.quoteContainer a {
	background: #b2afaf !important;
	font-size: 14px !important;	
	line-height: 110%; 
	font-family: liberation sans, verdana; 
}
div[class*="bbCodeSpoilerText"] {
	background:#ddd !important
}
a[class^="dark_postrating"] {
	color: #202020d4
}
a[href*="ratings"] {;
	text-decoration: underline
}
ul[class*="inputlist_undo"] a{
	color: brown
}





}

@-moz-document domain("christianforums.com"), domain("wilderssecurity.com") {
/************************
 GENERAL, CONTINUED */ 

/*Sidebar*/
div.section.widget-single div[class^="secondaryContent"] {
	background: #bdbdbd !important
}

/* Round Corners */
li[id^="post"],
div.discussionList.section,
.discussionList.sectionMain,
form.sectionMain {border-radius: 14px }
div.messageHeading  {border-radius: 14px 14px 0 0 }
div.messageMeta.ToggleTriggerAnchor   {border-radius: 0 0 14px 14px }

/* EDITOR */
body[contenteditable="true"],
iframe[class*="MessageEditor"],
div.placeholder,
div.redactor_box  /* EDIT BOX */ {
    background-color: #bdbdbd;
    font-size: 15px;
    resize: both;
    font-family: open sans, verdana;
}                          
body > p {
	margin: 0px !important;
	line-height: 1.2 !important
}
body >  p a {
	color:brown !important 
}
/*   */
#pageDescription,
#pageDescription a  {  /* DESC. UNDER THREAD TITLE*/
    color: black !important }                            
/*Move Scroll Buttons higher */
div.xbScrollButtons {bottom: 400px }

/*User info popup */
div.userInfo > dl.userStats.pairsInline > dd > span {color: #333 !important}






}

@-moz-document url("https://www.christianforums.com/view/new-threads/") {
/**********************
NEW THREADS LIST   */
div[class*="WidgetRenderer_Threads"]{
	line-height: 150% !important;
}
div[class*="Threads_ListCompact"] ol li div a {
	color:#222 !important;
	text-decoration: none
}

a.username span[class^="style"] {
	color: #222;
	font-weight: normal
}

div.pageContent{ 
	border-color: black !important
}

div[class="forum.responsive--aware--from-medium"] a {
	padding-left: 10px !important
}

/* OP header color prob */
ol li[class*="thread"]:nth-child(odd),
ol li[class^="discussionListItems"] { 
    background-color: #bdbdbd !important;
} 
ol li[class*="thread"]:nth-child(even),
ol li[class^="discussionListItems"] { 
    background-color: #acacac !important; 
}

ol li[class*="thread"]:nth-child(odd) span[class^="prefix"] {
	background: #acacac;
	border-color: #acacac;
}
ol li[class*="thread"]:nth-child(even) span[class^="prefix"] {
	background: #bdbdbd;
	border-color: #bdbdbd;
}
}

@-moz-document url("https://www.christianforums.com/watched/forums"), url-prefix("https://www.christianforums.com/watched") {
/**********************
WATCHED THREADS */
/* Alternate b/g colors */
li.discussionListItem:nth-child(odd),
div.nodeInfo:nth-child(odd),
ol.subForumList:nth-child(odd) { 
    background-color: #bdbdbd !important ;
	border-color: #bdbdbd
}
li.discussionListItem:nth-child(even),
div.nodeInfo:nth-child(even),
ol.subForumList:nth-child(even) { 
    background-color: #acacac!important;
	border-color: #acacac;
}
li.discussionListItem a {
	color: #202020 ;
	font-weight:  normal !important;
}
li.discussionListItem h3 a {
	text-decoration:underline
}

a.PreviewTooltip,
a[href*="preview"]{
	font-family: open sans, verdana
}
/*User sidbar box*/
div.section.visitorPanel div.secondaryContent {
	background: #bdbdbd;
}
div.section.visitorPanel div.secondaryContent span {
	color: #202020;
}
div.section.visitorPanel div.secondaryContent a[href="blessings/"] {
	color: #202020;
	text-decoration: underline
}

/* WATCHED FORUMS */
ol.nodeList { 
	width: 820px !important; margin-left: 0px !important 
}
h3.subHeading { 
	width: 800px !important; margin-left: 0px !important; margin-top: 0px !important; border-radius: 14px 14px 0 0 
}
h3.nodeTitle { 
	width: 785px !important; margin-left: 0px !important 
}

form.sectionMain {
	background-color: #696969 
}
}

@-moz-document domain("christianforums.com"), domain("wilderssecurity.com") {
/* HIDE VARIOUS ELEMENTS */
/* These filters can be turned off by enclosing them in Comment markers (a slash and an asterisk on each end) */

/* Hide user ratings in Threads */
i.fa.fa-star,
div.extraUserInfo  span.dark_postrating_positive {display: none }

/* Kill separate FEATURED THREAD*/
div[id*="featured-thread"] { 
    display: none
}  

/* KILL RED BLOG COUNT FLAG IN NAVBAR */
#navigation > div > nav > div.navTabs > div > ul.publicTabs > li.navTab.ubs.Popup.PopupControl.PopupClosed.PopupContainerControl > a.navLink > 
strong.itemCount {display: none !important;}

/*BLOGS*/

P.explain,
label[for="ctrl_message"] dfn {
	color: black !important
}



}

@-moz-document url("https://www.christianforums.com/forums/american-politics.909/"), url("https://www.christianforums.com/forums/general-politics.1250/"), url("https://www.christianforums.com/forums/current-news-events.489/"), url("https://www.christianforums.com/forums/christian-apologetics-center.237/") {
/*******************************
 CERTAIN FORUM PAGES - HIDE ALL STICKIES */
li[class*="sticky"] { display: none }
}

@-moz-document url-prefix("https://www.christianforums.com/threads/") {
/*****************************
FLAG USER LIST*/
/*HIDE*/
li[data-author="christianforumsuser1"] {
	color:gray !important
}

/*EMPHASIZE*/
li[data-author="christianforumsuser2"] {
	color:red !important
}


}
