css:
@-moz-document domain("cryptonomist.ch") {
.td-container-wrap {
    /* background-color: #460067 !important; */
    background-color: #2f0966  !important;
}

.td-header-menu-wrap.td-affix, .td-header-style-3 .td-affix .td-header-main-menu, .td-header-style-4 .td-affix .td-header-main-menu, .td-header-style-8 .td-header-menu-wrap.td-affix {
    background-color: #2e0343 !important;
}

.td-header-wrap .td-header-menu-wrap-full, .td-header-menu-wrap.td-affix, .td-header-style-3 .td-header-main-menu, .td-header-style-3 .td-affix .td-header-main-menu, .td-header-style-4 .td-header-main-menu, .td-header-style-4 .td-affix .td-header-main-menu, .td-header-style-8 .td-header-menu-wrap.td-affix, .td-header-style-8 .td-header-top-menu-full {
    background-color: #2d0142 !important;
}


/* tab a comparsa */

.td-theme-wrap .sf-menu .td_mega_menu_sub_cats .cur-sub-cat, .td-theme-wrap .td-mega-menu ul {
    background-color: black !important;
}

.td_stretch_content .td_block_mega_menu, .td_stretch_content_1200 .td_block_mega_menu, .td_stretch_content_1400 .td_block_mega_menu, .td_stretch_content_1600 .td_block_mega_menu, .td_stretch_content_1800 .td_block_mega_menu {
    background-color: #50f !important;
}

.td_stretch_content .td_block_mega_menu, .td_stretch_content_1200 .td_block_mega_menu, .td_stretch_content_1400 .td_block_mega_menu, .td_stretch_content_1600 .td_block_mega_menu, .td_stretch_content_1800 .td_block_mega_menu {
    margin-left: 0px;
    margin-right: 0px !important;
    overflow: hidden !important;
}

.td_mega_menu_sub_cats {
    background-color: #30009c !important;
}
.td-theme-wrap .sf-menu .td_mega_menu_sub_cats .cur-sub-cat, .td-theme-wrap .td-mega-menu ul {
    border-color: #000 !important;
}
.td-theme-wrap .td_mod_mega_menu:hover .entry-title a, .td-theme-wrap .sf-menu .td_mega_menu_sub_cats .cur-sub-cat {
    color: #e5ff00 !important;
}
.td_mega_menu_sub_cats a {
    color: #8c9300 !important;
}
.td-mega-span h3 a {
    color: #988100 !important;
}

/* homepage */

.td-header-wrap .td-header-menu-wrap .sf-menu > li > a, .td-header-wrap .td-header-menu-social .td-social-icon-wrap a, .td-header-style-4 .td-header-menu-social .td-social-icon-wrap i, .td-header-style-5 .td-header-menu-social .td-social-icon-wrap i, .td-header-style-6 .td-header-menu-social .td-social-icon-wrap i, .td-header-style-12 .td-header-menu-social .td-social-icon-wrap i, .td-header-wrap .header-search-wrap #td-header-search-button .td-icon-search {
    color: #949604 !important;
}

.td_uid_13_5c6dc8966edb3_rand_style {
    background-color: #5f1a9d !important;
}

a:not(:hover) {
     color: #b400f1 !important;
}
a:hover {
     color: #dc00ff !important;
    background-color: inherit !important;
}

.td-excerpt {
    color: #8b00ff !important;
}
.page-nav a, .page-nav span {
    border: 1px solid #a51eef !important;
}

/* widget & search */
.widget-container {
    border: 1px solid #000 !important;
    background-color: #5904da !important;
}
.widget-header {
    background: #5904da !important;
    border-bottom: 1px solid #081d7b !important;
}
.header-price .price {
    color: #d0ff00 !important;
}

.td-header-wrap .td-drop-down-search {
    background-color: #410c8f !important;
}
input[type="text"], input[type="url"], input[type="tel"], input[type="email"] {
    color: #ceff00 !important;
    border: 1px solid #2b00ff !important;
    background-color: black !important;
}
.td-header-wrap #td-header-search {
    border-color: #2b00ff !important;
}
.td-header-wrap .td-aj-search-results {
    border-top: 1px solid #482672 !important;
    border-bottom: 1px solid #5b0404 !important;
}

/*articoli*/
.td-page-content, .td-post-content, .wpb_text_column, .td_block_text_with_title, .woocommerce #tab-description, .mce-content-body .post blockquote p, .page blockquote p, .td-post-text-content blockquote p  {
    color: #9051ff !important;
}
.author-box-wrap {
    border: 1px solid #9d00ff  !important;
}
.td-page-content .td_quote_box, .td-post-content .td_quote_box, .wpb_text_column .td_quote_box, .td_block_text_with_title .td_quote_box, .bbpress .td_quote_box, .woocommerce #tab-description .td_quote_box, .mce-content-body .td_quote_box, .comment-content .td_quote_box {
    background-color: #22004e !important;
}
.post blockquote p, .page blockquote p, .td-post-text-content blockquote p {
    color: #9051ff !important;
}
.author-box-wrap .desc {
    color: darkgoldenrod !important;
}

/* .tdc-row {
    background-color: #f00 !important;
} */

}
