css:
@-moz-document domain("twoo.com") {
body {
    background: #222 !important;
}
.paymentPage .tw3-content{
    background: #222 !important;
}
.paymentPage .tw3-content .tw3-container{
    background: #333 !important;
}
.background--white{
     background: #333 !important;
}
.paymentPage .tw3-content .paymentPage--sidebar{
    background: #333 !important;
}
.text--black{
    color: #E5E5E5;
}
.tw3-promoCard__text{
    color: #93bcd1;
}
.tw3-box--curvedBottom--premium:after {
    display: block;
    height: 20px;
    position: absolute;
    bottom: 0;
    left: 0;
    right: 0;
    content: " ";
    background-color: #333;
    border-top-left-radius: 50% 20px;
    border-top-right-radius: 50% 20px;
}
.paymentPage .tw3-content .paymentPage--content{
    border-left: 1px solid #444;
    border-right: 1px solid #444;
}
.paymentPage .tw3-content .tw3-container{
    border-right: 1px solid #444;
}
a {
    color: #93bcd1;
    text-decoration: none;
}

.tw3-itpContainer {
    background: #111;
    border-bottom: 1px solid #E5E5E5;
}
.tw3-itp__addme:after {
    background: none;
}
.tw3-footerContainer {
    background: #3e3e3e;
}
.tw3-footerContainer .tw3-footer--mainMenu a {
    color: #93bcd1 !important;
    font-weight: 500;
}
.tw3-notifications__container {
    min-height: 130px;
    position: relative;
    background: #3e3e3e;
}
.tw3-notifications {
    background: #3e3e3e;
    padding: 10px 0;
    border: 1px solid rgba(0, 0, 0, 0.1);
    -webkit-box-shadow: 0 2px 10px 0 rgba(0, 0, 0, 0.25);
    -moz-box-shadow: 0 2px 10px 0 rgba(0, 0, 0, 0.25);
    box-shadow: 0 2px 10px 0 rgba(0, 0, 0, 0.25);
}
.tw3-notifications__container > ul .tw3-mediav2 {
    background: #3e3e3e;
}
.tw3-mediav2--desktop .tw3-mediav2__body {
    color: white;
}
.tw3-pointerMenu {
    background: #3e3e3e;
    border: 1px solid #111;
}
.tw3-headerV2__actions__item .dropDownMenu .tw3-pointerMenu__item--grey {
    background-color: #3e3e3e;
}
.tw3-headerV2__actions__item .dropDownMenu > li > a,
.tw3-headerV2__actions__item .dropDownMenu .fakeLink {
    color: white;
}

.tw3-pointerMenu > li > .fakeLink:hover,
.tw3-pointerMenu > li > a:hover {
    background-color: #333;
    text-decoration: none;
}
.tw3-notifications__container > ul .tw3-mediav2:hover:not(.tw3-personList__empty) {
    background: #333;
    cursor: pointer;
}
.game .photoCover__info,
.activity--followers .photoCover__info,
.activity--likesyou .photoCover__info {
    color: white;
}
.tw3-notifications__container li {
    color: #93bcd1;
}
.tw3-notifications__container > ul .tw3-mediav2.selected:not(.tw3-personList__empty) {
    background: #555;
}
.photoCover {
    background: #444;
}
.tw3-whoLikesYouContainer {
    background: #444;
}
.tw3-box {
    background: #444;
}
.tw3-box--padding--off {
    background: #444;
}
.tw3-menu li a {
    color: #93bcd1;
}
.tw3-menu--dark li {
    background: #555;
    color: #93bcd1;
}
.tw3-box--qAndA--subtle,
.tw3-box--qAndA--subtle--noCategory {
    background: #444;
    color: #93bcd1;
    border-color: #555;
}
.conversationsContainer__item__bottomBar__container {
    background: #444;
    color: #93bcd1;
}
.conversationsContainer__item__bottomBar__container .bottomBar__textarea .tw3-textarea {
    background: #444;
    color: #93bcd1;
}
.conversationsContainer__item__bottomBar__container .bottomBar__textarea .tw3-textarea:focus {
    background: #444;
    color: #93bcd1;
}
.tw3-pointerMenu > li > .fakeLink,
.tw3-pointerMenu > li > a {
    color: #949494 !important;
    display: block;
    padding: 15px 10px;
    font-size: 14px;
    font-weight: normal;
}
.messagesContainer .inboxContainer__menu {
    background: #444;
    color: #93bcd1;
    border-color: #555;
}
.tw3-tabsHolder {
    background: #444;
    color: #93bcd1;
    border-color: #555;
}
.tw3-pointerMenu > li > ul > li > .fakeLink,
.tw3-pointerMenu > li > ul > li > a {
    color: #949494 !important;
}
.tw3-pointerMenu > li > ul > li:hover {
    background-color: #333;
    text-decoration: none;
}
html:not(.touch) .inboxContainer .tw3-personList li:hover {
    background: #555
}
.inboxContainer .tw3-personList li.dummy:hover {
    background: #555
}
.inboxContainer .tw3-personList li.selected {
    background: #555;
}
.inboxContainer .tw3-personList li .tw3-person__info__title,
.inboxContainer .tw3-personList li .tw3-person__info__description {
    color: #93bcd1;
}
.inboxContainer .tw3-personList li.grouped {
    background: #333;
}
.inboxContainer .tw3-personList li {
    background: #333;
}
.inboxContainer .tw3-personList li:hover {
    background: #555;
}
.inboxContainer .tw3-personList li .tw3-person__info__description{
    color: #a8a8a8;
}
.profilev4seo .tw3-profile__info,
.profilev4 .tw3-profile__info {
    background: #333;
}
.profilev4seo .tw3-profile__body,
.profilev4 .tw3-profile__body {
    background: #333;
    color: white;
}
.tw3-editable__block .tw3-field__editIcon {
    background: none;
}
.tw3-editable__block .tw3-field__editIcon::after {
    background: none;
}
.tw3-media--verifications {
    background: #555;
}
.tw3-box--lightGrey {
    background: #555;
}
.chatPromo__container {
    color: white;
}
.inboxContainer .tw3-personList li.grouped.selected {
    background: #666;
    color: white;
}
.tw3-pager__item{
    color: #93bcd1;
}
.tw3-pager__item.first, .tw3-pager__item.last{
    color: #93bcd1;
}
.tw3-pager__item.first i, .tw3-pager__item.last i{
    color: #93bcd1;
}
.tw3-promoHor{
    background: #333;
    border: 5px solid #666;
}
.text--grey {
    color: #999;
}
.tw3-promoCard--boost{
        background: #555;
    border: 5px solid #666;
}
.tw3-promoCard--avatarContainer{
    background: #555;
    border: 5px solid #666;
}
.tw3-promoCard--spotlight{
    background: #555;
    border: 5px solid #666;
}
.tw3-promoCard{
    background: #555;
    border: 5px solid #666;
}
.text--dark{
    color: #111;
}
.tw3-promoCard--sticker{
    background: #555;
}
.tw3-promoCard{
        border: 5px solid #999;
}
.tw3-iconGrey{
    color: #e9e9e9;
}
.tw3-card {
    background: #666;
    border-color: #777;
}
.peopleList li .peopleList__item__info__title a {
    color: #93bcd1;
}
.pt--tight {
    color: #93bcd1;
}
.text--subtle {
    color: #E7E7E7;
}
.tw3-card__userInfo__locAndWork:after {
    background: none;
}
.tw3-box mb--compact {
    color: #93bcd1;
}
.text--bold {
    color: #93bcd1;
}
.tw3-form__row {
    color: #93bcd1;
}
.tw3-showMore:before {
    background: none;
}
.profilev4seo .tw3-profile__body__boxChat,
.profilev4 .tw3-profile__body__boxChat {
    background: #333;
}
.tw3-profile__header {
    background: #333;
}
.tw3-box--qAndA--subtle .tw3-qAndA__explanation,
.tw3-box--qAndA--subtle--noCategory .tw3-qAndA__explanation {
    background: #555;
}
.tw3-box--qAndA--subtle .tw3-qAndA__explanation__send--disabled,
.tw3-box--qAndA--subtle--noCategory .tw3-qAndA__explanation__send--disabled {
    color: #93bcd1;
}
.tw3-box--qAndA--subtle .tw3-qAndA__explanation .tw3-textarea,
.tw3-box--qAndA--subtle--noCategory .tw3-qAndA__explanation .tw3-textarea {
    background: #555;
    color: white;
}
.boostMenuSidebar .boostSlideIn {
    border: 1px solid #555;
}
.boostMenuSidebar .boostSlideIn{
    background: #444;
}
.boostMenuSidebar .boostSlideIn>.boostedUsers>li>a .city {
    text-decoration: none;
    color: #999;
}
.peopleList li .peopleList__item__info__description{
    color: #d7d7d7
}
.tw3-box--curvedBottom--credits:after{
    background-color: #333;
}
}
