css:
@-moz-document url-prefix(https://aldaba.com/) {

BODY {

    margin:0px;

    padding:0px;

    -webkit-font-smoothing:antialiased;

    -moz-osx-font-smoothing:grayscale;

    color:lime;
    
    background-color:black;

}

SELECT, INPUT {

    font:normal 100%'Open Sans', calibri, arial, sans-serif;

    color:limegreen;

    background-color:none;

    border:1px solid #dddddd;

    -webkit-border-radius:5px;

    -moz-border-radius:5px;

    border-radius:5px;

    margin:4px;

    outline-color:#aaa;

    -moz-outline-color:#aaa;

    padding:3px;

}

.rounded {

    border-radius: 50%;

}

h1, h2, h3 {

    font:normal 100%'Open Sans', calibri, arial, sans-serif;

    display:inline;

}

.th1 {

    font-size:180%;

    font-weight:bold;

    text-decoration:none;

    color:yellowgreen;

}

.th2 {

    font-size:165%;

    color:black;

}

.tm1 {

    font-size:180%;

    font-weight:bold;

    color:white;

}

.wide {

    width:50%;

    font-size:100%;

    color:#696969;

    border:1px solid #dddddd;

}

TEXTAREA, .TAREA {

    font-size:100%;

    font-family:'Open Sans', calibri, sans-serif;

    color:#696969;

    width:95%;

    border:1px solid #dddddd;

    -webkit-border-radius:5px;

    -moz-border-radius:5px;

    border-radius:5px;

    margin:4px;

    outline-color:#ccc;

    -moz-outline-color:#ccc;

    padding:3px;

}

TABLE {

    font-size:100%;
    background-color:#2B2B2B;

}

.gtop {

    padding-top:18px;

}

.gbot {

    padding-bottom:18px;

}

.gright {

    padding-right:18px;

}

tr.lista td {

    padding:2px 0px;
    background-color:#00192c;

}

tr.lista_s td {

    padding:4px 0px;

}

tr.lista_m td {

    padding:1px 0px;
    background-color:#2B2B2B;

}

.lista {

    padding:1px;

}

TH, .ih {

    position:sticky;

    top:0px;

    background-color:white;

}

A {

    color:white;

    font-size:100%;

    text-decoration:underline;

}

A:link {

    color:#0ae0c9;

    text-decoration:none;

}

A:visited {

    color:red;

}

A:hover {

    color:blue;

    text-decoration:none;

}

.top {

    color:#777777;

    font-size:100%;

}

A.match {

    color:#B22222;

    text-decoration:underline;

}

A.match:visited {

    color:#B22222;

}

A.match:hover {

    text-decoration:none;

    color:#FF0000;

}

.match {

    color:#B22222;

    font-size:100%;

}

A.menu {

    color:black;

    text-decoration:none;

}

A.menu:visited {

    color:black;

}

A.menu:hover {

    text-decoration:none;

    color:silver;

}

.menu {

    color:black;

    font-size:125%;

}

.piepagina {

    color:Black;

    font-size:100%;

}

.azul_0 {

    background-color:#fafafa;

}

.azul_1 {

    background-color:#e5e5e5;

    color:#aaaaaa;

}

.azul_10 {

    color:#a9a9a9;

}

.azul_2 {

    color:#777;

    font-size:120%;

    height:36px;

}

.azul_3 {

    background-color:#dddddd;

    color:#ffffc1;

}

.cabecilla {

    font-size:125%;

    color:#ff5e5e;

    background-color:black;

}

.cabecillat {

    font-size:125%;

    color:#777;

    background-color:none;

}

.ficha {

    font-size:110%;

}

.ficha_head {

    font-size:140%;

}

.box {

    color:#777;

    font-size:125%;

    text-align:right;

    padding: 12px 0px 0px 0px;

}

.box_cabecilla {

    border:1px solid #dddddd;

    font-size:125%;

    color:#777;

    text-align:center;

    padding:9px;

}

.nb {

    width:17px;

    height:17px;

    border:none;

    margin:0;

    box-shadow: 0px 0px 0px #fff;

    -moz-box-shadow: 0px 0px 0px #fff;

    -webkit-box-shadow: 0px 0px 0px #fff;

}

.button {

    font:normal 100%'Open Sans', calibri, arial, sans-serif;

    width:200px;

    color:white;

    background-color:#E33B1E;

    border:1px solid #E33B1E;

    padding:12px 21px;

    -webkit-border-radius:5px;

    -moz-border-radius:5px;

    border-radius:5px;

    outline-color:#ccc;

    -moz-outline-color:#ccc;

}

.button:hover {

    background-color:silver;

    border:1px solid silver;

    color:white;

}

.nota {

    border:1px none;

    background-color:#F5F5F5;

}

.b, .texto {

    font-size:100%;

}

HR {

    color:black;

    height:1px;

}

.indice {

    color:#FF9900;

}

.titulo {

    color:#708090;

}

.titulo_0 {

    color:#708090;

}

.italica {

    font-size:100%;

    color:#696969;

}

.row {

    border-bottom:none 1px #fbfbfb;

}

.e {

    color:#eeeeee;

}

A:active, A.top, A.clear {

    color:#777777;

    text-decoration:none;

}

A.top:visited, A.clear:visited, .clear {

    color:#777777;

}

A.top:hover, A.clear:hover {

    text-decoration:none;

    color:silver;

}

.black_overlay {

    display: none;

    position: fixed;

    top: 0%;

    left: 0%;

    width: 100%;

    height: 100%;

    background-color: whitesmoke;

    z-index:1001;

    -moz-opacity: 0.6;

    opacity:.60;

    filter: alpha(opacity=60);

}

.white_content {

    display: none;

    position:fixed;

    top: 50%;

    left: 50%;

    transform: translate(-50%, -50%);

    padding: 16px 32px 42px 32px;

    /*border: 2px solid gray;*/

    background-color: white;

    z-index:1002;

    overflow: auto;

}

.tbutton {

    width:130px;

    height:40px;

    font:normal 100%'open sans', calibri, arial, sans-serif;

    border:1px solid #FFFAF0;

    background-color:#FFFAF0;

}

.tbutton:hover {

    background-color:silver;

    border:1px solid silver;

    color:white;

    cursor:pointer;

}

.gbutton {

    width:200px;

    font:normal 100%'Open Sans', calibri, arial, sans-serif;

    color:white;

    background-color:#E33B1E;

    border:1px solid firebrick;

    padding:12px 21px;

    -webkit-border-radius:5px;

    -moz-border-radius:5px;

    border-radius:5px;

    outline-color:#ccc;

    -moz-outline-color:#ccc;

}

.gbutton:hover {

    background-color:silver;

    border:1px solid silver;

    color:white;

}

.rbutton {

    width:200px;

    font:normal 100%'Open Sans', calibri, arial, sans-serif;

    color:white;

    background-color:#E33B1E;

    border:1px solid #E33B1E;

    padding:12px 21px;

    -webkit-border-radius:5px;

    -moz-border-radius:5px;

    border-radius:5px;

    outline-color:#ccc;

    -moz-outline-color:#ccc;

}

.rbutton:hover {

    background-color:silver;

    border:1px solid silver;

    color:white;

}

.xbutton {

    width:200px;

    font:normal 100%'Open Sans', calibri, arial, sans-serif;

    color:white;

    background-color:slategray;

    border:1px solid slategray;

    padding:12px 21px;

    -webkit-border-radius:5px;

    -moz-border-radius:5px;

    border-radius:5px;

    outline-color:#ccc;

    -moz-outline-color:#ccc;

}

.xbutton:hover {

    background-color:silver;

    border:1px solid silver;

    color:white;

}

.circle {

    width:128px;

    height:128px;

    border:1px solid #eee;

    -webkit-border-radius:128px;

    -moz-border-radius:128px;

    border-radius:128px;

    display:table-cell;

    vertical-align:middle;

}

A.menu2 {

    font-size:100%;

    color:#000000;

    text-decoration:none;

}

A.menu2:visited {

    color:#000000;

}

A.menu2:hover {

    text-decoration:none;

    color:silver;

}

.line {

    color:#e5e5e5;

    font-weight:normal;

}
	}
