css:
@-moz-document url-prefix("https://svn-forge.din.developpement-durable.gouv.fr") {
body {
    font-size: 0.9em;
}

#top-menu {

    background-color: #000;
    font-size: 1em;
    padding: 0.5em
}

table.list,
.table-list {

    border: 1px solid #e4e4e4;
    border-collapse: collapse;
    width: 100%;
    margin-bottom: 6px;
    font-size: 0.9em;
}

tr.priority-4 {
    border: 1px solid #ccc;
    background: #eef9ee;
}

tr.priority-4 a,
tr.priority-4 avisited {
    color: #3f4b89;
}

tr.priority-4 {
    border: 1px solid #ccc;
    color: #787777;
    background: #f5fff5;
}


tr.priority-4:hover td,
tr.priority-4:hover a,
tr.priority-4:hover a:hover,
tr.priority-4:hover span.status,
tr.priority-4:hover p.extra-fields {

    color: #fff;
    background: #42a942;
    text-decoration: none;
}

table.list th {
    background-color: #f0f0f0;
}

table.issues tr > td {

    padding: 2px 10px;
}

.author,
.assigned_to,
.updated_on {
    font-size: 0.8em
}

#main-menu li {
    margin: 0 2px;
}

#main-menu li a {
    background-color: #5F5F5F;
}

#header {

    background-color: #3c3e36;
}


#header h1,
#header h1 a {

    color: #fff;
    text-shadow: none;
}

h2,
.wiki h1 {

    background-color: #dedcdc;
    font-size: 22px;
}

div#activity dl,
#search-results {

    margin-left: 2em;
    font-size: 1.1em;
}

div#activity dd,
#search-results dd {

    margin-bottom: 1em;
    padding-left: 1.8em;
    font-size: 0.9em;
}


.status-1.priority-high2,
.status-1.priority-highest {

    background-color: #ffe8dc;
}

.list .status {

    white-space: normal;
    width: 85px;
    overflow: hidden;
}

table.issues tr > td {

    padding: 2px 10px;
}

.list .author,
.list .assigned_to {

    text-align: left;
    text-overflow: ellipsis;
    overflow: hidden;
}

tr.closed {
    background: #fffce4;
}
}
