css:
@-moz-document domain('4chan.org'), domain('4channel.org') {
.board {
    width: 1000px;
    margin: 0 auto;
    border: none;
}
div.thread {
    clear: both;
    display: block;
    padding: 0;
    background: #fff;
    border: 1px solid #b7c5d9;
    border-radius: 10px;
    margin: 1em auto;
}
div.opContainer {
    display: block;
    padding: 1em;
}
div.op {
    display: block;
    background: none;
}
div.op .extButton, div.op .threadHideButton {
    float: left;
    margin: 0 0;
}
div.post {
    margin: 0;
}
#quote-preview {
    padding: 1em;
}
div.post.preview {
    background: #fff;
    border: 1px solid #b7c5d9;
    border-radius: 10px;
    padding: 1em;
    max-width: 800px;
}
div.post div.file {
    display: block;
}
div.post div.file .fileText {
    margin: 5px 0;
}
div.post div.file:before {
    content:" ";
    clear: both;
    display: block;
}
div.post div.file .fileThumb {
    float: none;
    margin: 0.6em 0;
    display: block;
}
div.post div.file .fileThumb img {
    border: none;
    float: none;
}
div.post div.file .fileThumb img.expanded-thumb {
    display: block;
    width: 100%;
}
div.post blockquote.postMessage {
    padding: 0;
    margin: 0.5em 0;
    border: 1px solid #b7c5d9;
    border-radius: 10px;
    padding: 1em;
}
div.post div.postInfo {
    display: block;
    margin: 0.5em 0;
}
span.summary {
    color: #707070;
    margin: 0 0 1em;
    display: block;
    background: #EAECF8;
    border: 1px solid #b7c5d9;
    border-width: 1px 0;
    padding: 0.5em;
}
div.sideArrows {
    display: none;
}
div.reply {
    background-color: #d6daf0;
    border: none;
    display: block;
    padding: 1em;
    margin: 0em 1em 1em 1em;
    border: 1px solid #b7c5d9;
    border-radius: 10px;
}
}
