css:
@-moz-document domain("plug.dj") {
#chat > #chat-messages .emote .text {
    padding-left: 8px;
}
#chat > #chat-messages .emote, #chat > #chat-messages .mention, #chat > #chat-messages .message, #chat > #chat-messages .moderation, #chat > #chat-messages .skip, #chat > #chat-messages .system, #chat .update, #chat > #chat-messages .welcome {
    min-height: 0px;
}

.community #chat > #chat-messages .cm.mention.is-admin {
    background: none;
    background: linear-gradient(180deg, black 0%, transparent 50%, black 100%), linear-gradient(90deg, transparent -1%, rgba(66,165,220,0.7) 1%, transparent 3%);
}
.community #chat > #chat-messages .cm.mention.is-ambassador {
    background: none;
    background: linear-gradient(180deg, black 0%, transparent 50%, black 100%), linear-gradient(90deg, transparent -1%, rgba(137,190,108,0.7) 1%, transparent 3%);
}
.community #chat > #chat-messages .cm.mention.is-sitemod {
    background: none;
    background: linear-gradient(180deg, black 0%, transparent 50%, black 100%), linear-gradient(90deg, transparent -1%, rgba(231,76,60,0.7) 1%, transparent 3%);
}
.community #chat > #chat-messages .cm.mention.is-plot {
    background: none;
    background: linear-gradient(180deg, black 0%, transparent 50%, black 100%), linear-gradient(90deg, transparent -1%, rgba(230,126,34,0.7) 1%, transparent 3%);
}
.community #chat > #chat-messages .cm.mention.is-promoter {
    background: none;
    background: linear-gradient(180deg, black 0%, transparent 50%, black 100%), linear-gradient(90deg, transparent -1%, rgba(26,188,156,0.7) 1%, transparent 3%);
}
.community #chat > #chat-messages .cm.mention.is-dj, .community #chat > #chat-messages .cm.mention.is-staff {
    background: none;
    background: linear-gradient(180deg, black 0%, transparent 50%, black 100%), linear-gradient(90deg, transparent -1%, rgba(172,118,255,0.7) 1%, transparent 3%);
}
.community #chat > #chat-messages .cm.mention.is-subscriber {
    background: none;
    background: linear-gradient(180deg, black 0%, transparent 50%, black 100%), linear-gradient(90deg, transparent -1%, rgba(197,152,64,0.7) 1%, transparent 3%);
}
.community #chat > #chat-messages .cm.mention.is-subscriber.silver {
    background: none;
    background: linear-gradient(180deg, black 0%, transparent 50%, black 100%), linear-gradient(90deg, transparent -1%, rgba(134,148,178,0.7) 1%, transparent 3%);
}
.community #chat > #chat-messages .cm.mention.is-you {
    background: none;
    background: linear-gradient(180deg, black 0%, transparent 50%, black 100%), linear-gradient(90deg, transparent -1%, rgba(255,221,111,0.7) 1%, transparent 3%);
}

#chat > #chat-messages .badge-box {
    display: none;
}
#chat > #chat-messages .emote .from {
    float: left;
    margin-bottom: -1px;
    padding-right: 5px;
}
#chat > #chat-messages .emote .timestamp {
    display: none !important;
}
#chat > #chat-messages .emote .un {
    font-style: italic;
}
#chat > #chat-messages .msg {
    padding: 5px 8px 6px 8px;
}
#chat > #chat-messages .text {
    padding-left: 10px;
}
#chat > #chat-messages .text > br {
    border-top: 1px solid #404246;
    content:' ';
    display: block;
    margin: 1px 0px 1px -8px;
}
#chat-messages, #chat-input-field {
    font-family: Arial;
}
}
