css:
@-moz-document url-prefix("http://localhost:5050/browser/") {
body {
	font-family: -apple-system, BlinkMacSystemFont, Segoe UI, Oxygen, Ubuntu, Cantarell, Fira Sans, Droid Sans, Helvetica Neue, Roboto, sans-serif;
    color: #9aa1aa;
}
.wcDocker, .wcFrameFlasher, .wcFrameShadower, .wcFrameTitleBar, .wcModalBlocker, .wcPanelBackground, .wcPanelBackground .wcCenter {
    background-color: #214664;
}
.negative-space {
    background: #222;
}
.card {
	background-color: #484d56;
}
.card-header {
    background-color: #1b1e1e; 
    border-bottom: 1px solid #dde0e6;
}
.nav-tabs {
    background-color: #1b1e1e;
    border-bottom: 1px solid #1b1e1e;
    border-top: 1px solid #436bba;
}
.nav.nav-tabs .nav-link {
	color: #9aa1aa;
}
.nav.nav-tabs .nav-link.active {
    border-bottom: 3px solid #7cb6e4!important;
    border-bottom-width: 3px !important;
    border-bottom-style: solid !important;
    border-bottom-color: rgb(124, 182, 228) !important;
    color: #7cb6e4;
}
.wcFrameButtonBar {
    background-color: #214664;
}
  .wcFrameTitle, .wcPanelTab {
    color: #81e1ff;
}
.aciTree .aciTreeButton, .aciTree .aciTreeColumn, .aciTree .aciTreeIcon, .aciTree .aciTreeItem, .aciTree .aciTreePush, .aciTree .aciTreeText {
    color: #8acaf2;
}
.table {
    background-color: #214664;
}
.backgrid thead td, .backgrid thead th {
    background: #326690;
    background-color: #326690!important;
    text-align: left;
}
table tr th button {
    color: #35f3b7;
}
/*EDITOR*/
.editor-toolbar {
    background: #214664;
}
.wcDocker, .wcFrameFlasher, .wcFrameShadower, .wcFrameTitleBar, .wcModalBlocker, .wcPanelBackground, .wcPanelBackground .wcCenter {
    background-color: #214664;
}
.wcFrameTitle, .wcPanelTab {
    color: #7be1ff;
}
.wcPanelTabActive {
    color: #8ad78d;
}
#datagrid .slick-header .slick-header-columns {
    background: #8a9dae;
    font-weight: 600;
    color: #5d2376;
}
.ui-widget-content.slick-row.even, .ui-widget-content.slick-row.odd {
	background-color: #214664;
    font-weight: 600;
    color: #9aa1aa;
}
}
