css:
@-moz-document domain("zetcode.com") {
/* UNCOMMENT THE LINES BELOW FOR GOOGLE FONTS */
/* @import url('https://fonts.googleapis.com/css?family=Russo+One');
@import url('https://fonts.googleapis.com/css?family=Roboto+Slab'); */

h1, h2, h3, h4, h5, h6, h1.main-title { font-family: 'Russo One', sans-serif; font-weight: normal; color: #586e75; }
h1.main-title { color: #073642; }


body, div.container, div.content, #ebooks { background-color: #fdf6e3; } 
p, .content ul *, .content ol * { color: #073642; }
p, .content ul *, .content ol *, a { font-family: 'Roboto Slab', sans-serif; }

a, .content a { color: #268bd2; }
a:hover, .content a:hover { color: #268bd2; }
a:visited, .content a:visited { color: #93a1a1; }
a:active, .content a:active { color: #cb4b16; }

div.codehead, pre, pre.code, pre.explanation {
    font-family: "Consolas";
}
p > code {
    background-color: #eee8d5; padding: 0px 4px; border-radius: 2px;
}
div.codehead, pre, pre.code, pre.explanation {
    text-shadow: 1px 1px 1px #002730; /* #002730; */
    font-size: 10pt;
}
div.codehead { background-color: #073642; color: #859900; padding: 2px 10px; border-radius: 5px 5px 0 0; }
pre, pre.code, pre.explanation { background-color: #002b36; color: #839496; padding: 10px 10px; border-radius: 5px; line-height: 11pt; }
div.codehead + pre { border-radius: 0 0 5px 5px; border-top: 1px solid #002730; padding: 5px 10px 15px 10px; }

#ebooks { margin-top: 65px; opacity: 0.5; }
#ebooks ul, h2 { text-align: left; line-height: 1.2em; } 
#ebooks ul li a { font-family: 'Roboto Slab', monospace; font-size: 10pt; text-decoration: none; }
}
