@namespace url(http://www.w3.org/1999/xhtml);
/* By Jos van Nijnatten. <11-04-2009> */

@-moz-document domain(google.com),  domain(google.nl), domain(google.co.uk),  domain(google.co.au), domain(google.pl), domain(google.be), domain(google.fr), domain(google.de), domain(google.es), domain(google.it) {/*Edit this*/
  form[name="f"] * td[width="25%"] a,
  td[class~="xsm"],
  *.std nobr,
  *#ssb p, *#res *.e,
  *#res *.ts,
  *#res *.s div,
  *#res *.s cite,
  *.clr,
  *#resultStats,
  *.tc[colspan="2"],
  *#ap,
  table[class="ft t bb bt"],
  font[size="-1"],
  *.gbh,
  *#gbar *.gb2,
  *#gbar *.gb3,
  *#gbar *.gbi,
  *#gbar * b,
  #mbEnd,
  div#main table:first-child * td[valign="top"] table * tr[bgcolor="#3366cc"] td,
  div#main table[width="90%"] * td[width="75%"],
  div#main table[width="90%"] * td[valign="top"] table:NOT(:first-child),
  div#main div#maincontent table tr td[width*="75%"] p,
  div#main div#maincontent table tr td[width*="75%"] div,
  div#main div#maincontent * div#rhs_login_signup_box table:NOT(:first-child),
  div#main div.footer,
  div#main * tr#ga-fprow,
  *#guser nobr a:NOT([href*="accounts"]),
  *#gb nobr a:NOT([href*="accounts"]),
  *#gbar nobr a[href*="maps"],
  *#gbar nobr a[href*="video"],
  *#gbar nobr a[href*="mail"],
  center>font[size="-1"],
  center p,
  *#ImgCont>table,
  *#tads,
  img[src*="key_security"],
  img[src*="globe_getinvolved"],
  img[src*="spraycan_customize"],
  img[src*="tshirt"],
  form[name="f"] table#frame * td[nowrap=""] font[size="-2"],
  table[width="94%"] * td[width="75%"],
  table[width="95%"][bgcolor="#c3d9ff"] {
    display:none;
  }

  body {background-color:#fff;background-image:none!important;}
  *#guser, *#gb, *#gbar {margin-top:10px!important;}
  *#guser nobr, *#gb nobr {color:#FFF; margin-right:15px;}
  *#guser nobr b, *#gb nobr b {position:relative; left:7px; color:#000;}
  *#gbar {margin-left:15px;}

  center * {font-variant:small-caps;}
  center:first-child, *#lgpd {margin-top:150px;}
  *#rstc center:first-child {margin-top:0px!important;}
  div#main table[width="90%"],
    form[name="f"] table#frame tr:first-child td img[height="20"] {
      margin-top:120px;
    }
  div[id*="res"][class*="med"] * ol li div.s {max-width:100%!important;width:100%;}
  table[width="94%"] {width:400px !important;}
  div#main div#maincontent table tr td[width*="75%"] {width:40%!important;}
  div#main div#maincontent table {width:75%!important;}
  div#main div#maincontent * *#gaia_loginbox {margin-top:10em;}



  table[width="94%"] tr:first-child td[style="text-align: left;"][valign="top"] {padding-top:50px!important;}
  table * tr[bgcolor="#e8eefa"] * div[style="margin: 10px 0pt;"]{height: 65px;}/*hidden*/
  table[bgcolor="#e8eefa"] {display:none;}/*hidden*/



  form[name="f"] table#frame * td[nowrap=""] {text-align:center;}
  form[name="f"] table#frame * td[nowrap=""][valign="top"] {width:40px;}
  input[type="submit"] {margin-bottom:20px;}
  *#ssb {height:22px;}
  div#main table[width="90%"]{width:25%!important;}
  *#bsf, *#ssb, *.blk, *.t,
    div#main table:first-child * td[valign="top"] table * tr[bgcolor="#e5ecf9"] td {
      border-top:1px solid #777!important;
      background:#F9F9F9 none repeat scroll 0 0!important;
    }

  li.g a:link,li.g a:visited,li.g a:hover,li.g a:active {color:#005!important;text-decoration:none;}

  input[class="gaia le button"] {margin:0px!important;}
  input, select {
    border: 1px solid rgb(170, 170, 170) !important;
    background-color:#FFF;
    -moz-border-radius: 2px !important;
    -moz-appearance:none !important; 
    margin-bottom: 5px;
    color: rgb(51, 51, 51);
    padding: 2px;
  }
  input[type="submit"], select {
    margin-right: 5px;
    width: 150px;/*Edit this*/
    background-image: url(data:image/png;base64,);
  }
}
