css:
@-moz-document domain("soundgasm.net") {
body {
    background-color: #0a0106;
    margin: 40px;
    /* font: 13px/20px normal Helvetica, Arial, sans-serif; */
    font-family: Ubuntu, sans-serif;
    color: rgb(217, 208, 208);
    align-content: center;
}

a:link {
    color: rgb(119,82, 131);
}

/* visited link */
a:visited {
    color: #703d8e;
}

/* mouse over link */
a:hover {
        transition: all 0.5s ease;
    color: #75a2d0;
}



/* selected link */
a:active {
    color: #580b2b;
}



margin.a {
    color: #fff;
    text-decoration: underline;
    cursor: auto;
}


.logo {
    height: 90px;
    width: 200px;
    display: inherit;
    text-decoration: none;
    color: #848484;
}


nav > a {
    /* width: 80px; */
    color: #98858a;
    background-color: #0a0106;
    font-weight: 500;
    font-family: Ubuntu, sans-serif;
    font-size: 16px;
    text-align: center;
    text-decoration: none;
    padding: 5px 20px;
}

element.style {
    color: #6b1292;
}

.jp-audio, .jp-audio-stream, .jp-video {
    font-size: 16px;
    font-family: Verdana, Arial, sans-serif;
    line-height: 1.6;
    color: #b389c6;
    border: 1px solid #7ec2e2;
    border-radius: 4px;
    background-color: #000;
}
.jp-details, .jp-playlist {
    width: 100%;
    background-color: #3e3e3e;
    border-top: 1px solid #9400e3;
}


.jp-description {
color: #FFF;
}


.jp-toggles button {
    display: block;
    float: right;
    width: 25px;
    height: 18px;
    text-indent: -9px;
    line-height: 100%;
    border: none;
    cursor: pointer;
}

h1 {
    color: #804ba0;
    background-color: #373636;
    border-bottom: 1px solid #D0D0D0;
    font-size: 19px;
    font-weight: normal;
    margin: 0 0 14px 0;
    padding: 14px 15px 10px 15px;
}


input, textarea {
    font-size: 12px;
    padding: 4px 0;
    border: solid 1px #aacfe4;
    width: 100%;
    margin: 2px 0 20px 0;

    background-color: #696969;
}


/*-------My Audios-----------*/
.sound-management>a.private
{
    color: #ac1068;
}

.sound-management>a.public
{
    color: #1268bf;
}

::selection {
  background: #ac1068;
}


/*------------Player -------------*/
.jp-play {
    background: url(https://i.imgur.com/qnZg2jC.jpg) 0 0 no-repeat;
}

.jp-play:focus {
    background: url(https://i.imgur.com/qnZg2jC.jpg) -41px 0 no-repeat;
}
.jp-state-playing .jp-play {
    background: url(https://i.imgur.com/qnZg2jC.jpg) 0 -42px no-repeat;
}

.jp-state-playing .jp-play:focus {
    background: url(https://i.imgur.com/qnZg2jC.jpg) -41px -42px no-repeat;
}
.jp-stop {
    background: url(https://i.imgur.com/qnZg2jC.jpg) 0 -83px no-repeat;
    margin-left: 10px;
}
.jp-stop:focus {
    background: url(https://i.imgur.com/qnZg2jC.jpg) -29px -83px no-repeat;
}

.jp-repeat {
    background: url(https://i.imgur.com/qnZg2jC.jpg) 0 -290px no-repeat; 
}
.jp-repeat:focus {
    background: url(https://i.imgur.com/qnZg2jC.jpg) 0 -290px no-repeat; 
}
.jp-mute {
    background: url(https://i.imgur.com/qnZg2jC.jpg) 0 -170px no-repeat;
}

.jp-mute:focus {
    background: url(https://i.imgur.com/qnZg2jC.jpg) 0 -170px no-repeat;
}
.jp-volume-bar-value {
    background: url(https://i.imgur.com/qnZg2jC.jpg) 0 -256px repeat-x;
    width: 0px;
    height: 5px;
}
.jp-volume-max {
    background: url(https://i.imgur.com/qnZg2jC.jpg) 0 -186px no-repeat;
}
.jp-volume-bar-value:focus {
    background: url(https://i.imgur.com/qnZg2jC.jpg) 0 -256px repeat-x;
    width: 0px;
    height: 5px;
}
.jp-volume-max:focus {
    background: url(https://i.imgur.com/qnZg2jC.jpg) 0 -186px no-repeat;
}



.jp-seek-bar {
  background: url(https://i.imgur.com/qnZg2jC.jpg) 0 -202px repeat-x;
  width: 0px;
  height: 100%;
  cursor: pointer; }

.jp-play-bar {
  background: url(https://i.imgur.com/qnZg2jC.jpg) 0 -218px repeat-x;
  width: 0px;
  height: 100%; }



/*------------Player -------------*/
}
