css:
@-moz-document regexp("https?://live.bilibili.com/\\d+.*") {
/*-- 标题栏部分 --*/
/* 隐藏多余信息 */
div.room-info-ctnr > div.room-info-upper-row div.hour-rank,
div.room-info-ctnr > div.room-info-upper-row div.week-star-rank,
div.room-info-ctnr > div.room-info-down-row div.icon-label {
    display: none !important;
}
/*-- 聊天栏 --*/
/* 隐藏榜单信息 */
div#rank-list-vm {
    display: none !important;
}
/* 修复聊天栏 */
.live-room-app .app-content .app-body .player-and-aside-area .aside-area .chat-history-panel {
    height: calc(100% - 145px) !important;
    margin-top: 5px !important;
}
.chat-history-panel {
    margin-top: 0 !important;
}
/*-- 宝箱 */
.box-icon {
    height: 35px !important;
    width: 40px !important;
}
.count-down {
    margin: 0;
    padding: 0 !important;
}
/* 辣条 */
div.penury-gift-msg {
    display: none !important;
}
.chat-history-panel .chat-history-list.with-penury-gift {
    height: 100%;
}
/*-- 礼物栏 --*/
.gift-control-panel .gift-left-part {
    padding: 5px 0 0 15px !important;
}
.gift-panel-switch {
    top: 0px !important;
    height: 30px !important;
}
.gift-panel-switch > span {
    margin-top: 7px !important;
}
.gift-panel-box.on {
    height: 80px !important;
}
.gift-item {
    padding-top: 0px !important;
    height: 36px !important;
    width: 60px !important;
}
.gift-frame {
    width: 30px !important;
    height: 30px !important;
    animation: none !important;
}
.gift-control-panel {
    height: 60px !important;
}
.gift-control-section {
    height: 60px !important;
}
.gift-item > img {
    margin-top: 2px;
    height: 30px !important;
    width: 30px !important;
}
.live-skin-highlight-text.btn {
    display: none !important;
}
.guard-ent {
    display: none !important;
}
.corner-mark {
    display: none !important;
}
.gift-item.buy > .info {
    display: none !important;
}
.gift-presets {
    margin-top: 10px;
    height: 40px !important;
}
.supporting-info {
    float: right !important;
    margin: 13px !important;
}
.bilibili-helper-treasure-box {
    width: 30px !important;
    height: 30px !important;
}
.wish-icon {
    width: 48px !important;
    height: 48px !important;
}
.bilibili-helper-treasure-box {
    width: 48px !important;
    height: 48px !important;
}
.bilibili-helper-treasure-counter {
    margin-top: 20px;
}
.supporting-info {
    float: right !important;
    margin: 17px 0 20px 0 !important;
}
.gift-control-panel .right-part .supporting-info {
    height: 20px !important;
}
/*-- 视频区域 --*/
div.bilibili-live-player div.bilibili-live-player-video-gift,
div.bilibili-live-player div.bilibili-live-player-video-logo {
    display: none !important;
}
/*-- Live Haruna --*/
div#my-dear-haruna-vm {
    display: none !important;
}
}
