	font-size: larger;}
	.card {
	position: relative;
	display: flex;
	-webkit-box-orient: vertical;
	-webkit-box-direction: normal;
	flex-direction: column;
	min-width: 0;
	word-wrap: break-word;
	background-color: #0091031a;
	background-clip: border-box;
	border: 1px solid #ffb400;
	border-radius: .25rem;}
	

	
	body {
	font-family: Roboto,"Helvetica Neue",Helvetica,Arial,sans-serif;
	font-size: 1rem;
	font-weight: 500 !important;
	line-height: 1.5;
	color: rgb(110, 127, 172);
	text-align: left;}
	
	.card-header {
	padding: .75rem 1.25rem;
	margin-bottom: 0;
	background-color: rgb(27, 0, 8);
	border-bottom: 1px solid rgb(255, 0, 0);}
	
	.card-body {
	-webkit-box-flex: 1;
	-ms-flex: 1 1 auto;
	flex: 1 1 auto;
	padding: 1rem;}
	
	.col-xl-4 {
	-webkit-box-flex: 0;
	flex: 0 0 100% !important;
	max-width: 100% !important;}
	
	
	.text-muted {color: rgb(255, 0, 255) !important;}
  
