	.EmbeddedTweet--mediaForward:hover .MediaCard-borderOverlay {border-color: #f00;}
	.MediaCard-borderOverlay {border: 1px solid #ff0;}
	.Icon--informationCircleWhite, .Icon--twitter {display: none;}
