css:
@-moz-document url-prefix("https://www.rte.ie/") {
article.rte-article {
    background: #292d32 !important;
}

.sidebar_panels.stuck {
    background: #292d32 !important;
}

body {
    color: #f4f6f8 !important;
    background: #292d32 !important;
}

.after .panel:nth-child(odd) {
    background: #292d32 !important;
}

.panel {
    background: #292d32 !important;
}

.panel-top-stories .takeover.use-takeover-color {
    background: #000000 !important;
}

.panel a {
    color: #f4f6f8 !important;
}

.panel-authors.even article {
    background: #292d32 !important;
}

.panel-most-popular ul li a .most-popular-headline {
    color: #f4f6f8 !important;
}

.panel-av .programme-list {
    background: #292d32 !important;
}

.paginator .columns {
    background-color: #292d32 !important;
}

.paginator .columns.results {
    color: #f4f6f8 !important;
}

.results-list-container ul .menu-heading {
    background: #292d32 !important;
}

body.article table tbody tr:nth-child(odd) {
    background-color: #292d32 !important;
}

.panel-carousel .slick-arrow,
.panel-player-carousel .slick-arrow {
    background: #000000 !important;
}

article.rte-article ul.tags li a {

    background: #000000 !important;
}

.wrapper {
    background-color: #000000 !important;
}

.fill-white {
    background-color: #292d32 !important;
}

.m20-station-news-feed-carousel a,
.m27-prog-description-play-option a,
.m27b-prog-description-play-option-with-image a,
.m31b-prog-podcasts-with-image a,
.fixed-height-wrapper a,
.m11-radio-player-launcher-two-cols a,
.m34-live-next-side-panel a,
.m33-prog-highlights a,
.mb-presenter-info a,
.mb-contact-info a {
    color: #ccd0d5 !important;
}

.tab-content {
    background: #000000 !important;
}

.daily_top_10 {
    background: #292d32 !important;
}

.article_unit_xlarge,
.article_unit_large,
.article_unit_medium,
.article_unit_small {
    background: #000000 !important;
}

.article_title h1 a,
.article_title h2 a {
    color: #f2f2f2 !important;
}

.article_leadin {
    color: #ddd !important;
}

.exhibits_panel,
.exhibit_top_10 {
    background: #3a3d40 !important;
}
}
