css:
@-moz-document url-prefix("https://office.mailbox.org/appsuite/#!!&app=io.ox/mail") {
:root {
	--main-bg-color: hsl(0, 0%, 10%);
	--main-bg-light-color: hsl(0, 0%, 20%);
	--main-bg-vlight-color: hsl(0, 0%, 40%);
	--select-bg-color: hsl(0, 0%, 5%);
	--text-color: hsl(0, 0%, 80%);
	--text-color-light: hsl(0, 0%, 40%);
	--focus-color: #283f73;
	--accent-color: hsl(222, 100%, 65%);
}

.categories-toolbar-container .category.selected button:focus,
.categories-toolbar-container .category.selected {
	background-color: var(--focus-color) !important;
}

.classic-toolbar > li > button:focus,
.categories-toolbar-container .category,
.mail-detail section.body,
.dropdown-menu,
.detail-view-header,
.form-control {
	background-color: var(--main-bg-color) !important;
}

.dropdown-menu > li > a,
.dropdown-menu > [role="group"] > li > a,
.generic-toolbar,
.window-container-center,
.classic-toolbar,
.inline-toolbar,
.folder-tree,
.leftside,
.list-view-control,
.mce-panel.mce-edit-area,
body#tinymce,
.window-content,
.window-footer,
.notifications,
.mobile-navbar {
	background-color: var(--main-bg-color) !important;
	color: var(--text-color) !important;
}


.mce-container-body {
	background-color: var(--main-bg-light-color) !important;
}


.thread-view-list,
.rightside.mail-detail-pane,
.list-view.visible-selection .list-item.selected,
.mentions-autocomplete.visible-selection .list-item.selected,
.list-view .list-item:hover,
.mentions-autocomplete .list-item:hover,
.list-view.visible-selection .list-item.selected:hover,
.mentions-autocomplete.visible-selection .list-item.selected:hover,
.list-view.visible-selection .list-item.selected,
.mentions-autocomplete.visible-selection .list-item.selected {
	background-color: var(--select-bg-color) !important;
}

.mce-ico,
.twitter-typeahead .tt-input,
.io-ox-mail-compose .sender .name,
.twitter-typeahead .tt-hint {
	color: var(--text-color) !important;
}

.mail-detail-content {
	background-color: var(--main-bg-color) !important;
	color: var(--text-color) !important;
}

.mail-detail .detail-view-row .flag-picker a,
.list-item .flag {
	color: var(--text-color-light);
}


.io-ox-label {
	color: var(--text-color);
}

.category > .btn,
.categories-toolbar-container,
.classic-toolbar,
.generic-toolbar,
.free-space,
.border-right,
.window-sidepanel {
	border-color: var(--main-bg-vlight-color) !important;
	border-bottom-color: var(--main-bg-vlight-color) !important;
}

/* From name and To name */
.mail-detail .from .person-link.person-from,
a.person-to {
	color: var(--text-color-light) !important;
}

/*For the external image warning */
button.close {
	color: rgb(255, 0, 0) !important;
	opacity: 0.6;
	text-shadow: 0 2px 0 #000;
}

button.close:hover {
	opacity: 0.9;
	text-shadow: 0 2px 0 #000;
}

.notification-item {
	background-color: rgba(255, 0, 0, .2);
}
}
