css:
@-moz-document domain("microstock.ru") {
:root {
  --font: Georgia;
  
  --size: 1.4rem;
	--mid: 1.3rem;
  --h1: 1.7rem;
  --h2: 1.5rem;
  --h3: 1.4rem;
  --f0: 9px;
  --f1: 11px;
  --f2: 12px;
  --f3: 13px;
  --f4: 15px;
  --f5: 20px;
  
  --bg: #fff;
  --url: #05a;
  --norm: #000;
  --dark: #666;
  --light: #bbb;
  --fluid: #0002;
  --quote: #a4a;
  --shadow: #000D;
  --shade: #0007;
  
  --corner: 4px;
}

/* - init */
body,
html {
  background: var(--bg);
}
* {
  border: none !important;
}
a {
  color: var(--url) !important;
  text-decoration: underline !important;
}
br {
  font-size: 30% !important;
}
br + br {
  line-height: .5 !important;
}
br + br + br {
  line-height: .3 !important;
}
br + br + br + br {
  line-height: .1 !important;
}
li,
p {
  text-align: left;
}
table {
  border-spacing: 0;
}
td {
  padding: 0;
}

input:not([type="image"]),
select,
textarea {
  border: 1px solid var(--fluid) !important;
}
input:not([type="checkbox"]):not([type="radio"]):not([type="image"]) {
  font-size: var(--f2);
  height: 25px;
  padding: 2px 5px;
}
input[type="submit"],
select {
  font-size: var(--f2);
  padding: 2px 10px;
}
input[type="checkbox"] {
  margin: 0 5px !important;
}

/* - init / misc */
.thead[align="right"] {
  text-align: right;
}

/* image bullets */
img[id^="collapseimg"],
img[src^="images/statusicon/forum"],
_ {
  border-radius: 50% !important;
}
img[src="images/misc/menu_open.gif"] {
  height: 7px;
  width: 7px;
  position: relative;
  top: 1px;
  padding: 0 3;
}

/* - init / hide */
.bg01,
.bor1_1,
.bor1_3,
[class^="corn_"],
.corn_lb,
div + br,
.f_corn1,
.f_corn2,
.f_corn3,
.frame_table_bottom,
hr,
[class^="hline"],
[class^="hr"],
input[name="daysprune"] + strong,
.line_ht,
.msep_cont,
.mbg2,
.mbg3,
.r,
.r.c_g.f11,
#seedbnrs42,
.sep,
.sep2,
.smallfont[style="margin-bottom:2px"],
[style^="font-size:9px"],
.thead > a .inlineimg,
.vbmenu_control_my_border,
_ {
  display: none !important;
}

body > table:nth-of-type(1) > tbody > tr > td[width$="%"] {
  overflow: hidden !important;
  font-size: 0 !important;
  width: 0 !important;
}

[style^="padding:28px"] {
  padding: 0 !important;
}

/* - init / background */
.alt1,
.alt2,
.alt1Active,
[class^="bg"],
.bankmark,
[class^="dig"],
#displaymodes a,
#hor_menu > table > td,
.even,
[class^="line"],
.menu_b,
.menu_g,
.mes_container2,
.n_bl,
.n_gr,
.panel,
.panelsurround,
.tcat,
.tfoot,
.thead,
.thead_my,
#threadtools,
#threadtools a,
#threadsearch,
#threadsearch a,
#threadrating,
#threadrating a,
#displaymodes,
.vBulletin_editor,
[id^="vB_Editor"],
.vbmenu_option,
.vbmenu_control,
.vbmenu_control_my,
.vbmenu_control_my a,
[style^="background-color: #F0F0F0;"],
_ {
  background: none !important;
  color: var(--dark) !important;
}

/* - header */
/* - header -1 / logo */
body.vsc-initialized::before {
  background-color: var(--url);
  border-radius: 0 0 var(--corner) var(--corner);
  box-shadow: 2px 2px 10px var(--url);
  content: "microstock.ru";
  color: var(--bg);
  display: block;
  font-size: var(--f4) !important;
  letter-spacing: .2;
  margin-left: 77px;
  opacity: .9;
  padding: 5px 14px 8px 14px;
  position: fixed;
  top: 0;
  z-index: 999;
}

/* - header -1 / topic */
.main_beat_text .smallfont,
.main_beat_text .smallfont .inlineimg,
_ {
  display: none;
}
#_posts > div:first-of-type td[width="90%"] > .smallfont,
.navbar.navbar_my > .navbar_otst3_my,
_ {
  background-color: var(--quote);
  border-radius: 0 0 var(--corner) 0;
  box-shadow: 2px 2px 10px var(--quote);
  color: var(--bg);
  display: block;
  margin-left: 175px;
  opacity: .9;
  padding: 5px 14px 8px 14px;
  position: fixed;
  top: 0;
  z-index: 999;
}
.navbar.navbar_my > .navbar_otst3_my > strong {
  font-size: var(--f4) !important;
  letter-spacing: .2;
}

/* - header 0 / banner */
.seedbnrs {
  height: 120px !important;
}

/* - header 1 / menu */
/* bg */
.mbg1 {
  background: #0001;
}

/* - header 1 / menu left */
.mbg1 > table > tbody > tr > td {
  padding: 0 10px !important;
}
.mtext,
.mseltext,
_ {
  padding: 6px 11px 2px 11px;
  font-weight: normal;
  font-size: var(--f1);
}
.mseltext a.mseltext_link {
  font-weight: normal;
  font-size: var(--f1);
}
/* active item */
.mbg4 {
  background: white;
}

/* - header 1 / menu right */
.tborder_my {
  position: absolute;
  right: 20px;
  top: 123px;
  width: auto;
}
.tborder_my a {
  font-weight: normal;
}

/* - header 1 / menu popup */
#serv_menu_itself,
.vbmenu_popup,
_ {
  border-radius: var(--corner);
  box-shadow: 3px 3px 15px var(--shade);
  clip: auto !important;
  margin-left: 6px;
  padding: 5px 0 10px 0 !important;
}
#serv_menu_itself div,
.vbmenu_popup .thead,
.vbmenu_popup .vbmenu_option,
.vbmenu_popup .vbmenu_hilite,
_ {
  padding: 5px 15px !important;
}
#serv_menu_itself .serv_menu_hover,
.vbmenu_popup .vbmenu_hilite,
_ {
  background: var(--fluid) !important;
}
#serv_menu_itself,
#navbar_search_menu,
#usercptools_menu,
_ {
  margin-top: 4px !important;
}
#serv_menu_itself {
  margin-top: 10px !important;
  margin-left: -12px !important;
}

/* - header 2 / breadcrumb */
.navbar_otst3_my,
.navbar_otst1_my_sponsor,
_ {
  padding: 0;
}
.f_corn1 + .tborder .alt1 > div > table > tbody > tr > td {
  vertical-align: top !important;
  padding-right: 10px;
}
.navbar_otst1_my_sponsor tr[valign="bottom"] > td > a,
.navbar_otst1_my_sponsor .navbar.navbar_my a,
_ {
  display: none;
}

/* - header 2 / banner */
.page .tborder .alt1 > div > table td:nth-of-type(2) > a > img {
  _display: none;
  height: 29px !important;
  position: absolute;
  top: 120px;
  left: 222px;
}

/* - header 2 / login */
/* username */
.navbar_otst2_my br {
  display: none;
}
.navbar_otst2_my strong {
  font-weight: normal;
}

/* login form */
form[action="login.php"] > table > tbody {
  display: flex;
}
form[action="login.php"] td {
  padding-left: 6px;
}

/* - header 3 / paging */
tr[valign="bottom"] > td {
  vertical-align: top;
}
#poststop + table[style="margin-bottom:3px"] {
  margin: 0;
  margin-top: 20px;
}
.pagenav td {
  white-space: nowrap;
}
/* paging */
.vbmenu_control ~ td.alt1 a,
.vbmenu_control ~ td.alt2 strong,
_ {
  font-family: sans-serif !important;
  font-size: var(--f3) !important;
  padding: 0;
}
.vbmenu_control ~ td.alt2 strong {
  background: var(--light);
  border-radius: 20px;
  color: var(--bg);
  padding: 5px 9px;
}
.pagenav {
  padding-right: 2px;
}
/* forum rules */
#poststop + table[style="margin-bottom:3px"] tr[valign="middle"] > td:nth-of-type(2),
/* faq */
#same_thread,
_ {
  display: none !important;
}

/* - header 4 / menu topic */
.tborder .tcat a strong,
.vbmenu_control,
_ {
  font-weight: normal;
}
form .vbmenu_control {
  font-weight: normal;
  letter-spacing: .3px;
}
.tborder .tcat a .inlineimg {
  display: none;
}

/* - message */
/* - message / post */
img[src="images/buttons/reply.gif"]::before {
	content: "! ! ! ! ! !";
	font-size: var(--f3);
	color: var(--norm) !important;
}
/* - message / sidebar */
.alt2 > noindex {
  display: flex;
  flex-direction: column;
}
.alt2 > noindex > div:nth-of-type(3) {
  font-size: 0;
}
.alt2 > noindex > div:nth-of-type(3) br {
  display: none;
}

/* - message / sidebar / username */
.bigusername {
  color: #555 !important;
  font-weight: normal;
  letter-spacing: .5;
  text-decoration: none !important;
  position: relative;
  top: -2px !important;
}

/* - message / sidebar / stats */
.alt2 > noindex > div:last-of-type {
  background: none;
  display: block;
  float: left;
  height: 20px;
  width: 100px !important;
  opacity: 0;
  overflow: hidden;
  position: absolute !important;
  margin-top: 15px;
}
.alt2 > noindex > div.smallfont:last-child {
  font-size: 0;
}
.alt2 > noindex > div.smallfont:last-child div {
  font-size: var(--f2);
}
.alt2 > noindex > div:last-of-type:hover {
  background: var(--bg);
  border-radius: var(--corner);
  box-shadow: 3px 3px 15px var(--shade);
  display: block !important;
  line-height: 1.4;
  height: auto;
  opacity: 1;
  overflow: visible;
  padding: 7px 12px;
  position: absolute !important;
  min-width: 180 !important;
  z-index: 2;
}

/* - message / sidebar / icons */
[id^="post"] tr:nth-of-type(3) > .alt2 {
  opacity: .1;
}
[id^="post"] tr:nth-of-type(3) > .alt2:hover {
  opacity: 1;
}

/* - message / sidebar / thanks */
[id^="post_thanks_box"] .page {
  padding-bottom: 0;
}
[id^="post_thanks_box"] > div::before {
  content: "👍";
  font-size: 14px;
  padding-left: 1px;
  opacity: .2;
}
[id^="post_thanks_box"] {
  position: relative !important;
  top: -30px;
  left: 70px;
  width: 170px;
}
[id^="post_thanks_box"] > div {
  display: block !important;
  float: left !important;
  height: 20px;
  overflow: hidden;
  position: absolute;
  left: 0px;
  width: 18px;
  text-align: left;
}
[id^="post_thanks_box"] > div:hover {
  background: white;
  border-radius: var(--corner);
  box-shadow: 3px 3px 15px var(--shade);
  display: block !important;
  float: left !important;
  height: auto;
  margin-bottom: 0px !important;
  padding: 10px 12px 7px 12px;
  min-width: 170px;
  z-index: 99;
}
[id^="post_thanks_box"] > div:hover::before {
  content: "";
  display: none;
}
[id^="post_thanks_box"] .alt2,
[id^="post_thanks_box"] .alt1,
_ {
  display: inline-block;
  padding: 0 !important;
}
[id^="post_thanks_box"] .alt2 > .smallfont {
  font-size: 0;
}
[id^="post_thanks_box"] .alt1 > .smallfont {
  font-size: var(--f2) !important;
  padding: 0 !important;
}
[id^="post_thanks_box"] .alt2 > .smallfont::before {
  content: "Поблагодарившие:";
  font-size: var(--f2);
}

/* - message / text */
/* - message / text / url */
[id^="post"] > tbody > tr > td[align="right"].thead:last-of-type {
  color: var(--fluid) !important;
  display: inline-block;
  margin-left: -80px;
  font-size: var(--f1);
  width: 4rem;
  text-align: right;
}
[id^="post"] > tbody > tr > td.thead:last-of-type a {
  color: var(--fluid) !important;
}

/* - message / text / edit buttons */
[id^="post"] .alt1[align="right"] {
  opacity: .1;
  text-align: left;
}
[id^="post"] .alt1[align="right"]:hover {
  opacity: 1;
}

/* - message / text / quotation */
.alt2[style^="border:1px"] {
  border-left: 1px solid var(--fluid) !important;
  padding: 0 0 4px 15px;
}
.alt2[style^="border:1px"],
.alt2[style^="border:1px"] * {
  color: var(--quote) !important;
  font-family: var(--font);
  font-size: var(--mid) !important;
  line-height: 1.3;
}
.alt2 > .post_comment_my:first-child {
  font-size: 0 !important;
}
/* qouted name */
.alt2 > .post_comment_my:first-child > strong {
  font-family: var(--font);
  font-size: .8rem !important;
  font-style: italic !important;
	color: var(--shadow) !important;
}

/* - message / text / itself */
[id^="td_post"] > table {
  margin-top: -25px;
}
.main_beat_text {
  padding: 0 !important;
}
.search_result_post_text,
div[id^="post_message"] {
  font-family: var(--font) !important;
  font-size: var(--size) !important;
  line-height: 1.4;
}

/* - message / images */
[id^="post_message_"] img {
	max-width: 100%;
}

/* - message / answer form */
#_vB_Editor_QR_textarea {
  width: 100% !important;
  height: 250px !important;
}
.panel {
  padding: 0;
}
#collapseobj_quickreply .panel > table > tbody > tr:first-of-type,
#collapseobj_quickreply .panel > table > tbody > tr legend,
_ {
  display: none;
}
#qr_preview {
  margin-left: 7px;
}
.vBulletin_editor {
  padding: 0;
}

/* - footer */
/* prev/next topic */
table + br + .smallfont[align="center"] {
  font-size: var(--f3) !important;
  text-align: right;
  padding: 15px 5px;
}

/* - footer / go to */
select[name="f"] {
  height: 25px;
}

img[src="images/misc/whos_online.gif"],
img[src="images/misc/stats.gif"],
img[src="images/misc/birthday.gif"],
_ {
  margin-right: 10px;
}

/* - footer / similar */
#collapseobj_similarthreads > tr.thead {
  display: none;
}
#collapseobj_similarthreads > tr > td:first-child {
  padding-left: 19px;
}
#threadrating_menu + table + br + table td {
  display: inline-block;
}
/* button */
[valign="bottom"] .tborder .thead > a,
br + .tborder .tcat[colspan="5"] > a,
_ {
  float: left !important;
  padding-right: 2px;
  padding-top: 2px;
}
.tborder .thead > a img,
.tborder .tcat[colspan="5"] > a img {
  width: 13px !important;
}
/* title */
.tborder .tcat[colspan="2"],
.tborder .tcat[colspan="5"] {
  font-weight: normal;
  font-size: var(--f2);
}

/* - footer / menu left */
div[style="background-color: #F0F0F0;"] + div > table {
  text-align: center;
}

/* - footer / menu right */
.page > tbody > tr > td.tfoot,
table[style="background:#FFFFFF"] + form[action="index.php"] {
  display: none;
}
form .page {
  float: left;
  top: 0px;
  right: 0px;
}

/* - footer / time */
.tborder ~ .smallfont[align="center"] {
  font-size: var(--f2);
  padding: 0;
  text-align: center;
}

/* ≡ pages ---------------------------------------- */
/* = index */
/* bullets & paddings */
.adobestock_ads_line,
.frame_table_top_tr,
[colspan="2"][style="padding: 7px 0px 7px 3px;"],
[src="/forum/images/buttons/lastpost.gif"],
td[height="38"], 
_ {
  display: none;
}
.menu_b, 
.menu_g,
[style="padding: 15px 0px 10px 0px;"],
_ {
  padding: 10px 0 0 0 !important;
}
.n_anons,
.bg01 td {
  padding: 0;
  font-size: var(--f1);
}
.n_anons:not(:last-of-type)::after {
  content: ".";
}
[style="padding: 20px 0px 23px 35px;"],
[style*="padding: 15px 17px 17px 35px;"],
.bg02,
.bg03 {
  padding: 10px 0 0 20px !important;
}
.bg04 {
  padding: 0 17px;
  margin: 0;
}
.bg05,
.adobestock_ads {
  margin: 0;
  padding: 10px 0 0px 0;
  background: none;
}
.page > div > br:first-of-type {
  display: none;
}
.pad1 {
  float: left;
  padding: 1px 5px 0 0;
}
.pad2 {
  padding: 0 0 10px 0;
}
.pad4, .dig_list {
  padding: 0 20px 0 0;
}
.dig_list h2 {
  margin: 0 !important;
  padding: 0 0 0 2px;
}

/* vote */
[id^="vote"] {
  margin: 5px 5px;
  position: relative;
  top: 2px;
}

form[action="poll.php"] .tborder {
  width: 60%;
  padding: 20px 0;
}
[style="width:640px"] {
  width: 100% !important;
}
form[action="poll.php"] .tcat {
  font-size: var(--f4);
  padding: 20px 0;
}
.panelsurround .fieldset {
  padding: 0;
}
.panelsurround .fieldset [style="padding:3px"] div {
  height: 25px;
}
.panelsurround .fieldset input[type="radio"] {
  margin-right: 10px;
}
/* vote result */
.tborder .tcat {
  font-size: var(--f4)
}
#pagenav_menu + .tborder td {
  _padding: 10px;
}
#pagenav_menu + .tborder td.alt1,
#pagenav_menu + .tborder .alt1 + .alt2 + .alt1,
#pagenav_menu + .tborder .alt1 + .alt2 + .alt1 + .alt2,
_ {
  width: 5%;
}

/* footer */
[style="padding: 15px 17px 15px 17px;"],
[style="padding: 20px 16px 0px 18px;"],
[style="padding: 15px 0px 15px 0px;"],
_ {
  padding: 0 10px 0 0 !important;
}

/* = search */
[id^="post"].tborder[style^="margin:7px"] .alt2 {
  padding: 0 !important;
}

/* = toc */
/* title */
.navbar_otst3_my strong {
  font-weight: normal;
  font-size: var(--f1);
}
.forum_dept_my {
  font-size: var(--f4);
  padding: 10px 0 5px 0;
  font-weight: normal;
}
#pagenav_menu + .tborder {
  margin-top: 20px;
}
#pagenav_menu + .tborder > thead > tr {
  display: none;
}

/* footer titles */
.thead {
  padding: 0 !important;
  padding-top: 20px !important;
  padding-bottom: 3px !important;
  font-size: var(--f3);
  text-align: left;
}

/* list */
.forum_otst_my > div > div + .smallfont {
  font-size: var(--f0);
  position: relative;
  top: -2px;
  color: var(--light) !important;
}

.forum_otst_my > div > div,
_ {
  padding: 0 !important;
  display: inline-block;
}

td.alt2.f11 + .alt1,
td.alt2.f11 + .alt1 + .alt2,
#threadslist [class^="alt"][align="center"],
_ {
  text-align: right;
}

.thead_my,
.thead,
_ {
  font-weight: normal;
}

[id^="td_title"],
.thread_title_my,
.thread_otst_my,
_ {
  margin: 0;
  padding: 3px 0;
}

/* = service */
.menu_g_pad {
	background: none;
	padding: 0 0 5px 10px;
}
div.gray_text,
.bg03_2,
_ {
	padding: 0;
}

/* = info */
.line_vl .f11 table > tbody > tr > td {
  height: 1.5rem;
}

/* = article = news = feedback */
[width="56%"] p,
[width="56%"] li,
[width="56%"] li div,
[width="56%"] font,
[width="76%"] p,
[width="76%"] li,
[width="76%"] li div,
_ {
	font-family: var(--font);
	font-size: var(--size) !important;
	line-height: 1.4;
}
[width="56%"] .infotable td {
  font-family: var(--font);
	font-size: var(--mid);
  padding: 0 20px 10px 0 !important;
}

[width="76%"] h1,
[width="56%"] h1,
_ {
	font-size: var(--h1);
	font-weight: normal;
}
[width="76%"] h2,
[width="56%"] h2,
_ {
	font-size: var(--h2);
	font-weight: normal;
}
[width="76%"] h3,
[width="56%"] h3,
_ {
	font-size: var(--h3);
	font-weight: normal;
}
.mes_container2 td {
	padding: 5px;
}

/* = faq */
.gmenu {
  background: none;
  margin: 0;
  padding: 25px 0 0 0;
}
.line_vl [style="padding: 6px 13px 6px 13px;"] {
  padding: 10px 0 !important;
}
.bmark {
  background: none;
  padding: 3px 0;
}
.c_or {
  color: var(--dark);
}
[id^="quiestion"] {
  color: var(--url) !important;
  font-family: var(--font);
  font-size: 1rem !important;
}
[id^="answer"] p {
  font-family: var(--font) !important;
  font-size: var(--size) !important;
  line-height: 1.4;
}

[id^="answer"] > table > tbody > tr > td:first-of-type {
  padding-top: 0 !important;
}

/* = settings */
#infotable td.f11,
#infotable td.f12,
_ {
  padding-top: 10px;
}

/* profile menu */
td > .tborder .thead {
  padding-top: 20px !important;
}
td > .tborder .alt2 {
  padding-top: 3px;
}

}
