css:
@-moz-document domain("9anime.to"), domain("www1.9anime.nl") {
::-webkit-scrollbar-thumb {
	border-radius: 10px;
	-webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, .3);
	background-color: #eee;
}
::-webkit-scrollbar-track {
	border-radius: 10px;
	background-color: #16151d
}
::-webkit-scrollbar {
	width: 12px;
	background-color: #16151d
}
::-webkit-scrollbar-thumb {
	border-radius: 10px;
	-webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, .3)
}

body.watch #body #main {
    padding-right:0;
}
body.watch #body #sidebar {
    display:none;
}
body.watch .container {
    max-width:100%;
}
body.watch .widget.player #player {
    height:400px;
    flex:1 1 auto !important;
}
.list-film {
    max-width:650px;
    margin:0 auto;
}
.widget .widget-title{
    display:flex;
    width:100%;
    flex-direction:row;
    justify-content:start;
    flex-wrap:wrap;
}
.hotnew .title,#header .container #logo{order:1;flex:1 1 30%;}
.hotnew .tabs,#header .container #search {order:2;flex:1 1 20%;}
.hotnew .paging.control {order:3;flex:1 1 20%;text-align:right;}

#header .container {display:flex;width:100%;max-width:1100px;flex-direction:row;flex-wrap:wrap;}
#header .container #logo {order:1;flex:0 1 190px}
#header .container #logo a {font-size:9px;}
#header .container #search {order:2;flex:1 1;}
#header .nav {padding:10px 0;height:50px;box-shadow:0 0 5px #000;margin-bottom:30px;}
body.watch #header .nav {margin-bottom:0}

#header {
    position: sticky;
    top: 0;
    z-index: 99999;
}

.schedule .more,
.ongoing .more{
    order:1;
    flex: 0 0 55px;
    text-align:center;
}
.schedule .widget-title .title,
.ongoing .widget-title .title{
    order:2;
    flex:1 1 80%;
    padding-left:20px;
}
.widget-title {
    display:flex;
    width:100%;
    flex:0 0 100%;
    justify-content:center;
}
@media screen and (min-height: 600px) {
    body.watch .widget.player #player {
        height:600px;
        display:flex;
        flex-wrap:wrap;
        flex-direction:row;
        flex:1 1 auto !important;
    }
}
@media screen and (min-height: 600px) and (min-width: 1280px) {
    body.watch .widget.player #player {
        height:calc(100vh - 350px);
        display:flex;
        flex-wrap:wrap;
        flex-direction:row;
        flex:1 1 auto !important;
    }
    body.watch .widget.player #player iframe {
        flex:1 1 auto !important;
        display:flex;
        flex-wrap:wrap;
    }
}
/* Extra color tweaks */
.alert-primary {
    background: linear-gradient(to bottom right, #523b7e, #30293e);
}
}
