css:
@-moz-document url-prefix("http://tamlyhoctoipham.com"), domain("tamlyhoctoipham.com") {
/* Credit - tamlyhoctoipham.com dark theme
Thằng vọc css (Author): JAK
Facebook: https://www.facebook.com/wblekhoa (Lê Khoa)
Email: wblekhoa@gmail.com */

:root {
--mainC1: #1d1d1d;
--mainC2: #464646;
--mainC3: #2d2d2d;
}

:root {
--darkC0: #171717;
--darkC1: #1d1d1d;
--darkC2: #212121;
--darkC3: #282828;
--darkC4: #3a3a3a;
--whiteC0: #fff;
--whiteC1: #e7e7e7;
--whiteC2: #e0e0e0;
--whiteC3: #8d8d8d;
--hlC1: #ed2242;
}

/* Background color */
.boxed:not(.active) { /* bg1 */
	background-color: var(--darkC1);
}

html, .composs-main-article-head .composs-main-article-meta, .widget .tagcloud a:hover, .composs-panel-pager a.page-numbers:hover { /* bg2 body */
	background-color: var(--darkC2);
}

.content-wrapper>#sidebar:before, .widget .tagcloud a, .composs-archive-list h3.item-title, #sidebar .widget>h3, #main-menu, .header-top>ul>li:hover>a>span, #main-menu>ul>li:hover>a>span, .header-top>ul ul.sub-menu, #main-menu ul ul.sub-menu, #main-menu>ul>li:hover>a, a.ot-color-hover-facebook:hover i.fa, ._517h, ._59pe:focus, ._59pe:hover, .fb-comments ._4uyl ._1cb, .widget .search-form label input, .widget .search-form>input, .composs-panel>.composs-panel-title:not(.composs-panel-title-tabbed)>strong, .composs-panel>.composs-panel-title>strong.active, .composs-tag-list a, .sf-reset h1, .composs-panel-pager .page-numbers { /* bg3 */
    background-color: var(--darkC3);
}

.widget .search-form>input:hover, .composs-tag-list a:hover, .composs-panel>.composs-panel-title>strong:hover, .composs-panel-pager span.page-numbers { /* bg4 */
	background-color: var(--darkC4);
}

.composs-comments {  /*bgw1*/
	background-color: var(--whiteC1);
}

/* border color */
.ot-w-article-list .item, .composs-main-article-head, .header .header-top, .header-top-socials>a i.fa, .composs-author-block, .composs-tag-list, hr, #footer .widget h3, .footer-copyright, ._491z, ._517h, ._59pe:focus, ._59pe:hover, ._5lm5, .composs-panel>.composs-panel-title:not(.composs-panel-title-tabbed)>strong, .composs-panel>.composs-panel-title>strong.active, .composs-panel>.composs-panel-title, .content div, .sf-reset h1 { /* bd3 */
	border-color: var(--darkC3)!important;
}

.composs-archive-list h3.item-title, #sidebar .widget>h3, .composs-panel>.composs-panel-title>strong { /* bd2 */
	border-color: var(--darkC2);
}

/* Text colorr */
body, span { /* goldbaltc2 */
	color: var(--whiteC2);
}

h3 span, b, strong, h4 span { /* tc1 */
	color: var(--whiteC1)!important;
}

.composs-main-article-content p, .composs-main-article-content p span, a, h6, h5, h4, h3, h2, h1, .header-top>ul>li:hover>a>span, #main-menu>ul>li:hover>a>span, .header-top ul ul.sub-menu:not(.ot-mega-menu) li>a, .header-top ul ul.sub-menu>li>a, #main-menu ul ul.sub-menu:not(.ot-mega-menu) li>a, #main-menu ul ul.sub-menu>li>a, .composs-author-block .item-content>strong, body, h2 strong, h3 strong, li span { /* tc2 */
	color: var(--whiteC2)!important;
}

.composs-main-article-head .composs-main-article-meta .item, .fa-facebook-f:before, .fa-facebook:before, ._pup, .composs-blog-list .item-meta .item-meta-item, .ot-w-article-list .item .item-meta .item-meta-item { /* tc3 */
	color: var(--whiteC3)!important;
}

a[style^="color"] {
	color: var(--hlC1)!important;
}

/* icons */
.header .header-content>div img, #footer .footer-widgets .widget-split .widget:last-child a[href="/"] img
{ /* icons */
    filter: grayscale(100%) invert(85%) brightness(1);
}

/* remove shadow */
.header-top>ul>li:hover>a>span, #main-menu>ul>li:hover>a>span, .ot-w-socialize>a i.fa { /* rmshadow */
	box-shadow: none!important;
}

}

@-moz-document url-prefix("https://ybox.vn"), domain("ybox.vn") {
/* Credit - ybox.vn dark theme
Thằng vọc css (Author): JAK
Facebook: https://www.facebook.com/wblekhoa (Lê Khoa)
Email: wblekhoa@gmail.com */

:root {
--mainC: #eca829;
--mainC2: #464646;
--mainC3: #2d2d2d;
}

:root {
--darkC0: #171717;
--darkC1: #1d1d1d;
--darkC2: #212121;
--darkC3: #282828;
--darkC4: #3a3a3a;
--whiteC0: #fff;
--whiteC1: #e7e7e7;
--whiteC2: #e0e0e0;
--whiteC3: #8d8d8d;
--hlC1: #ed2242;
}

*::-webkit-scrollbar-track, .scrollable::-webkit-scrollbar-track {
    background-color: var(--darkC3) !important;
    border-radius: 0 !important
}

*::-webkit-scrollbar, .scrollable::-webkit-scrollbar {
    width: 10px !important;
    background-color: var(--darkC1) !important
}

*::-webkit-scrollbar-thumb, .scrollable::-webkit-scrollbar-thumb {
    border-radius: 0px !important;
    background-color: var(--mainC) !important;
    box-shadow: 0px 15px 15px -4px var(--mainC) !important
}

body, .bg-gray-ver { /* bg0 */
	background-color: var(--darkC0);
}

.user_card--two, .thumbnail, .panel, .navbar-default .navbar-form, .comment__container .media-body.comment__form, .community__nav, .background-masker { /* bg1 */
    background-color: var(--darkC1);
}
.header__container .nav_second__container, .dropdown-menu, .btn-group--post-actions .btn--bookmark.btn--active, .btn-group--post-actions .btn--bookmark:hover, .ant-select-dropdown-menu-item-active, .ant-select-dropdown-menu-item:hover { /* bg2 */
    background-color: var(--darkC2);
}

.navbar-default .navbar-collapse, .recommended-posts .title, .header__container .nav_second__container form .form-group input, .btn-default, .bg-primary, .btn-group--likes .btn, .btn-group--post-actions .btn, .comment__container .media-body.comment__form textarea, .alert-top, .header__container .navbar.nav_first__container, .swiper-pagination-bullets .number-pagination, .post .panel-body .content .due-date, .post.post--article-advanced .panel-body .photo span.label, .post--article-two span.due-date, .post--article-two span.due-date span, input.form-control, .ant-select-selection, .ant-select-dropdown, .background-masker.header-avatar, .animated-background, .recommended-posts--horizontal .slick-next .btn, .recommended-posts--horizontal .slick-prev .btn { /* bg3 */
    background-color: var(--darkC3);
    background: var(--darkC3);
}

.header__container .nav_third__container ul li.active a, .btn-default.active, .btn-default.active.focus, .btn-default.active:focus, .btn-default.active:hover, .btn-default.focus, .btn-default:active, .btn-default:active.focus, .btn-default:active:focus, .btn-default:active:hover, .btn-default:focus, .btn-default:hover, .open>.dropdown-toggle.btn-default, .open>.dropdown-toggle.btn-default.focus, .open>.dropdown-toggle.btn-default:focus, .open>.dropdown-toggle.btn-default:hover, .navbar-default .navbar-nav>.active>a, .navbar-default .navbar-nav>.active>a:focus, .navbar-default .navbar-nav>.active>a:hover, .navbar-default .navbar-nav>li>a:focus, .navbar-default .navbar-nav>li>a:hover, .swiper-pagination-bullets .number-pagination__active { /* bg4 */
    background-color: var(--darkC4);
}

.comment__container .media-body.comment__form { /* bd1 */
    border-color: var(--darkC1);
}

.header__container .navbar.nav_first__container, div[class^="col"] div, .header__container .nav_second__container, .dropdown-menu, .btn-group--post-actions .btn--bookmark.btn--active, .btn-group--post-actions .btn--bookmark:hover { /* bd2 */
    border-color: var(--darkC2);
}

.navbar-default .navbar-collapse, .navbar-default .navbar-form, .header__container .nav_third__container, .header__container .nav_third__container ul li a, .user-card-one .avatar, .user-card-one hr, .header__container .nav_second__container form .form-group input, .btn-outline--primary, .btn-group--likes .btn, .btn-group--post-actions .btn, hr, textarea, .bg-primary, .comment__container .media-body.comment__form textarea, .community__nav .nav__container, .community__nav .nav__container .border-right, .homepage-left-widget .homepage-total-follow, .swiper-pagination-bullets .number-pagination, .post.post--article-advanced .panel-body .photo span.label, .post .panel-body .content .due-date, .post--article-two span.due-date, input.form-control, .form-control:focus, .recommended-posts--horizontal .slick-next .btn, .recommended-posts--horizontal .slick-prev .btn { /* bd3 */
    border-color: var(--darkC3);
}

.btn-outline--primary:hover, .swiper-pagination-bullets .number-pagination__active { /* bd4 */
    border-color: var(--darkC4);
}

.btn-group--likes .btn:last-child.btn--dislike-active, .btn-group--likes .btn:last-child:hover span, .btn-group--likes .btn:first-child.btn--like-active, .btn-group--likes .btn:first-child:hover span { /* tc0 */
	color: var(--whiteC0)!important;
}

body, body a, div a, span, .user_card--two .header__container .auth__addition h6 a, .navbar-default .navbar-nav>li>a, .navbar-default .navbar-text, .post .panel-body .publisher__container .publisher__name h6 a, .post .panel-body .content h2.post-title a, .navbar-default .navbar-nav>.active>a, .navbar-default .navbar-nav>.active>a:focus, .navbar-default .navbar-nav>.active>a:hover, .navbar-default .navbar-nav>li>a:focus, .navbar-default .navbar-nav>li>a:hover, .btn-outline--primary, .btn-outline--primary:hover, .dropdown-menu, .panel, .post--article-two h6 a, .post--article-two h6 a:hover, .post.post--article-advanced .panel-body .description, .header__container .nav_second__container form .form-group input, .swiper-pagination-bullets .number-pagination, .icon-infomation .path5:before, input.form-control, .fa-angle-right:before, .fa-angle-left:before { /* tc2 */
	color: var(--whiteC2);
}

.icon-like-color .path2:before, .post .panel-body .publisher__container .publisher__name h6 span, .post--article .panel-body .actions .counting, .icon-comment:before, .icon-facebook:before, .post--article .panel-body .actions i, .post--article .panel-body .actions .text, .header__container .nav_second__container form i, .post.post--article-advanced .panel-body .actions .text, .alert-top a, .ant-select-search__field__placeholder, .ant-select-selection__placeholder, #yb-infocompany { /* tc3 */
	color: var(--whiteC3);
}

.text-grey-600, .text-grey-600:focus, .text-grey-600:hover, .icon-like:before, .icon-dislike:before { /* tc3i */
	color: var(--whiteC3)!important;
}

.text-default, .text-default:focus, .text-default:hover, #post-content span, #post-content p, .username, .icon-bell:before, li { /* tc2i */
	color: var(--whiteC2)!important;
}

.publisher__container .publisher__name h6 a { /* fs16 */
	font-size: 18px;
}

.publisher__container .publisher__name h6 span, .post.post--article-advanced .panel-body .actions .text { /* fs14 */
	font-size: 14px!important;
}

.swiper-pagination-button, .swiper-pagination-view { /* centerAlign */
	right:50%;
    transform: translateY(-50%);
}

.navbar-form { /* search */
	margin: 10px;
}

.swiper-pagination-bullets .post--article .panel-body {
	padding: 20px 30px;
}
	
.header__container .navbar.nav_first__container {
    padding: 10px 20px;
    border-bottom: none;
}

.icon-members {
	display: none;
}

.panel-body {
	padding: 30px;
    max-width: 700px;
    margin: auto;
}
.panel {
    width: 100%;
}
.spp-container .content .spp-photo {
    margin-left: auto;
    margin-right: auto;
    position: relative;
}
.panel-body p, .panel-body span {
	font-size: 18px!important;
}
.panel-body p {
    margin: 8px 0 8px!important;
}

.swiper-pagination-bullets .post--article {
	width: 340px;
}

/* ads */
.rpa__ads, .YBBN, .mt-10, div[class*="swiper-pagination-cup"], div[class*="swiper-pagination-view"] {
	display: none!important;
}

/* remove */
.header__container .nav_third__container, .recommended-posts .title, .community__nav, .background-masker.header-avatar:before, .recommended-posts--horizontal .slick-next .btn, .recommended-posts--horizontal .slick-prev .btn { /* rmshadow */
	box-shadow: none!important;
}

/* icons */
.logo
{ /* icons */
    filter: grayscale(100%) invert(85%) brightness(1);
}
}
